    if (userDropdown && user.isGuest) {
        // Add upgrade option for guests
        const upgradeItem = document.createElement('a');
        upgradeItem.href = './auth.html';
        upgradeItem.className = 'dropdown-item';
        upgradeItem.textContent = 'Upgrade Account';
        upgradeItem.style.color = 'var(--accent-green)';

        const divider = userDropdown.querySelector('.dropdown-divider');
        if (divider) {
            userDropdown.insertBefore(upgradeItem, divider);
        }
    }
}

// Update UI for logged out user
function updateUIForLoggedOutUser() {
    // Update main action button
    const mainActionBtn = document.getElementById('main-action-btn');
    if (mainActionBtn) {
        mainActionBtn.textContent = 'Sign In';
        mainActionBtn.onclick = () => window.location.href = './auth.html';
    }

    // Update user avatar to show sign in
    const userAvatar = document.getElementById('user-avatar-btn');
    if (userAvatar) {
        userAvatar.onclick = () => window.location.href = './auth.html';
        userAvatar.title = 'Sign In';
    }
}

// Setup user dropdown functionality
function setupUserDropdown() {
    const userAvatar = document.getElementById('user-avatar-btn');
    const userDropdown = document.getElementById('user-dropdown');

    if (userAvatar && userDropdown) {
        // Check if user is logged in
        const storedUser = localStorage.getItem('curatated_user');
        const guestUser = sessionStorage.getItem('curatated_guest');

        if (storedUser || guestUser) {
            userAvatar.addEventListener('click', (e) => {
                e.stopPropagation();
                const isVisible = userDropdown.style.display === 'block';
                userDropdown.style.display = isVisible ? 'none' : 'block';
            });

            // Close dropdown when clicking outside
            document.addEventListener('click', (e) => {
                if (!userAvatar.contains(e.target) && !userDropdown.contains(e.target)) {
                    userDropdown.style.display = 'none';
                }
            });
        }
    }
}

// Generate star rating HTML
function generateStars(rating) {
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;
    let starsHTML = '';

    for (let i = 0; i < fullStars; i++) {
        starsHTML += '';
    }

    if (hasHalfStar) {
        starsHTML += '';
    }

    return starsHTML;
}

// Create canvas item HTML
function createCanvasItem(canvas) {
    return `
        <div class="canvas-item" data-id="${canvas.id}">
            <div class="canvas-preview"></div>
            <div class="canvas-info">
                <h4 class="canvas-title">${canvas.title}</h4>
                <p class="canvas-author">by ${canvas.author}</p>
                <div class="canvas-stats">
                    <div class="rating">
                        <span class="stars">${generateStars(canvas.rating)}</span>
                        <span>${canvas.rating}</span>
                    </div>
                    <div class="likes">${canvas.likes}</div>
                </div>
            </div>
        </div>
    `;
}

// Populate carousels with sample data
function populateCarousels() {
    const carousels = [
        { id: 'popular-carousel', data: sampleCanvases.popular },
        { id: 'recent-carousel', data: sampleCanvases.recent },
        { id: 'art-carousel', data: sampleCanvases.art },
        { id: 'tech-carousel', data: sampleCanvases.tech },
        { id: 'community-carousel', data: sampleCanvases.community }
    ];

    carousels.forEach(carousel => {
        const track = document.querySelector(`#${carousel.id} .carousel-track`);
        if (track) {
            track.innerHTML = carousel.data.map(createCanvasItem).join('');
        }
    });
}

// Setup carousel controls
function setupCarouselControls() {
    const prevButtons = document.querySelectorAll('.carousel-btn-prev');
    const nextButtons = document.querySelectorAll('.carousel-btn-next');

    prevButtons.forEach(button => {
        button.addEventListener('click', () => {
            const carouselId = button.getAttribute('data-carousel');
            moveCarousel(carouselId, -1);
        });
    });

    nextButtons.forEach(button => {
        button.addEventListener('click', () => {
            const carouselId = button.getAttribute('data-carousel');
            moveCarousel(carouselId, 1);
        });
    });

    // Add canvas item click handlers
    document.addEventListener('click', (event) => {
        const canvasItem = event.target.closest('.canvas-item');
        if (canvasItem) {
            const canvasId = canvasItem.getAttribute('data-id');
            handleCanvasClick(canvasId);
        }
    });
}

// Move carousel
function moveCarousel(carouselId, direction) {
    const carousel = document.getElementById(carouselId);
    const track = carousel.querySelector('.carousel-track');
    const items = track.querySelectorAll('.canvas-item');
    const state = carouselState[carouselId];

    if (!items.length) return;

    const totalItems = items.length;
    const maxIndex = Math.max(0, totalItems - state.itemsPerView);

    state.currentIndex += direction;
    state.currentIndex = Math.max(0, Math.min(state.currentIndex, maxIndex));

    const itemWidth = items[0].offsetWidth + 16; // Including gap
    const translateX = -state.currentIndex * itemWidth;

    track.style.transform = `translateX(${translateX}px)`;

    // Update button states
    updateCarouselButtons(carouselId, state.currentIndex, maxIndex);
}

// Update carousel button states
function updateCarouselButtons(carouselId, currentIndex, maxIndex) {
    const carousel = document.getElementById(carouselId);
    const prevBtn = carousel.querySelector('.carousel-btn-prev');
    const nextBtn = carousel.querySelector('.carousel-btn-next');

    prevBtn.style.opacity = currentIndex === 0 ? '0.5' : '1';
    prevBtn.style.cursor = currentIndex === 0 ? 'not-allowed' : 'pointer';

    nextBtn.style.opacity = currentIndex >= maxIndex ? '0.5' : '1';
    nextBtn.style.cursor = currentIndex >= maxIndex ? 'not-allowed' : 'pointer';
}

// Handle canvas item click
function handleCanvasClick(canvasId) {
    console.log(`Canvas clicked: ${canvasId}`);

    // Find the canvas data
    let canvasData = null;
    for (const category in sampleCanvases) {
        canvasData = sampleCanvases[category].find(c => c.id == canvasId);
        if (canvasData) break;
    }

    if (canvasData) {
        showCanvasModal(canvasData);
    }
}

// Show canvas modal
function showCanvasModal(canvas) {
    const modal = document.createElement('div');
    modal.innerHTML = `
        <div style="
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0,0,0,0.8);
            z-index: 1000;
            display: flex;
            align-items: center;
            justify-content: center;
        " onclick="this.remove()">
            <div style="
                background: var(--bg-secondary);
                border: 1px solid var(--border-color);
                border-radius: 12px;
                padding: 2rem;
                max-width: 500px;
                width: 90%;
                color: var(--text-primary);
            " onclick="event.stopPropagation()">
                <div style="
                    width: 100%;
                    height: 200px;
                    background: linear-gradient(135deg, var(--accent-green), var(--accent-orange));
                    border-radius: 8px;
                    margin-bottom: 1.5rem;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    font-size: 3rem;
                "></div>
                <h2 style="margin-bottom: 0.5rem; color: var(--text-primary);">${canvas.title}</h2>
                <p style="color: var(--text-muted); margin-bottom: 1rem;">by ${canvas.author}</p>
                <div style="display: flex; justify-content: space-between; align-items: center; margin-bottom: 1.5rem;">
                    <div style="color: var(--accent-orange);">
                        ${generateStars(canvas.rating)} ${canvas.rating}
                    </div>
                    <div style="color: var(--text-muted);"> ${canvas.likes} likes</div>
                </div>
                <p style="color: var(--text-secondary); margin-bottom: 1.5rem;">
                    This is a preview of the "${canvas.title}" canvas. Click "View Canvas" to explore the full interactive experience.
                </p>
                <div style="display: flex; gap: 1rem; justify-content: flex-end;">
                    <button onclick="this.closest('div[onclick]').remove()" style="
                        background: var(--bg-tertiary);
                        color: var(--text-primary);
                        border: 1px solid var(--border-color);
                        padding: 0.5rem 1rem;
                        border-radius: 4px;
                        cursor: pointer;
                    ">Close</button>
                    <button onclick="window.location.href='./canvas.html?id=${canvas.id}'" style="
                        background: var(--accent-green);
                        color: var(--bg-primary);
                        border: none;
                        padding: 0.5rem 1rem;
                        border-radius: 4px;
                        cursor: pointer;
                        font-weight: 500;
                    ">View Canvas</button>
                </div>
            </div>
        </div>
    `;

    document.body.appendChild(modal);
}

// Setup responsive carousels
function setupResponsiveCarousels() {
    function updateItemsPerView() {
        const width = window.innerWidth;
        let itemsPerView = 5;

        if (width < 768) {
            itemsPerView = 2;
        } else if (width < 1024) {
            itemsPerView = 3;
        } else if (width < 1200) {
            itemsPerView = 4;
        }

        // Update all carousel states
        Object.keys(carouselState).forEach(carouselId => {
            carouselState[carouselId].itemsPerView = itemsPerView;
            carouselState[carouselId].currentIndex = 0;

            // Reset carousel position
            const track = document.querySelector(`#${carouselId} .carousel-track`);
            if (track) {
                track.style.transform = 'translateX(0px)';
            }
        });
    }

    updateItemsPerView();
    window.addEventListener('resize', updateItemsPerView);
}

// Initialize button states on load
window.addEventListener('load', () => {
    Object.keys(carouselState).forEach(carouselId => {
        const carousel = document.getElementById(carouselId);
        const track = carousel?.querySelector('.carousel-track');
        const items = track?.querySelectorAll('.canvas-item');

        if (items?.length) {
            const maxIndex = Math.max(0, items.length - carouselState[carouselId].itemsPerView);
            updateCarouselButtons(carouselId, 0, maxIndex);
        }
    });
});

