            print(f"üìù Step 1: Selecting {tool_name}...")
            button = self.driver.find_element(By.CSS_SELECTOR, f"button[onclick*='{tool_id}']")
            self.highlight_element(button, 1)
            location = button.location
            size = button.size
            center_x = location['x'] + size['width'] // 2
            center_y = location['y'] + size['height'] // 2
            self.show_cursor_pointer(center_x, center_y, 1)
            self.actions.move_to_element(button).click().perform()
            time.sleep(2)

            # Step 2: Test pointer drag
            print(f"üìù Step 2: Testing pointer drag with {tool_name}...")
            pointer_success = self.test_pointer_drag_on_canvas(tool_name)

            # Step 3: Test three-finger drag
            print(f"üìù Step 3: Testing three-finger drag with {tool_name}...")
            three_finger_success = self.test_three_finger_drag_visual(tool_name)

            # Step 4: Compare results
            print(f"üìù Step 4: Comparing results...")
            if pointer_success and three_finger_success:
                print(f"‚úÖ BOTH methods tested successfully for {tool_name}")
            elif pointer_success:
                print(f"‚ö†Ô∏è POINTER works, THREE-FINGER needs investigation for {tool_name}")
            elif three_finger_success:
                print(f"‚ö†Ô∏è THREE-FINGER works, POINTER needs investigation for {tool_name}")
            else:
                print(f"‚ùå BOTH methods failed for {tool_name}")

            time.sleep(3)
            return True

        except Exception as e:
            print(f"‚ùå Complete workflow failed for {tool_name}: {e}")
            return False

    def run_complete_visual_test(self):
        """Run complete visual diagnostic test"""
        print("üé¨ STARTING COMPLETE VISUAL DIAGNOSTIC TEST")
        print("=" * 60)

        if not self.start_server_if_needed():
            return False

        if not self.setup_browser():
            return False

        try:
            if not self.navigate_to_canvas():
                return False

            print("\nüëÅÔ∏è VISUAL TESTING STARTED - Watch the browser window!")
            time.sleep(3)

            # Test tool selection
            self.test_tool_selection_visual()

            # Test three-finger drag
            self.test_three_finger_drag_visual()

            print("\n‚úÖ VISUAL DIAGNOSTIC TEST COMPLETED")
            print("üìä Check browser window for visual feedback")

            # Keep browser open for user to see results
            input("\nPress Enter to close browser and exit...")

            return True

        except Exception as e:
            print(f"‚ùå Error during visual testing: {e}")
            return False

        finally:
            if self.driver:
                self.driver.quit()

def main():
    print("üöÄ Claude's Visual Diagnostic Runner")
    print("üëÅÔ∏è This will show you EXACTLY what I'm testing with visible pointer movements")
    print()

    runner = VisualDiagnosticRunner()

    try:
        success = runner.run_complete_visual_test()
        if success:
            print("üéØ Visual diagnostic test completed successfully!")
        else:
            print("‚ùå Visual diagnostic test failed!")

    except KeyboardInterrupt:
        print("\n‚ö†Ô∏è Test interrupted by user")
    except Exception as e:
        print(f"‚ùå Unexpected error: {e}")

    print("\nüëã Visual diagnostic runner finished")

if __name__ == "__main__":
    main()

