#!/usr/bin/env python3
"""
Visual Diagnostic Runner - Claude's Automated Testing with Visible Pointer
Shows real pointer movement and clicks on the actual canvas page
"""

import time
import subprocess
import sys
import os
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.chrome.options import Options
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.common.exceptions import TimeoutException, NoSuchElementException

class VisualDiagnosticRunner:
    def __init__(self, server_url="http://localhost:8000"):
        self.server_url = server_url
        self.driver = None
        self.actions = None

    def setup_browser(self):
        """Setup Chrome browser with visible automation"""
        print("üîß Setting up Chrome browser for visual diagnostics...")

        chrome_options = Options()
        # Make browser visible and slow for demonstration
        chrome_options.add_argument("--no-sandbox")
        chrome_options.add_argument("--disable-dev-shm-usage")
        chrome_options.add_argument("--window-size=1400,900")
        chrome_options.add_argument("--start-maximized")
        # Don't use headless mode - we want to see the browser

        try:
            self.driver = webdriver.Chrome(options=chrome_options)
            self.actions = ActionChains(self.driver)

            # Slow down actions for visibility
            self.actions.reset_actions()

            print("‚úÖ Chrome browser initialized successfully")
            return True
        except Exception as e:
            print(f"‚ùå Failed to initialize browser: {e}")
            return False

    def start_server_if_needed(self):
        """Check if server is running, start if needed"""
        print("üåê Checking if server is running...")

        try:
            # Try to connect to server
            import requests
            response = requests.get(self.server_url, timeout=3)
            print(f"‚úÖ Server is running at {self.server_url}")
            return True
        except:
            print("üöÄ Starting Python server...")
            try:
                # Start server in background
                subprocess.Popen([sys.executable, "start-server.py"], 
                               cwd=os.getcwd(),
                               stdout=subprocess.DEVNULL,
                               stderr=subprocess.DEVNULL)

                # Wait for server to start
                time.sleep(3)
                return True
            except Exception as e:
                print(f"‚ùå Failed to start server: {e}")
                return False

    def navigate_to_canvas(self):
        """Navigate to the canvas page with visual feedback"""
        print(f"üìç Navigating to canvas page: {self.server_url}/public/canvas.html")

        try:
            self.driver.get(f"{self.server_url}/public/canvas.html")

            # Wait for page to load
            WebDriverWait(self.driver, 10).until(
                EC.presence_of_element_located((By.TAG_NAME, "body"))
            )

            print("‚úÖ Canvas page loaded successfully")
            time.sleep(2)  # Give user time to see the page
            return True

        except TimeoutException:
            print("‚ùå Timeout waiting for canvas page to load")
            return False
        except Exception as e:
            print(f"‚ùå Error navigating to canvas: {e}")
            return False

    def highlight_element(self, element, duration=2):
        """Add visual highlighting to an element"""
        try:
            # Add red border to element for visibility
            self.driver.execute_script("""
                arguments[0].style.border = '3px solid red';
                arguments[0].style.boxShadow = '0 0 15px red';
                arguments[0].style.backgroundColor = 'rgba(255, 0, 0, 0.1)';
            """, element)

            time.sleep(duration)

            # Remove highlighting
            self.driver.execute_script("""
                arguments[0].style.border = '';
                arguments[0].style.boxShadow = '';
                arguments[0].style.backgroundColor = '';
            """, element)

        except Exception as e:
            print(f"‚ö†Ô∏è Could not highlight element: {e}")

    def show_cursor_pointer(self, x, y, duration=1):
        """Show a visible pointer at coordinates"""
        try:
            self.driver.execute_script(f"""
                // Create cursor indicator
                var cursor = document.createElement('div');
                cursor.id = 'claude-cursor';
                cursor.style.position = 'fixed';
                cursor.style.left = '{x}px';
                cursor.style.top = '{y}px';
                cursor.style.width = '20px';
                cursor.style.height = '20px';
                cursor.style.backgroundColor = 'red';
                cursor.style.borderRadius = '50%';
                cursor.style.border = '3px solid white';
                cursor.style.boxShadow = '0 0 15px rgba(255, 0, 0, 0.8)';
                cursor.style.zIndex = '999999';
                cursor.style.pointerEvents = 'none';
                cursor.style.animation = 'pulse 0.5s infinite';

                // Add pulse animation
                var style = document.createElement('style');
                style.textContent = `
                    @keyframes pulse {{
                        0% {{ transform: scale(1); opacity: 1; }}
                        50% {{ transform: scale(1.3); opacity: 0.7; }}
                        100% {{ transform: scale(1); opacity: 1; }}
                    }}
                `;
                document.head.appendChild(style);
                document.body.appendChild(cursor);

                // Remove after duration
                setTimeout(() => {{
                    var cursor = document.getElementById('claude-cursor');
                    if (cursor) cursor.remove();
                }}, {duration * 1000});
            """)

            time.sleep(duration)

        except Exception as e:
            print(f"‚ö†Ô∏è Could not show cursor pointer: {e}")

    def test_tool_selection_visual(self):
        """Test tool selection with visible pointer movements"""
        print("\nüéØ VISUAL TEST: Tool Selection")
        print("üëÅÔ∏è Watch the browser - you should see red highlighting and cursor movement")

        tools = [
            ("select", "Select Tool"),
            ("rectangle", "Rectangle Tool"), 
            ("ellipse", "Ellipse Tool"),
            ("eraser", "Eraser Tool")
        ]

        for tool_id, tool_name in tools:
            print(f"\nüîß Testing {tool_name}...")

            try:
                # Find tool button
                button_selector = f"button[onclick*='{tool_id}'], #{tool_id}-tool, .tool-{tool_id}"

                # Try multiple possible selectors
                button = None
                selectors_to_try = [
                    f"button[onclick*='{tool_id}']",
                    f"#{tool_id}-tool",
                    f".tool-{tool_id}",
                    f"button:contains('{tool_name}')",
                    f"[data-tool='{tool_id}']"
                ]

                for selector in selectors_to_try:
                    try:
                        button = self.driver.find_element(By.CSS_SELECTOR, selector)
                        break
                    except:
                        continue

                if not button:
                    print(f"‚ùå Could not find {tool_name} button")
                    continue

                # Get button position
                location = button.location
                size = button.size
                center_x = location['x'] + size['width'] // 2
                center_y = location['y'] + size['height'] // 2

                print(f"üìç Button found at ({center_x}, {center_y})")

                # Show cursor pointer at button location
                self.show_cursor_pointer(center_x, center_y, 2)

                # Highlight the button
                self.highlight_element(button, 2)

                # Move mouse to button slowly (visible movement)
                self.actions.move_to_element(button).perform()
                time.sleep(1)

                # Click with visual feedback
                print(f"üëÜ Clicking {tool_name}...")
                self.actions.click(button).perform()

                # Show click feedback
                self.show_cursor_pointer(center_x, center_y, 1)

                time.sleep(2)

                print(f"‚úÖ {tool_name} clicked successfully")

            except Exception as e:
                print(f"‚ùå Error testing {tool_name}: {e}")

    def find_canvas_element(self):
        """Find the drawing canvas - try multiple selectors"""
        canvas_selectors = [
            "canvas",  # Traditional canvas
            "svg",     # SVG-based canvas
            ".tldraw-canvas",
            "#canvas", 
            ".canvas-container",
            "[data-testid='canvas']",
            ".tl-canvas",
            ".drawing-surface"
        ]

        for selector in canvas_selectors:
            try:
                element = self.driver.find_element(By.CSS_SELECTOR, selector)
                print(f"üìç Found canvas using selector: {selector}")
                return element
            except:
                continue

        print("‚ùå No canvas element found with any selector")
        return None

    def test_pointer_drag_on_canvas(self, tool_name):
        """Test pointer drag after tool selection"""
        print(f"\nüñ±Ô∏è VISUAL TEST: Pointer Drag with {tool_name} Tool")
        print("üëÅÔ∏è Watch pointer drag on canvas to test tool functionality")

        try:
            canvas = self.find_canvas_element()
            if not canvas:
                print("‚ùå Cannot test pointer drag - canvas not found")
                return False

            # Get canvas position and size
            location = canvas.location
            size = canvas.size

            start_x = location['x'] + 100
            start_y = location['y'] + 100
            end_x = location['x'] + 250
            end_y = location['y'] + 150

            print(f"üìç Canvas found at ({location['x']}, {location['y']}) size ({size['width']}x{size['height']})")
            print(f"üñ±Ô∏è Performing pointer drag from ({start_x}, {start_y}) to ({end_x}, {end_y})")

            # Show drag start indicator
            self.show_cursor_pointer(start_x, start_y, 1)

            # Perform actual mouse drag
            self.actions.move_to_element_with_offset(canvas, 100, 100).perform()
            time.sleep(0.5)

            self.actions.click_and_hold().perform()
            time.sleep(0.5)

            self.actions.move_to_element_with_offset(canvas, 250, 150).perform()
            time.sleep(1)

            self.actions.release().perform()

            # Show drag end indicator
            self.show_cursor_pointer(end_x, end_y, 1)

            print(f"‚úÖ Pointer drag completed with {tool_name} tool")

            # Wait to see if shape was created
            time.sleep(2)

            return True

        except Exception as e:
            print(f"‚ùå Error testing pointer drag: {e}")
            return False

    def test_three_finger_drag_visual(self, tool_name):
        """Test three-finger drag with visual indicators"""
        print(f"\nüëÜ VISUAL TEST: Three-Finger Drag with {tool_name} Tool")
        print("üëÅÔ∏è Watch for three touch point indicators on canvas")

        try:
            canvas = self.find_canvas_element()
            if not canvas:
                print("‚ùå Cannot test three-finger drag - canvas not found")
                return False

            # Get canvas position and size
            location = canvas.location
            size = canvas.size

            start_x = location['x'] + 120
            start_y = location['y'] + 120
            end_x = location['x'] + 280
            end_y = location['y'] + 180

            print(f"üìç Canvas found at ({location['x']}, {location['y']})")
            print(f"üëÜ Simulating three-finger drag from ({start_x}, {start_y}) to ({end_x}, {end_y})")

            # Show three touch points at start
            self.driver.execute_script(f"""
                // Create three touch indicators
                var indicators = [];
                var positions = [
                    {{x: {start_x}, y: {start_y}, color: 'blue'}},
                    {{x: {start_x + 30}, y: {start_y + 15}, color: 'green'}},
                    {{x: {start_x + 60}, y: {start_y - 10}, color: 'orange'}}
                ];

                positions.forEach((pos, index) => {{
                    var indicator = document.createElement('div');
                    indicator.className = 'touch-indicator-' + index;
                    indicator.style.position = 'fixed';
                    indicator.style.left = pos.x + 'px';
                    indicator.style.top = pos.y + 'px';
                    indicator.style.width = '15px';
                    indicator.style.height = '15px';
                    indicator.style.backgroundColor = pos.color;
                    indicator.style.borderRadius = '50%';
                    indicator.style.border = '2px solid white';
                    indicator.style.boxShadow = '0 0 10px ' + pos.color;
                    indicator.style.zIndex = '999999';
                    indicator.style.pointerEvents = 'none';
                    document.body.appendChild(indicator);
                    indicators.push(indicator);
                }});

                // Animate to end positions
                setTimeout(() => {{
                    var endPositions = [
                        {{x: {end_x}, y: {end_y}}},
                        {{x: {end_x + 30}, y: {end_y + 15}}},
                        {{x: {end_x + 60}, y: {end_y - 10}}}
                    ];

                    indicators.forEach((indicator, index) => {{
                        indicator.style.transition = 'all 1s ease';
                        indicator.style.left = endPositions[index].x + 'px';
                        indicator.style.top = endPositions[index].y + 'px';
                    }});

                    // Remove indicators after animation
                    setTimeout(() => {{
                        indicators.forEach(indicator => indicator.remove());
                    }}, 2000);
                }}, 1000);
            """)

            # Simulate touch events on canvas
            try:
                # Dispatch touch events to canvas
                self.driver.execute_script(f"""
                    var canvas = arguments[0];
                    var rect = canvas.getBoundingClientRect();

                    // Create touch start event
                    var touchStart = new TouchEvent('touchstart', {{
                        touches: [
                            new Touch({{
                                identifier: 1,
                                target: canvas,
                                clientX: {start_x},
                                clientY: {start_y}
                            }}),
                            new Touch({{
                                identifier: 2,
                                target: canvas,
                                clientX: {start_x + 30},
                                clientY: {start_y + 15}
                            }}),
                            new Touch({{
                                identifier: 3,
                                target: canvas,
                                clientX: {start_x + 60},
                                clientY: {start_y - 10}
                            }})
                        ],
                        bubbles: true
                    }});

                    canvas.dispatchEvent(touchStart);

                    // Create touch move event
                    setTimeout(() => {{
                        var touchMove = new TouchEvent('touchmove', {{
                            touches: [
                                new Touch({{
                                    identifier: 1,
                                    target: canvas,
                                    clientX: {end_x},
                                    clientY: {end_y}
                                }}),
                                new Touch({{
                                    identifier: 2,
                                    target: canvas,
                                    clientX: {end_x + 30},
                                    clientY: {end_y + 15}
                                }}),
                                new Touch({{
                                    identifier: 3,
                                    target: canvas,
                                    clientX: {end_x + 60},
                                    clientY: {end_y - 10}
                                }})
                            ],
                            bubbles: true
                        }});
                        canvas.dispatchEvent(touchMove);
                    }}, 500);

                    // Create touch end event
                    setTimeout(() => {{
                        var touchEnd = new TouchEvent('touchend', {{
                            touches: [],
                            bubbles: true
                        }});
                        canvas.dispatchEvent(touchEnd);
                    }}, 1500);
                """, canvas)

            except Exception as touch_error:
                print(f"‚ö†Ô∏è Touch event simulation failed: {touch_error}")

            # Wait for animation to complete
            time.sleep(4)

            print(f"‚úÖ Three-finger drag visual simulation completed with {tool_name} tool")
            return True

        except Exception as e:
            print(f"‚ùå Error testing three-finger drag: {e}")
            return False

    def complete_tool_test_workflow(self, tool_id, tool_name):
        """Complete workflow: select tool ‚Üí test pointer ‚Üí test three-finger ‚Üí compare"""
        print(f"\nüîÑ COMPLETE WORKFLOW TEST: {tool_name}")
        print("=" * 50)

        try:
            # Step 1: Select tool (already working)
            print(f"üìù Step 1: Selecting {tool_name}...")
            button = self.driver.find_element(By.CSS_SELECTOR, f"button[onclick*='{tool_id}']")
            self.highlight_element(button, 1)
            location = button.location
            size = button.size
            center_x = location['x'] + size['width'] // 2
            center_y = location['y'] + size['height'] // 2
            self.show_cursor_pointer(center_x, center_y, 1)
            self.actions.move_to_element(button).click().perform()
            time.sleep(2)

            # Step 2: Test pointer drag
            print(f"üìù Step 2: Testing pointer drag with {tool_name}...")
            pointer_success = self.test_pointer_drag_on_canvas(tool_name)

            # Step 3: Test three-finger drag
            print(f"üìù Step 3: Testing three-finger drag with {tool_name}...")
            three_finger_success = self.test_three_finger_drag_visual(tool_name)

            # Step 4: Compare results
            print(f"üìù Step 4: Comparing results...")
            if pointer_success and three_finger_success:
                print(f"‚úÖ BOTH methods tested successfully for {tool_name}")
            elif pointer_success:
                print(f"‚ö†Ô∏è POINTER works, THREE-FINGER needs investigation for {tool_name}")
            elif three_finger_success:
                print(f"‚ö†Ô∏è THREE-FINGER works, POINTER needs investigation for {tool_name}")
            else:
                print(f"‚ùå BOTH methods failed for {tool_name}")

            time.sleep(3)
            return True

        except Exception as e:
            print(f"‚ùå Complete workflow failed for {tool_name}: {e}")
            return False

    def run_complete_visual_test(self):
        """Run complete visual diagnostic test"""
