#!/usr/bin/env python3
"""
Enhanced Diagnostic Runner with Canvas Detection and Shape Validation
"""

import time
import subprocess
import sys
import os
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.chrome.options import Options
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC

class EnhancedDiagnosticRunner:
    def __init__(self, server_url="http://localhost:8000"):
        self.server_url = server_url
        self.driver = None
        self.actions = None

    def setup_browser(self):
        """Setup Chrome browser with enhanced capabilities"""
        print("üîß Setting up enhanced Chrome browser...")

        chrome_options = Options()
        chrome_options.add_argument("--no-sandbox")
        chrome_options.add_argument("--disable-dev-shm-usage")
        chrome_options.add_argument("--window-size=1400,900")
        chrome_options.add_argument("--start-maximized")

        self.driver = webdriver.Chrome(options=chrome_options)
        self.actions = ActionChains(self.driver)
        return True

    def find_actual_canvas(self):
        """Find the actual drawing canvas area, not just any SVG"""
        print("üé® Searching for actual drawing canvas...")

        # Try to find canvas container first
        canvas_container_selectors = [
            "#canvas-container",
            ".canvas-container", 
            "#tldraw-container",
            ".tldraw-container",
            "[data-testid='canvas-container']"
        ]

        for selector in canvas_container_selectors:
            try:
                container = self.driver.find_element(By.CSS_SELECTOR, selector)
                print(f"üìç Found canvas container: {selector}")
                location = container.location
                size = container.size
                print(f"   Container at ({location['x']}, {location['y']}) size ({size['width']}x{size['height']})")

                # Look for actual drawing area within container
                drawing_selectors = ["svg", "canvas", ".drawing-surface"]
                for draw_sel in drawing_selectors:
                    try:
                        drawing_area = container.find_element(By.CSS_SELECTOR, draw_sel)
                        draw_location = drawing_area.location
                        draw_size = drawing_area.size
                        print(f"   Drawing area ({draw_sel}) at ({draw_location['x']}, {draw_location['y']}) size ({draw_size['width']}x{draw_size['height']})")

                        # Return the largest drawing area found
                        if draw_size['width'] > 100 and draw_size['height'] > 100:
                            return drawing_area
                    except:
                        continue

                # If no drawing area found, return container itself
                if size['width'] > 200 and size['height'] > 200:
                    return container

            except:
                continue

        # Fallback: find largest SVG that could be the canvas
        try:
            svgs = self.driver.find_elements(By.TAG_NAME, "svg")
            largest_svg = None
            largest_area = 0

            for svg in svgs:
                size = svg.size
                area = size['width'] * size['height']
                if area > largest_area and size['width'] > 200 and size['height'] > 200:
                    largest_area = area
                    largest_svg = svg

            if largest_svg:
                location = largest_svg.location
                size = largest_svg.size
                print(f"üìç Using largest SVG as canvas: ({location['x']}, {location['y']}) size ({size['width']}x{size['height']})")
                return largest_svg

        except:
            pass

        print("‚ùå Could not find actual drawing canvas")
        return None

    def validate_shape_creation(self, tool_name, before_count):
        """Validate that a shape was actually created"""
        print(f"üîç Validating shape creation for {tool_name}...")

        try:
            # Check DOM for new shape elements
            shape_selectors = [
                "svg path",
                "svg rect", 
                "svg ellipse",
                "svg circle",
                ".tl-shape",
                "[data-shape]",
                ".shape"
            ]

            total_shapes = 0
            for selector in shape_selectors:
                try:
                    elements = self.driver.find_elements(By.CSS_SELECTOR, selector)
                    count = len(elements)
                    if count > 0:
                        print(f"   Found {count} elements matching '{selector}'")
                        total_shapes += count
                except:
                    continue

            print(f"   Total shape elements found: {total_shapes}")

            # Check JavaScript state
            try:
                js_check = self.driver.execute_script("""
                    var result = {
                        editorExists: !!window.tlEditor,
                        hasShapes: false,
                        shapeCount: 0,
                        currentTool: 'unknown',
                        errors: []
                    };

                    try {
                        if (window.tlEditor) {
                            result.currentTool = window.tlEditor.getCurrentTool ? window.tlEditor.getCurrentTool() : 'no getCurrentTool';

                            // Try to get shapes from editor
