function setupCanvasEventHandlers() {
    console.log('üéØ Setting up canvas event handlers for shape creation...');

    if (!window.tlEditor || !window.tlEditor.canvas) {
        console.error('‚ùå Cannot setup handlers - no editor or canvas');
        return;
    }

    const canvas = window.tlEditor.canvas;
    let isDrawing = false;
    let startPoint = null;
    let currentShape = null;

    // Mouse/Pointer event handlers
    canvas.addEventListener('pointerdown', (e) => {
        console.log('üéØ POINTERDOWN detected:', e.clientX, e.clientY);

        const currentTool = window.tlEditor.getCurrentTool();
        if (!currentTool || currentTool === 'select') return;

        const rect = canvas.getBoundingClientRect();
        startPoint = {
            x: e.clientX - rect.left,
            y: e.clientY - rect.top
        };

        isDrawing = true;

        // Create shape based on current tool
        if (currentTool === 'rectangle' || currentTool === 'ellipse') {
            createShapeFromUserInput(currentTool, startPoint);
        }

        e.preventDefault();
    });

    canvas.addEventListener('pointermove', (e) => {
        if (!isDrawing || !startPoint || !currentShape) return;

        const rect = canvas.getBoundingClientRect();
        const currentPoint = {
            x: e.clientX - rect.left,
            y: e.clientY - rect.top
        };

        updateShapeFromUserInput(currentPoint);
        e.preventDefault();
    });

    canvas.addEventListener('pointerup', (e) => {
        console.log('üéØ POINTERUP detected');
        isDrawing = false;
        startPoint = null;
        currentShape = null;
        e.preventDefault();
    });

    // Touch event handlers for three-finger gestures
    canvas.addEventListener('touchstart', (e) => {
        console.log('üéØ TOUCHSTART detected - fingers:', e.touches.length);

        if (e.touches.length === 3) {
            // Three-finger gesture detected
            const currentTool = window.tlEditor.getCurrentTool();
            if (!currentTool || currentTool === 'select') return;

            const rect = canvas.getBoundingClientRect();
            const touch = e.touches[0]; // Use first touch as reference
            startPoint = {
                x: touch.clientX - rect.left,
                y: touch.clientY - rect.top
            };

            isDrawing = true;

            // Create shape from three-finger gesture
            if (currentTool === 'rectangle' || currentTool === 'ellipse') {
                createShapeFromUserInput(currentTool, startPoint);
            }

            e.preventDefault();
        }
    });

    canvas.addEventListener('touchmove', (e) => {
        if (e.touches.length === 3 && isDrawing && startPoint && currentShape) {
            const rect = canvas.getBoundingClientRect();
            const touch = e.touches[0];
            const currentPoint = {
                x: touch.clientX - rect.left,
                y: touch.clientY - rect.top
            };

            updateShapeFromUserInput(currentPoint);
            e.preventDefault();
        }
    });

    canvas.addEventListener('touchend', (e) => {
        console.log('üéØ TOUCHEND detected');
        isDrawing = false;
        startPoint = null; 
        currentShape = null;
    });

    console.log('‚úÖ Canvas event handlers setup complete');

    // Helper functions
    function createShapeFromUserInput(tool, point) {
        console.log('üéØ Creating shape from user input:', tool, point);

        try {
            const geoTool = window.tlEditor.rootState.geoTool;
            if (geoTool) {
                const geoCreating = geoTool.children.get('creating');
                if (geoCreating) {
                    currentShape = geoCreating.createGeoShape(point);
                    geoCreating.currentShape = currentShape;
                    geoCreating.startPoint = point;
                    console.log('‚úÖ Shape created:', currentShape);
                } else {
                    console.error('‚ùå GeoCreating state not found');
                }
            } else {
                console.error('‚ùå GeoTool not found');
            }
        } catch (error) {
            console.error('‚ùå Error creating shape:', error);
        }
    }

    function updateShapeFromUserInput(point) {
        try {
            const geoTool = window.tlEditor.rootState.geoTool;
            if (geoTool) {
                const geoCreating = geoTool.children.get('creating');
                if (geoCreating && currentShape) {
                    geoCreating.updateShape(point);
                    console.log('üìê Shape updated to:', point);
                }
            }
        } catch (error) {
            console.error('‚ùå Error updating shape:', error);
        }
    }
}

