const puppeteer = require('puppeteer');
const fs = require('fs').promises;
const path = require('path');

// Chicago timezone formatter
function getChicagoTime() {
  return new Date().toLocaleString('en-US', { 
    timeZone: 'America/Chicago',
    year: 'numeric',
    month: '2-digit', 
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
  });
}

// DOM Elements Catalog with complete mappings
const DOM_CATALOG = {
  header: {
    elements: [
      { id: 'header-toggle-left', capability: 'Toggle left sidebar', functions: ['app.js:794-795', 'toggleToolbar()'] },
      { id: 'header-toggle-right', capability: 'Toggle right sidebar', functions: ['app.js:797-798', 'toggleToolbar()'] },
      { id: 'header-reset-layout', capability: 'Reset panel layout', functions: ['app.js:799', 'resetLayoutSizing()'] },
      { id: 'chrome-status', capability: 'Display Chrome connection status', functions: ['app.js:32-34', 'updateStatus()'] },
      { id: 'connection-status', capability: 'Show active connections count', functions: ['app.js:36-38', 'updateConnectionCount()'] },
      { id: 'header-screenshot-btn', capability: 'Take screenshot', functions: ['app.js:47-49', 'captureScreenshot()'] }
    ]
  },
  leftToolbar: {
    elements: [
      { id: 'leftToolbar', capability: 'Strike Errors & AI File Browser container', functions: ['app.js:56', 'container'] },
      { id: 'leftToolbarToggle', capability: 'Collapse/expand left panel', functions: ['app.js:787-788', 'toggleToolbar()'] },
      { id: 'launch-strike-errors', capability: 'Launch AI code analysis', functions: ['app.js:68-70', 'launchStrikeErrors()'] },
      { id: 'upload-files', capability: 'Upload directory for analysis', functions: ['app.js:648', 'uploadDirectory()'] },
