import puppeteer from 'puppeteer';
import { promises as fs } from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Chicago timezone formatter
function getChicagoTime() {
  return new Date().toLocaleString('en-US', { 
    timeZone: 'America/Chicago',
    year: 'numeric',
    month: '2-digit', 
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
  });
}

async function captureScreenshots() {
  console.log(`[${getChicagoTime()}] Starting Chrome Container screenshot capture...`);

  const browser = await puppeteer.launch({ headless: 'new' });
  const page = await browser.newPage();

  // Set viewport to match typical desktop resolution
  await page.setViewport({ width: 1920, height: 1080 });

  try {
    // Navigate to the Chrome Container HTML file
    const htmlPath = path.join(__dirname, 'apps/chrome-container/chrome-container-app/renderer/index.html');
    await page.goto(`file://${htmlPath}`, { waitUntil: 'networkidle0', timeout: 10000 });

    // Wait for potential JavaScript initialization
    await page.waitForTimeout(2000);

    // Create screenshots directory
    const screenshotsDir = path.join(__dirname, 'chrome-container-screenshots');
    try {
      await fs.mkdir(screenshotsDir, { recursive: true });
    } catch (error) {
      // Directory might already exist
    }

    // Capture full page screenshot
    const fullPagePath = path.join(screenshotsDir, 'chrome-container-full-page.png');
    await page.screenshot({ 
      path: fullPagePath, 
      fullPage: true,
      quality: 95
    });
    console.log(`[${getChicagoTime()}] Captured full page screenshot: ${fullPagePath}`);

    // Capture viewport screenshot (what user sees)
    const viewportPath = path.join(screenshotsDir, 'chrome-container-viewport.png');
    await page.screenshot({ 
      path: viewportPath,
      quality: 95
    });
    console.log(`[${getChicagoTime()}] Captured viewport screenshot: ${viewportPath}`);

    // Try to expand left toolbar if collapsed
    try {
      const leftToggle = await page.$('#leftToolbarToggle');
      if (leftToggle) {
        await leftToggle.click();
        await page.waitForTimeout(500);

        const leftExpandedPath = path.join(screenshotsDir, 'chrome-container-left-toolbar-expanded.png');
        await page.screenshot({ 
          path: leftExpandedPath,
          quality: 95
        });
        console.log(`[${getChicagoTime()}] Captured left toolbar expanded: ${leftExpandedPath}`);
      }
    } catch (error) {
      console.log(`[${getChicagoTime()}] Could not expand left toolbar: ${error.message}`);
    }

    // Try to expand right toolbar if collapsed
    try {
      const rightToggle = await page.$('#rightToolbarToggle');
      if (rightToggle) {
        await rightToggle.click();
        await page.waitForTimeout(500);

        const rightExpandedPath = path.join(screenshotsDir, 'chrome-container-right-toolbar-expanded.png');
        await page.screenshot({ 
          path: rightExpandedPath,
          quality: 95
        });
        console.log(`[${getChicagoTime()}] Captured right toolbar expanded: ${rightExpandedPath}`);
      }
    } catch (error) {
      console.log(`[${getChicagoTime()}] Could not expand right toolbar: ${error.message}`);
    }

    // Capture specific sections
    const sections = [
      { selector: 'header', name: 'header' },
      { selector: '.left-toolbar', name: 'left-toolbar' },
      { selector: '.chrome-viewport', name: 'central-viewport' },
      { selector: '.right-toolbar', name: 'right-toolbar' },
      { selector: 'footer', name: 'footer' }
    ];

    for (const section of sections) {
      try {
        const element = await page.$(section.selector);
        if (element) {
          const sectionPath = path.join(screenshotsDir, `chrome-container-${section.name}.png`);
          await element.screenshot({ path: sectionPath, quality: 95 });
          console.log(`[${getChicagoTime()}] Captured ${section.name} section: ${sectionPath}`);
        }
      } catch (error) {
        console.log(`[${getChicagoTime()}] Could not capture ${section.name}: ${error.message}`);
      }
    }

  } catch (error) {
    console.error(`[${getChicagoTime()}] Error during screenshot capture: ${error.message}`);
  }

  await browser.close();
  console.log(`[${getChicagoTime()}] Screenshot capture completed.`);
}

// Run the capture
captureScreenshots().catch(console.error);

