// Main app JavaScript - handles UI interactions and screenshot functionality

document.addEventListener('DOMContentLoaded', function () {
  // Get screenshot button - handle both IDs for compatibility
  const screenshotBtn =
    document.getElementById('take-screenshot-global') || document.getElementById('take-screenshot');

  // Handle screenshot button click
  if (screenshotBtn) {
    screenshotBtn.addEventListener('click', async function () {
      try {
        // Disable button to prevent multiple clicks
        screenshotBtn.disabled = true;
        screenshotBtn.textContent = 'ğŸ“¸ Capturing...';

        // Check if we're in Electron environment
        if (window.electronAPI && window.electronAPI.takeScreenshot) {
          // Use Electron API to take screenshot
          const result = await window.electronAPI.takeScreenshot();

          if (result.success) {
            // Show success feedback
            screenshotBtn.textContent = 'âœ… Captured!';

            // Open planning tab in renderer window if available
            if (window.electronAPI.openPlanningTab) {
              await window.electronAPI.openPlanningTab();
            }

            // Reset button after 2 seconds
            setTimeout(() => {
              screenshotBtn.textContent = 'ğŸ“¸';
              screenshotBtn.disabled = false;
            }, 2000);
          } else {
            throw new Error(result.error || 'Screenshot failed');
          }
        } else {
          // Fallback for non-Electron environment (browser)
          console.log('Taking screenshot using web API...');

          // Try to use the Screen Capture API
          if (navigator.mediaDevices && navigator.mediaDevices.getDisplayMedia) {
            const stream = await navigator.mediaDevices.getDisplayMedia({
              video: { mediaSource: 'screen' },
            });

            const video = document.createElement('video');
            video.srcObject = stream;
            video.play();

            // Wait for video to load
            await new Promise(resolve => {
              video.onloadedmetadata = resolve;
            });

            // Create canvas and capture screenshot
            const canvas = document.createElement('canvas');
            canvas.width = video.videoWidth;
            canvas.height = video.videoHeight;
            const ctx = canvas.getContext('2d');
            ctx.drawImage(video, 0, 0);

            // Stop the stream
            stream.getTracks().forEach(track => track.stop());

            // Convert to data URL
            const dataUrl = canvas.toDataURL('image/png');

            // Store in sessionStorage for access from planning tab
            const screenshot = {
              id: Date.now().toString(),
              timestamp: Date.now(),
              dataUrl: dataUrl,
            };

            // Get existing screenshots or initialize array
            const screenshots = JSON.parse(sessionStorage.getItem('screenshots') || '[]');
            screenshots.unshift(screenshot);
            sessionStorage.setItem('screenshots', JSON.stringify(screenshots));

            // Show success
            screenshotBtn.textContent = 'âœ… Captured!';

            // Open planning tab/page
            // Check if we have a renderer window open
            const rendererWindow = window.open('', 'renderer-window');
            if (rendererWindow && !rendererWindow.closed) {
              // Focus the renderer window and trigger planning tab
              rendererWindow.focus();
              rendererWindow.postMessage(
                {
                  type: 'open-planning-tab',
                  screenshot: screenshot,
                },
                '*'
              );
            }

            // Reset button
            setTimeout(() => {
              screenshotBtn.textContent = 'ğŸ“¸';
              screenshotBtn.disabled = false;
            }, 2000);
          } else {
            throw new Error('Screen capture not supported in this browser');
          }
        }
      } catch (error) {
        console.error('Screenshot error:', error);
        screenshotBtn.textContent = 'âŒ Failed';

        // Show error message
        alert('Failed to take screenshot: ' + error.message);

        // Reset button
        setTimeout(() => {
          screenshotBtn.textContent = 'ğŸ“¸';
          screenshotBtn.disabled = false;
        }, 2000);
      }
    });
  }

  // Handle user dropdown
  const userAvatarBtn = document.getElementById('user-avatar-btn');
  const userDropdown = document.getElementById('user-dropdown');

  if (userAvatarBtn && userDropdown) {
    userAvatarBtn.addEventListener('click', function (e) {
      e.stopPropagation();
      userDropdown.style.display = userDropdown.style.display === 'none' ? 'block' : 'none';
    });

    // Close dropdown when clicking outside
    document.addEventListener('click', function () {
      userDropdown.style.display = 'none';
    });
  }

  // Handle main action button - unified across UI systems
  const mainActionBtn = document.getElementById('main-action-btn');
  if (mainActionBtn) {
    mainActionBtn.addEventListener('click', function () {
      // Check if we're in Electron context
      if (window.electronAPI && window.electronAPI.navigateCanvas) {
        window.electronAPI.navigateCanvas();
      } else {
        // Fallback for web context
        window.location.href = 'canvas.html';
      }
    });
  }
});

// Logout function
window.logout = function () {
  if (window.confirm('Are you sure you want to sign out?')) {
    // Clear any stored auth data
    localStorage.removeItem('userToken');
    sessionStorage.clear();

    // Redirect to auth page
    window.location.href = 'auth.html';
  }
};

