// Authentication system for Curatated
// User state management
let currentUser = {
  id: null,
  username: null,
  email: null,
  displayName: null,
  isGuest: false,
  isAuthenticated: false,
  profile: {
    avatar: null,
    bio: null,
    stats: {
      canvases: 0,
      lists: 0,
      following: 0,
      followers: 0,
      likes: 0,
    },
  },
};

// Initialize authentication
document.addEventListener('DOMContentLoaded', function () {
  console.log('Auth system loading...');
  initializeAuth();
  setupEventListeners();
  checkExistingSession();
});

// Initialize authentication system
function initializeAuth() {
  // Setup tab switching
  const tabButtons = document.querySelectorAll('.auth-tab');

  tabButtons.forEach(button => {
    button.addEventListener('click', () => {
      const tabName = button.dataset.tab;
      switchTab(tabName);
    });
  });
}

// Switch between auth tabs
function switchTab(tabName) {
  // Remove active class from all tabs and forms
  document.querySelectorAll('.auth-tab').forEach(tab => tab.classList.remove('active'));
  document.querySelectorAll('.auth-form').forEach(form => form.classList.remove('active'));

  // Add active class to selected tab and form
  document.querySelector(`[data-tab="${tabName}"]`).classList.add('active');
  document.getElementById(`${tabName}-form`).classList.add('active');
}

// Setup event listeners
function setupEventListeners() {
  // Sign in form
  const signinForm = document.getElementById('signin-form-element');
  if (signinForm) {
    signinForm.addEventListener('submit', handleSignIn);
  }

  // Sign up form
  const signupForm = document.getElementById('signup-form-element');
  if (signupForm) {
    signupForm.addEventListener('submit', handleSignUp);
  }

  // Guest form
  const guestForm = document.getElementById('guest-form-element');
  if (guestForm) {
    guestForm.addEventListener('submit', handleGuestLogin);
  }

  // Social auth buttons
  const googleSigninBtn = document.getElementById('google-signin');
  const googleSignupBtn = document.getElementById('google-signup');

  if (googleSigninBtn) {
    googleSigninBtn.addEventListener('click', () => handleSocialAuth('google', 'signin'));
  }

  if (googleSignupBtn) {
    googleSignupBtn.addEventListener('click', () => handleSocialAuth('google', 'signup'));
  }
}

// Handle sign in
async function handleSignIn(e) {
  e.preventDefault();

  const formData = new FormData(e.target);
  const credentials = {
    email: formData.get('email'),
    password: formData.get('password'),
    remember: formData.get('remember') === 'on',
  };

  try {
    showLoadingState(e.target);

    // Simulate API call
    console.log('Signing in user:', credentials);

