// Authentication system for Curatated
// User state management
let currentUser = {
  id: null,
  username: null,
  email: null,
  displayName: null,
  isGuest: false,
  isAuthenticated: false,
  profile: {
    avatar: null,
    bio: null,
    stats: {
      canvases: 0,
      lists: 0,
      following: 0,
      followers: 0,
      likes: 0,
    },
  },
};

// Initialize authentication
document.addEventListener('DOMContentLoaded', function () {
  // Auth system initialization - removed console.log for production
  initializeAuth();
  setupEventListeners();
  checkExistingSession();
});

// Initialize authentication system
function initializeAuth() {
  // Setup tab switching
  const tabButtons = document.querySelectorAll('.auth-tab');

  tabButtons.forEach(button => {
    button.addEventListener('click', () => {
      const tabName = button.dataset.tab;
      switchTab(tabName);
    });
  });
}

// Switch between auth tabs
function switchTab(tabName) {
  // Remove active class from all tabs and forms
  document.querySelectorAll('.auth-tab').forEach(tab => tab.classList.remove('active'));
  document.querySelectorAll('.auth-form').forEach(form => form.classList.remove('active'));

  // Add active class to selected tab and form
  document.querySelector(`[data-tab="${tabName}"]`).classList.add('active');
  document.getElementById(`${tabName}-form`).classList.add('active');
}

// Setup event listeners
function setupEventListeners() {
  // Sign in form
  const signinForm = document.getElementById('signin-form-element');
  if (signinForm) {
    signinForm.addEventListener('submit', handleSignIn);
  }

  // Sign up form
  const signupForm = document.getElementById('signup-form-element');
  if (signupForm) {
    signupForm.addEventListener('submit', handleSignUp);
  }

  // Guest form
  const guestForm = document.getElementById('guest-form-element');
  if (guestForm) {
    guestForm.addEventListener('submit', handleGuestLogin);
  }

  // Social auth buttons
  const googleSigninBtn = document.getElementById('google-signin');
  const googleSignupBtn = document.getElementById('google-signup');

  if (googleSigninBtn) {
    googleSigninBtn.addEventListener('click', () => handleSocialAuth('google', 'signin'));
  }

  if (googleSignupBtn) {
    googleSignupBtn.addEventListener('click', () => handleSocialAuth('google', 'signup'));
  }
}

// Handle sign in
async function handleSignIn(e) {
  e.preventDefault();

  const formData = new FormData(e.target);
  const credentials = {
    email: formData.get('email'),
    password: formData.get('password'),
    remember: formData.get('remember') === 'on',
  };

  try {
    showLoadingState(e.target);

    // Simulate API call
    // Signing in user - debug logging removed for production

    // Mock successful login
    const userData = {
      id: 'user_' + Math.random().toString(36).substr(2, 9),
      username: credentials.email.split('@')[0],
      email: credentials.email,
      displayName: credentials.email.split('@')[0],
      isGuest: false,
      isAuthenticated: true,
      profile: {
        avatar: `https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?w=120&h=120&fit=crop&crop=face`,
        bio: null,
        stats: {
          canvases: Math.floor(Math.random() * 500),
          lists: Math.floor(Math.random() * 50),
          following: Math.floor(Math.random() * 20),
          followers: Math.floor(Math.random() * 100),
          likes: Math.floor(Math.random() * 50),
        },
      },
    };

    // Simulate network delay
    await new Promise(resolve => setTimeout(resolve, 1500));

    // Store user data
    currentUser = userData;
    localStorage.setItem('curatated_user', JSON.stringify(userData));

    showNotification('Welcome back! Signed in successfully.', 'success');

    // Redirect to profile or home
    setTimeout(() => {
      window.location.href = './profile.html';
    }, 1000);
  } catch (error) {
    console.error('Sign in error:', error);
    showNotification('Sign in failed. Please check your credentials.', 'error');
  } finally {
    hideLoadingState(e.target);
  }
}

// Handle sign up
async function handleSignUp(e) {
  e.preventDefault();

  const formData = new FormData(e.target);
  const userData = {
    username: formData.get('username'),
    email: formData.get('email'),
    password: formData.get('password'),
    confirm: formData.get('confirm'),
  };

  // Validate passwords match
  if (userData.password !== userData.confirm) {
    showNotification('Passwords do not match.', 'error');
    return;
  }

  // Validate password strength
  if (userData.password.length < 8) {
    showNotification('Password must be at least 8 characters long.', 'error');
    return;
  }

  try {
    showLoadingState(e.target);

    // Simulate API call
    console.log('Creating user account:', userData);

    // Mock successful registration
    const newUser = {
      id: 'user_' + Math.random().toString(36).substr(2, 9),
      username: userData.username,
      email: userData.email,
      displayName: userData.username,
      isGuest: false,
      isAuthenticated: true,
      profile: {
        avatar: `https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?w=120&h=120&fit=crop&crop=face`,
        bio: null,
        stats: {
          canvases: 0,
          lists: 0,
          following: 0,
          followers: 0,
          likes: 0,
        },
      },
    };

    // Simulate network delay
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Store user data
    currentUser = newUser;
    localStorage.setItem('curatated_user', JSON.stringify(newUser));

    showNotification('Account created successfully! Welcome to Curatated.', 'success');

    // Redirect to profile setup
    setTimeout(() => {
      window.location.href = './profile.html';
    }, 1000);
  } catch (error) {
    console.error('Sign up error:', error);
    showNotification('Account creation failed. Please try again.', 'error');
  } finally {
    hideLoadingState(e.target);
  }
}

// Handle guest login
async function handleGuestLogin(e) {
  e.preventDefault();

  const formData = new FormData(e.target);
  const guestName = formData.get('name') || 'Guest User';

  try {
    showLoadingState(e.target);
    console.log('Starting guest login process...');

    // Create guest user
    const guestUser = {
      id: 'guest_' + Math.random().toString(36).substr(2, 9),
      username: 'guest_' + Math.random().toString(36).substr(2, 4),
      email: null,
      displayName: guestName,
      isGuest: true,
      isAuthenticated: true,
      profile: {
        avatar: null,
        bio: null,
        stats: {
          canvases: 0,
          lists: 0,
          following: 0,
          followers: 0,
          likes: 0,
        },
      },
    };

    console.log('Guest user created:', guestUser);

    // Simulate network delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Store guest data (session only)
    currentUser = guestUser;
    sessionStorage.setItem('curatated_guest', JSON.stringify(guestUser));
    console.log('Guest data stored in sessionStorage');

    showNotification(`Welcome, ${guestName}! You're now browsing as a guest.`, 'success');

    // Redirect to home with a shorter delay and better error handling
    console.log('Preparing to redirect to home page...');
    setTimeout(() => {
      try {
        console.log('Attempting redirect to ./index.html');
        const currentLocation = window.location;
        console.log('Current location:', currentLocation.href);

        // Use relative path from current location
        if (currentLocation.pathname.includes('auth.html')) {
          window.location.replace('./index.html');
        } else {
          window.location.href = './index.html';
        }
      } catch (error) {
        console.error('Redirect error:', error);
        // Try absolute path as fallback
        try {
          window.location.href = '/index.html';
        } catch (fallbackError) {
          console.error('Fallback redirect failed:', fallbackError);
          // Last resort: reload the current page and let auth check handle redirect
          showNotification('Redirecting...', 'info');
          window.location.reload();
        }
      }
    }, 500); // Reduced delay
  } catch (error) {
    console.error('Guest login error:', error);
    showNotification('Guest login failed. Please try again.', 'error');
  } finally {
    hideLoadingState(e.target);
  }
}

// Handle social authentication
async function handleSocialAuth(provider, action) {
  try {
    showNotification(`Connecting to ${provider}...`, 'info');

    // Simulate OAuth flow
    console.log(`${action} with ${provider}`);

    // Mock successful OAuth
    const socialUser = {
      id: `${provider}_` + Math.random().toString(36).substr(2, 9),
      username: 'user_' + Math.random().toString(36).substr(2, 6),
      email: `user@example.com`,
      displayName: 'Social User',
      isGuest: false,
      isAuthenticated: true,
      profile: {
        avatar: `https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?w=120&h=120&fit=crop&crop=face`,
        bio: null,
        stats: {
          canvases: Math.floor(Math.random() * 200),
          lists: Math.floor(Math.random() * 30),
          following: Math.floor(Math.random() * 15),
          followers: Math.floor(Math.random() * 80),
          likes: Math.floor(Math.random() * 40),
        },
      },
    };

    // Simulate OAuth delay
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Store user data
    currentUser = socialUser;
    localStorage.setItem('curatated_user', JSON.stringify(socialUser));

    showNotification(
      `Successfully ${action === 'signin' ? 'signed in' : 'signed up'} with ${provider}!`,
      'success'
    );

    // Redirect
    setTimeout(() => {
      window.location.href = action === 'signin' ? './profile.html' : './profile.html';
    }, 1000);
  } catch (error) {
    console.error('Social auth error:', error);
    showNotification(`${provider} authentication failed. Please try again.`, 'error');
  }
}

// Check for existing session
function checkExistingSession() {
  // Check for full user session
  const storedUser = localStorage.getItem('curatated_user');
  if (storedUser) {
    currentUser = JSON.parse(storedUser);
    // Redirect if already logged in
    if (window.location.pathname.includes('auth.html')) {
      window.location.href = './profile.html';
    }
    return;
  }

  // Check for guest session
  const guestUser = sessionStorage.getItem('curatated_guest');
  if (guestUser) {
    currentUser = JSON.parse(guestUser);
    updateUIForLoggedInUser();
    return;
  }
}

// Update UI for logged in user
function updateUIForLoggedInUser() {
  // Update navigation if on other pages
  const userAvatar = document.querySelector('.user-avatar');
  if (userAvatar && currentUser.isAuthenticated) {
    if (currentUser.profile.avatar) {
      userAvatar.innerHTML = `<img src="${currentUser.profile.avatar}" alt="Avatar" style="width: 32px; height: 32px; border-radius: 50%; object-fit: cover;">`;
    } else {
      userAvatar.textContent = currentUser.displayName.charAt(0).toUpperCase();
    }
  }

  // Update + Log button
  const logButton = document.querySelector('.btn-primary');
  if (logButton && logButton.textContent.includes('Log')) {
    logButton.textContent = '+ Canvas';
    logButton.onclick = () => (window.location.href = 'canvas.html');
  }
}

// Show loading state
function showLoadingState(form) {
  const submitBtn = form.querySelector('.auth-submit');
  if (submitBtn) {
    submitBtn.disabled = true;
    submitBtn.innerHTML = '<div class="loading-spinner"></div> Loading...';
    submitBtn.style.opacity = '0.7';
  }
}

// Hide loading state
function hideLoadingState(form) {
  const submitBtn = form.querySelector('.auth-submit');
  if (submitBtn) {
    submitBtn.disabled = false;
    submitBtn.style.opacity = '1';

    // Restore original text
    if (form.id === 'signin-form-element') {
      submitBtn.textContent = 'Sign In';
    } else if (form.id === 'signup-form-element') {
      submitBtn.textContent = 'Create Account';
    } else if (form.id === 'guest-form-element') {
      submitBtn.textContent = 'Continue as Guest';
    }
  }
}

// Logout function
function logout() {
  currentUser = {
    id: null,
    username: null,
    email: null,
    displayName: null,
    isGuest: false,
    isAuthenticated: false,
    profile: {
      avatar: null,
      bio: null,
      stats: {
        canvases: 0,
        lists: 0,
        following: 0,
        followers: 0,
        likes: 0,
      },
    },
  };

  localStorage.removeItem('curatated_user');
  sessionStorage.removeItem('curatated_guest');

  showNotification('You have been logged out.', 'info');

  setTimeout(() => {
    window.location.href = './index.html';
  }, 1000);
}

// Show notification
function showNotification(message, type = 'info') {
  const notification = document.createElement('div');
  notification.className = `notification notification-${type}`;
  notification.textContent = message;
  notification.style.cssText = `
        position: fixed;
        top: 20px;
        right: 20px;
        padding: 1rem 1.5rem;
        border-radius: 8px;
        color: white;
        z-index: 2000;
        transition: all 0.3s ease;
        font-weight: 500;
        background: ${
          type === 'success'
            ? 'var(--accent-green)'
            : type === 'error'
              ? '#ff4444'
              : type === 'warning'
                ? 'var(--accent-orange)'
                : 'var(--bg-tertiary)'
        };
    `;

  // Add loading spinner styles
  const style = document.createElement('style');
  style.textContent = `
        .loading-spinner {
            display: inline-block;
            width: 14px;
            height: 14px;
            border: 2px solid rgba(255,255,255,0.3);
            border-radius: 50%;
            border-top: 2px solid white;
            animation: spin 1s linear infinite;
            margin-right: 0.5rem;
        }

        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }
    `;

  if (!document.querySelector('style[data-auth-styles]')) {
    style.setAttribute('data-auth-styles', 'true');
    document.head.appendChild(style);
  }

  document.body.appendChild(notification);

  setTimeout(() => {
    notification.style.opacity = '0';
    notification.style.transform = 'translateX(100%)';
    setTimeout(() => {
      if (document.body.contains(notification)) {
        document.body.removeChild(notification);
      }
    }, 300);
  }, 3000);
}

// Export functions for global use
window.currentUser = currentUser;
window.logout = logout;
window.showNotification = showNotification;

// Initialize on page load
if (document.readyState === 'loading') {
  document.addEventListener('DOMContentLoaded', updateUIForLoggedInUser);
} else {
  updateUIForLoggedInUser();
}

