/**
 * STRIKE ERRORS TOOLBAR INTEGRATION
 *
 * Handles the expandable Strike Errors toolbar and status footer
 * Integrates with the chrome container app's terminal system
 */

class StrikeErrorsToolbar {
  constructor() {
    this.isCollapsed = true;
    this.currentStatus = 'idle';
    this.analysisResults = null;
    this.uploadedFiles = [];

    this.initializeElements();
    this.setupEventListeners();
    this.updateFooterMetrics();

    console.log('ğŸ¯ Strike Errors toolbar initialized');
  }

  initializeElements() {
    // Main toolbar elements
    this.toolbar = document.getElementById('strikeErrorsToolbar');
    this.toggleButton = document.getElementById('strikeToggle');
    this.launchButton = document.getElementById('launch-strike-errors');
    this.uploadButton = document.getElementById('upload-files');

    // Status elements
    this.statusDot = document.getElementById('strikeStatusDot');
    this.statusText = document.getElementById('strikeStatusText');
    this.progressContainer = document.getElementById('strikeProgress');
    this.progressFill = document.getElementById('strikeProgressFill');
    this.progressText = document.getElementById('strikeProgressText');

    // Health score elements
    this.healthScoresSection = document.getElementById('healthScores');
    this.overallScore = document.getElementById('overallScore');
    this.dependencyScore = document.getElementById('dependencyScore');
    this.connectivityScore = document.getElementById('connectivityScore');
    this.complexityScore = document.getElementById('complexityScore');

    // Footer elements
    this.footerStrikeStatusIcon = document.getElementById('footer-strike-status-icon');
    this.footerStrikeStatusText = document.getElementById('footer-strike-status-text');
    this.connectionManagerToggle = document.getElementById('connection-manager-toggle');
    this.connectionManagerModal = document.getElementById('connection-manager-modal');
    this.closeConnectionModal = document.getElementById('close-connection-modal');

    // Configuration elements
    this.preserveMode = document.getElementById('preserveMode');
    this.comprehensiveMode = document.getElementById('comprehensiveMode');
    this.healthThreshold = document.getElementById('healthThreshold');

    // Set initial status
    this.updateStatus('Ready', 'idle');
  }

  setupEventListeners() {
    // Toolbar toggle
    this.toggleButton.addEventListener('click', () => this.toggleToolbar());

    // Action buttons
    this.launchButton.addEventListener('click', () => this.launchStrikeErrors());
    this.uploadButton.addEventListener('click', () => this.uploadFiles());

    // Connection manager modal
    this.connectionManagerToggle.addEventListener('click', () => this.openConnectionManager());
    this.closeConnectionModal.addEventListener('click', () => this.closeConnectionManager());

    // Close modal when clicking outside
    this.connectionManagerModal.addEventListener('click', e => {
      if (e.target === this.connectionManagerModal) {
        this.closeConnectionManager();
      }
    });

    // Configuration changes
    this.preserveMode.addEventListener('change', () => this.updateConfig());
    this.comprehensiveMode.addEventListener('change', () => this.updateConfig());
    this.healthThreshold.addEventListener('change', () => this.updateConfig());

    // Listen for Electron IPC events if available
    if (typeof window.electronAPI !== 'undefined') {
      this.setupElectronListeners();
    }

    // Keyboard shortcuts
    document.addEventListener('keydown', e => this.handleKeyboardShortcuts(e));
  }

  setupElectronListeners() {
    // Strike Errors events
    window.electronAPI.onStrikeErrorsStatusUpdate(data => {
      this.updateStatus(data.status, data.type);
    });

    window.electronAPI.onStrikeErrorsProgress(data => {
      this.updateProgress(data.progress, data.status);
    });

    window.electronAPI.onStrikeErrorsAnalysisComplete(results => {
      this.handleAnalysisComplete(results);
    });

    window.electronAPI.onStrikeErrorsAnalysisError(error => {
      this.handleAnalysisError(error);
    });

    window.electronAPI.onStrikeErrorsFilesUploaded(files => {
      this.handleFilesUploaded(files);
    });
  }

  toggleToolbar() {
    this.isCollapsed = !this.isCollapsed;

    if (this.isCollapsed) {
      this.toolbar.classList.add('collapsed');
    } else {
      this.toolbar.classList.remove('collapsed');
    }

    // Log to terminal
    this.logToTerminal(`ğŸ¯ Strike Errors toolbar ${this.isCollapsed ? 'collapsed' : 'expanded'}`);
  }

  async launchStrikeErrors() {
    if (this.currentStatus === 'running') {
      this.logToTerminal('âš ï¸ Analysis already in progress');
      return;
    }

    this.updateStatus('Launching Strike Errors interface...', 'loading');
    this.logToTerminal('ğŸš€ Launching Strike Errors universal code analysis...');

    try {
      if (typeof window.electronAPI !== 'undefined') {
        // Use Electron IPC
        const result = await window.electronAPI.strikeErrorsOpen();

        if (result.success) {
          this.updateStatus('Interface launched', 'complete');
          this.logToTerminal('âœ… Strike Errors interface opened');

          // Open full interface in 1 second
          setTimeout(() => {
            if (result.uiPath) {
              // Navigate to Strike Errors UI
              window.location.href = result.uiPath;
            }
          }, 1000);
        } else {
          throw new Error(result.error || 'Launch failed');
        }
      } else {
        // Fallback for web environment
        this.updateStatus('Opening interface...', 'loading');
        this.logToTerminal('âš ï¸ Running in web mode - opening Strike Errors interface...');

        // Navigate to Strike Errors UI
        setTimeout(() => {
          window.open('../src/strike-errors/ui/file-upload.html', '_blank');
        }, 500);

        this.updateStatus('Interface opened', 'complete');
      }
    } catch (error) {
      this.updateStatus(`Launch failed: ${error.message}`, 'error');
      this.logToTerminal(`âŒ Launch failed: ${error.message}`);
      console.error('Strike Errors launch failed:', error);
    }
  }

  async uploadFiles() {
    this.logToTerminal('ğŸ“ Opening file upload dialog...');

    try {
      if (typeof window.electronAPI !== 'undefined') {
        // Use Electron file dialog
        const result = await window.electronAPI.strikeErrorsOpenFileDialog();

        if (result.success && result.files) {
          this.handleFilesUploaded(result.files);
        } else if (result.cancelled) {
          this.logToTerminal('ğŸ“ File upload cancelled');
        } else {
          throw new Error(result.error || 'File upload failed');
        }
      } else {
        // Fallback for web environment
        const input = document.createElement('input');
        input.type = 'file';
        input.multiple = true;
        input.accept = '.js,.jsx,.ts,.tsx,.py,.html,.css,.scss,.less';

        input.onchange = e => {
          const files = Array.from(e.target.files);
          if (files.length > 0) {
            this.handleFilesUploaded(files);
          }
        };

        input.click();
      }
    } catch (error) {
      this.logToTerminal(`âŒ File upload failed: ${error.message}`);
      console.error('File upload failed:', error);
    }
  }

  handleFilesUploaded(files) {
    this.uploadedFiles = files;
    this.logToTerminal(`ğŸ“ ${files.length} files uploaded successfully`);

    // Update UI to show files are ready
    this.updateStatus(`${files.length} files ready`, 'complete');

    // Enable launch button if it was disabled
    this.launchButton.disabled = false;
    this.launchButton.textContent = 'âš¡ Start Analysis';
  }

  updateStatus(text, type = 'idle') {
    this.currentStatus = type;

    // Update toolbar status
    if (this.statusDot && this.statusText) {
      this.statusDot.className = `status-dot ${type}`;
      this.statusText.textContent = text;
    }

    // Update footer status
    if (this.footerStrikeStatusIcon && this.footerStrikeStatusText) {
      this.footerStrikeStatusIcon.className = `status-dot ${type}`;
      this.footerStrikeStatusText.textContent = `Strike: ${text}`;
    }
  }

  updateProgress(progress, status = '') {
    if (this.progressContainer && this.progressFill && this.progressText) {
      if (progress > 0) {
        this.progressContainer.style.display = 'block';
        this.progressFill.style.width = `${progress}%`;
        this.progressText.textContent = `${progress}%`;
      } else {
        this.progressContainer.style.display = 'none';
      }
    }

    if (status) {
      this.updateStatus(status, 'running');
    }

    this.logToTerminal(`ğŸ“Š Analysis progress: ${progress}%${status ? ` - ${status}` : ''}`);
  }

  handleAnalysisComplete(results) {
    this.analysisResults = results;
    this.updateStatus('Analysis complete', 'complete');
    this.updateProgress(0); // Hide progress bar

    // Show health scores
    if (this.healthScoresSection) {
      this.healthScoresSection.style.display = 'block';

      if (this.overallScore) this.overallScore.textContent = results.overallScore || '--';
      if (this.dependencyScore) this.dependencyScore.textContent = results.dependencyScore || '--';
      if (this.connectivityScore)
        this.connectivityScore.textContent = results.connectivityScore || '--';
      if (this.complexityScore) this.complexityScore.textContent = results.complexityScore || '--';
    }

    this.logToTerminal(
      `ğŸ‰ Analysis complete - Overall health score: ${results.overallScore || 0}/100`
    );

    // Update recent results
    this.updateRecentResults(results);
  }

  handleAnalysisError(error) {
    this.updateStatus(`Error: ${error.message}`, 'error');
    this.updateProgress(0);
    this.logToTerminal(`âŒ Analysis failed: ${error.message}`);
  }

  updateRecentResults(results) {
    const recentResults = document.getElementById('recentResults');
    if (!recentResults) return;

    // Clear no results message
    const noResults = recentResults.querySelector('.no-results');
    if (noResults) noResults.remove();

    // Create result item
    const resultItem = document.createElement('div');
    resultItem.className = 'result-item';
    resultItem.innerHTML = `
            <div class="result-header">
                <span class="result-score">${results.overallScore || 0}</span>
                <span class="result-date">${new Date().toLocaleTimeString()}</span>
            </div>
            <div class="result-details">
                <span>${this.uploadedFiles.length || 0} files analyzed</span>
            </div>
        `;

    recentResults.insertBefore(resultItem, recentResults.firstChild);

    // Keep only latest 3 results
    const items = recentResults.querySelectorAll('.result-item');
    if (items.length > 3) {
      items[items.length - 1].remove();
    }
  }

  updateConfig() {
    const config = {
      preserveMode: this.preserveMode?.checked || true,
      comprehensive: this.comprehensiveMode?.checked || true,
      healthThreshold: parseInt(this.healthThreshold?.value || '70'),
    };

    this.logToTerminal(
      `âš™ï¸ Configuration updated: Preserve=${config.preserveMode}, Comprehensive=${config.comprehensive}, Threshold=${config.healthThreshold}`
    );
  }

  openConnectionManager() {
    if (this.connectionManagerModal) {
      this.connectionManagerModal.classList.remove('hidden');
    }
  }

  closeConnectionManager() {
    if (this.connectionManagerModal) {
      this.connectionManagerModal.classList.add('hidden');
    }
  }

  handleKeyboardShortcuts(e) {
    // Ctrl/Cmd + Shift + S: Toggle Strike Errors toolbar
    if ((e.ctrlKey || e.metaKey) && e.shiftKey && e.key.toLowerCase() === 's') {
      e.preventDefault();
      this.toggleToolbar();
    }

    // Ctrl/Cmd + Shift + E: Launch Strike Errors
    if ((e.ctrlKey || e.metaKey) && e.shiftKey && e.key.toLowerCase() === 'e') {
      e.preventDefault();
      this.launchStrikeErrors();
    }

    // Escape: Close modals
    if (e.key === 'Escape') {
      this.closeConnectionManager();
    }
  }

  updateFooterMetrics() {
    // Update footer metrics from existing data
    const cpuUsage = document.getElementById('cpu-usage')?.textContent || '--';
    const memoryUsage = document.getElementById('memory-usage')?.textContent || '--';
    const chromeTabs = document.getElementById('chrome-tabs')?.textContent || '--';
    const connections = document.getElementById('live-connections')?.textContent || '0';

    // Update footer elements
    const footerCpu = document.getElementById('footer-cpu-usage');
    const footerMemory = document.getElementById('footer-memory-usage');
    const footerTabs = document.getElementById('footer-chrome-tabs');
    const footerConnections = document.getElementById('footer-connections');

    if (footerCpu) footerCpu.textContent = cpuUsage;
    if (footerMemory) footerMemory.textContent = memoryUsage;
    if (footerTabs) footerTabs.textContent = chromeTabs;
    if (footerConnections) footerConnections.textContent = connections;

    // Update progress bars
    const cpuBar = document.getElementById('cpu-bar');
    const footerCpuBar = document.getElementById('footer-cpu-bar');
    if (cpuBar && footerCpuBar) {
      footerCpuBar.style.width = cpuBar.style.width;
    }

    const memoryBar = document.getElementById('memory-bar');
    const footerMemoryBar = document.getElementById('footer-memory-bar');
    if (memoryBar && footerMemoryBar) {
      footerMemoryBar.style.width = memoryBar.style.width;
    }

    // Update AI status
    const aiStatusIcon = document.getElementById('ai-status-icon');
    const aiStatusText = document.getElementById('ai-status-text');
    const footerAiStatusIcon = document.getElementById('footer-ai-status-icon');
    const footerAiStatusText = document.getElementById('footer-ai-status-text');

    if (aiStatusIcon && footerAiStatusIcon) {
      footerAiStatusIcon.className = aiStatusIcon.className;
    }
    if (aiStatusText && footerAiStatusText) {
      footerAiStatusText.textContent = `AI: ${aiStatusText.textContent}`;
    }

    // Schedule next update
    setTimeout(() => this.updateFooterMetrics(), 2000);
  }

  logToTerminal(message) {
    // Log to terminal if available
    const timestamp = new Date().toLocaleTimeString();
    const logMessage = `[${timestamp}] [Strike Errors] ${message}`;

    // Try to send to terminal via various methods
    if (typeof window.electronAPI !== 'undefined') {
      window.electronAPI.sendTerminalInput(logMessage);
    }

    // Also log to console for debugging
    console.log(logMessage);

    // Try to add to terminal display if available
    const terminalOutput = document.querySelector('.terminal-output');
    if (terminalOutput) {
      terminalOutput.textContent += `\\n${logMessage}`;
      terminalOutput.scrollTop = terminalOutput.scrollHeight;
    }
  }

  // Public API methods
  getStatus() {
    return {
      status: this.currentStatus,
      isCollapsed: this.isCollapsed,
      uploadedFiles: this.uploadedFiles.length,
      analysisResults: this.analysisResults,
    };
  }

  setCollapsed(collapsed) {
    if (collapsed !== this.isCollapsed) {
      this.toggleToolbar();
    }
  }

  showHealthScores(scores) {
    this.handleAnalysisComplete(scores);
  }

  clearResults() {
    this.analysisResults = null;
    this.uploadedFiles = [];

    if (this.healthScoresSection) {
      this.healthScoresSection.style.display = 'none';
    }

    const recentResults = document.getElementById('recentResults');
    if (recentResults) {
      recentResults.innerHTML = `
                <div class="no-results">
                    <span class="no-results-icon">ğŸ“Š</span>
                    <p>No analysis completed yet</p>
                    <small>Upload files and run analysis to see results</small>
                </div>
            `;
    }

    this.updateStatus('Ready', 'idle');
    this.logToTerminal('ğŸ§¹ Strike Errors cleared - ready for new analysis');
  }
}

