/**
 * STRIKE ERRORS ENHANCED - COMPLETE REPLACEMENT
 *
 * A comprehensive, working error analysis system for Chrome Container App
 * Connects directly to uploaded directories and provides real analysis
 */

class StrikeErrorsEnhanced {
  constructor() {
    this.currentDirectory = null;
    this.analysisResults = {
      errors: [],
      warnings: [],
      suggestions: [],
      fileStats: {},
      overallHealth: 0,
    };

    this.errorPatterns = this.initializeErrorPatterns();
    this.isAnalyzing = false;

    this.initializeUI();
    this.setupEventListeners();

    console.log('ğŸ¯ Strike Errors Enhanced - Real Analysis System Initialized');
  }

  initializeErrorPatterns() {
    return {
      // JavaScript Error Patterns
      javascript: {
        syntax: [
          {
            pattern: /console\.log\(/g,
            severity: 'warning',
            message: 'Debug console.log statement found',
          },
          { pattern: /debugger;/g, severity: 'error', message: 'Debugger statement left in code' },
          { pattern: /alert\(/g, severity: 'warning', message: 'Alert dialog used' },
          {
            pattern: /var\s+\w+/g,
            severity: 'suggestion',
            message: 'Consider using let/const instead of var',
          },
          { pattern: /==\s*[^=]/g, severity: 'warning', message: 'Use === for strict equality' },
          {
            pattern: /!\s*=\s*[^=]/g,
            severity: 'warning',
            message: 'Use !== for strict inequality',
          },
        ],
        structure: [
          {
            pattern: /function\s+\w+\s*\([^)]*\)\s*\{[^}]{500,}\}/g,
            severity: 'warning',
            message: 'Large function detected - consider breaking down',
          },
          {
            pattern: /^\s*\/\*[\s\S]*?\*\/\s*$/gm,
            severity: 'suggestion',
            message: 'Dead comment block found',
          },
        ],
      },

      // HTML Error Patterns
      html: {
        accessibility: [
          {
            pattern: /<img(?![^>]*alt\s*=)/g,
            severity: 'warning',
            message: 'Image missing alt attribute',
          },
          {
            pattern: /<input(?![^>]*id\s*=)(?![^>]*aria-label)/g,
            severity: 'warning',
            message: 'Input missing id or aria-label',
          },
          {
            pattern: /<button(?![^>]*aria-label)(?![^>]*title)(?!>[^<]+<\/button>)/g,
            severity: 'warning',
            message: 'Button missing accessible text',
          },
        ],
        structure: [
          {
            pattern: /<script[^>]*src[^>]*><\/script>/g,
            severity: 'suggestion',
            message: 'External script reference found',
          },
          {
            pattern: /style\s*=\s*["'][^"']*["']/g,
            severity: 'suggestion',
            message: 'Inline styles found - consider CSS classes',
          },
        ],
      },

      // CSS Error Patterns
      css: {
        performance: [
          {
            pattern: /\*\s*\{/g,
            severity: 'warning',
            message: 'Universal selector usage - performance impact',
          },
          {
            pattern: /!important/g,
            severity: 'warning',
            message: '!important usage - specificity issue',
          },
          {
            pattern: /@import/g,
            severity: 'suggestion',
            message: '@import statement - consider bundling',
          },
        ],
        structure: [
          { pattern: /\{\s*\}/g, severity: 'warning', message: 'Empty CSS rule found' },
          { pattern: /;\s*;/g, severity: 'error', message: 'Double semicolon in CSS' },
        ],
      },

      // File Structure Patterns
      structure: {
        duplicates: [
          { pattern: /\.backup/, severity: 'error', message: 'Backup file found in source' },
          { pattern: /\.tmp$/, severity: 'warning', message: 'Temporary file found' },
          { pattern: /\.DS_Store/, severity: 'warning', message: 'System file found in source' },
        ],
        naming: [
          { pattern: /[A-Z]/, severity: 'suggestion', message: 'Consider lowercase file naming' },
          {
            pattern: /\s/,
            severity: 'warning',
            message: 'Space in filename - use dashes or underscores',
          },
        ],
      },
    };
  }

  initializeUI() {
    // Don't create a separate container - integrate with existing left toolbar
    // The UI elements are already in the HTML, just need to set up functionality

    // Show the Map Dependencies and Fix and Build buttons that were added to index.html
    const mapBtn = document.getElementById('map-dependencies');
    const fixBtn = document.getElementById('fix-and-build');

    if (mapBtn) mapBtn.style.display = 'block';
    if (fixBtn) fixBtn.style.display = 'block';

    // Add styles for enhanced functionality
    this.injectStyles();
  }

  createMainContainer() {
    const container = document.createElement('div');
    container.id = 'strike-errors-container';
    container.className = 'strike-errors-enhanced';

    // Insert after the left toolbar or at the beginning of main content
    const mainContent = document.querySelector('.main-content') || document.body;
    mainContent.appendChild(container);

    return container;
  }

  setupEventListeners() {
    // Work with existing left toolbar buttons
    const uploadBtn = document.getElementById('upload-files');
    const launchBtn = document.getElementById('launch-strike-errors');
    const mapBtn = document.getElementById('map-dependencies');
    const fixBtn = document.getElementById('fix-and-build');

    if (uploadBtn) {
      uploadBtn.addEventListener('click', () => this.selectDirectory());
    }

    if (launchBtn) {
      launchBtn.addEventListener('click', () => this.runAnalysis());
    }

    if (mapBtn) {
      mapBtn.addEventListener('click', () => this.mapDependencies());
    }

    if (fixBtn) {
      fixBtn.addEventListener('click', () => this.fixAndBuild());
    }

    // Note: clear-results button doesn't exist in main UI, so skip it

    // Tab switching
    document.addEventListener('click', e => {
      if (e.target.matches('.tab-btn')) {
        this.switchTab(e.target.dataset.tab);
      }
    });
  }

  async selectDirectory() {
    try {
      if (typeof window.electronAPI !== 'undefined') {
        // Electron environment
        const result = await window.electronAPI.openDirectory?.();

        if (result?.success && result?.directoryPath) {
          await this.handleDirectorySelected(result.directoryPath, result.files);
        } else if (!result?.cancelled) {
          throw new Error(result?.error || 'Directory selection failed');
        }
      } else {
        // Web environment fallback
        const input = document.createElement('input');
        input.type = 'file';
        input.webkitdirectory = true;
        input.multiple = true;

        input.onchange = e => {
          const files = Array.from(e.target.files);
          if (files.length > 0) {
            this.handleWebDirectorySelected(files);
          }
        };

        input.click();
      }
    } catch (error) {
      this.showError('Directory Selection Error', error.message);
    }
  }

  async handleDirectorySelected(directoryPath, files) {
    this.currentDirectory = directoryPath;

    // Update UI
    document.getElementById('current-path').textContent = directoryPath;
    document.getElementById('directory-info').style.display = 'block';
    document.getElementById('run-analysis').disabled = false;

    // Calculate file stats
    const stats = this.calculateFileStats(files);
    document.getElementById('file-stats').innerHTML = `
      <div class="stat-item">ğŸ“„ ${stats.totalFiles} files</div>
      <div class="stat-item">ğŸ“ ${stats.jsFiles} JavaScript</div>
      <div class="stat-item">ğŸŒ ${stats.htmlFiles} HTML</div>
      <div class="stat-item">ğŸ¨ ${stats.cssFiles} CSS</div>
      <div class="stat-item">ğŸ“¦ ${stats.otherFiles} Other</div>
    `;

    this.updateStatus('Directory loaded - Ready for analysis', 'ready');
  }

  handleWebDirectorySelected(files) {
    const directoryPath = files[0].webkitRelativePath.split('/')[0];
    this.handleDirectorySelected(directoryPath, files);
  }

  calculateFileStats(files) {
    const stats = {
      totalFiles: files.length,
      jsFiles: 0,
      htmlFiles: 0,
      cssFiles: 0,
      otherFiles: 0,
    };

    files.forEach(file => {
      const ext = file.name?.toLowerCase() || file.webkitRelativePath?.toLowerCase() || '';
      if (
        ext.includes('.js') ||
        ext.includes('.jsx') ||
        ext.includes('.ts') ||
        ext.includes('.tsx')
      ) {
        stats.jsFiles++;
      } else if (ext.includes('.html') || ext.includes('.htm')) {
        stats.htmlFiles++;
      } else if (ext.includes('.css') || ext.includes('.scss') || ext.includes('.sass')) {
        stats.cssFiles++;
      } else {
        stats.otherFiles++;
      }
    });

    return stats;
  }

  async runAnalysis() {
    if (this.isAnalyzing) return;

    this.isAnalyzing = true;
    this.updateStatus('Analyzing directory...', 'analyzing');

    // Show progress
    document.getElementById('analysis-progress').style.display = 'block';

    try {
      // Get files to analyze
      let files = [];
      if (typeof window.electronAPI !== 'undefined') {
        const result = await window.electronAPI.getDirectoryFiles?.(this.currentDirectory);
        files = result?.files || [];
      } else {
        // Web mode - files were stored during selection
        files = this.webFiles || [];
      }

      // Run analysis
      await this.analyzeFiles(files);

      // Display results
      this.displayResults();

      // Show action buttons if issues found
      const totalIssues =
        this.analysisResults.errors.length +
        this.analysisResults.warnings.length +
        this.analysisResults.suggestions.length;
      if (totalIssues > 0) {
        const fixButton = document.getElementById('fix-and-build');
        fixButton.style.display = 'inline-block';
        fixButton.disabled = false;
      }

      // Always show map dependencies button after analysis
      const mapButton = document.getElementById('map-dependencies');
      mapButton.style.display = 'inline-block';
      mapButton.disabled = false;

      this.updateStatus(`Analysis complete - ${totalIssues} issues found`, 'complete');
    } catch (error) {
      this.showError('Analysis Error', error.message);
      this.updateStatus('Analysis failed', 'error');
    } finally {
      this.isAnalyzing = false;
      document.getElementById('analysis-progress').style.display = 'none';
    }
  }

  async analyzeFiles(files) {
    // Reset results
    this.analysisResults = {
      errors: [],
      warnings: [],
      suggestions: [],
      fileStats: {},
      overallHealth: 0,
    };

    const totalFiles = files.length;
    let processedFiles = 0;

    for (const file of files) {
      // Update progress
      const progress = Math.round((processedFiles / totalFiles) * 100);
      document.getElementById('progress-fill').style.width = `${progress}%`;
      document.getElementById('progress-text').textContent =
        `Analyzing ${file.name || file.webkitRelativePath}... (${processedFiles}/${totalFiles})`;

      // Small delay for UI updates
      await new Promise(resolve => setTimeout(resolve, 10));

      // Analyze file
      await this.analyzeFile(file);

      processedFiles++;
    }

    // Calculate overall health score
    this.calculateHealthScore();
  }

  async analyzeFile(file) {
    try {
      // Get file content
      let content = '';
      let fileName = '';

      if (file.webkitRelativePath) {
        // Web File object
        content = await this.readFileContent(file);
        fileName = file.webkitRelativePath;
      } else {
        // Electron file path
        fileName = file.path || file.name;
        if (typeof window.electronAPI !== 'undefined') {
          const result = await window.electronAPI.readFile?.(fileName);
          content = result?.content || '';
        }
      }

      // Determine file type
      const fileType = this.getFileType(fileName);

      // Run pattern matching
      this.runPatternAnalysis(fileName, content, fileType);

      // Store file stats
      this.analysisResults.fileStats[fileName] = {
        type: fileType,
        size: content.length,
        lines: content.split('\n').length,
      };
    } catch (error) {
      this.analysisResults.errors.push({
        file: fileName,
        message: `Failed to analyze file: ${error.message}`,
        severity: 'error',
        line: 0,
      });
    }
  }

  readFileContent(file) {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = e => resolve(e.target.result);
      reader.onerror = () => reject(new Error('Failed to read file'));
      reader.readAsText(file);
    });
  }

  getFileType(fileName) {
    const ext = fileName.toLowerCase();
    if (
      ext.includes('.js') ||
      ext.includes('.jsx') ||
      ext.includes('.ts') ||
      ext.includes('.tsx')
    ) {
      return 'javascript';
    } else if (ext.includes('.html') || ext.includes('.htm')) {
      return 'html';
    } else if (ext.includes('.css') || ext.includes('.scss') || ext.includes('.sass')) {
      return 'css';
    }
    return 'other';
  }

  runPatternAnalysis(fileName, content, fileType) {
    // Check file structure patterns first
    this.checkFileStructure(fileName);

    // Then check content patterns based on file type
    if (this.errorPatterns[fileType]) {
      Object.entries(this.errorPatterns[fileType]).forEach(([category, patterns]) => {
        patterns.forEach(pattern => {
          const matches = content.match(pattern.pattern);
          if (matches) {
            matches.forEach(match => {
              const issue = {
                file: fileName,
                message: pattern.message,
                severity: pattern.severity,
                line: this.findLineNumber(content, match),
                match: match,
              };

              this.analysisResults[
                pattern.severity === 'error'
                  ? 'errors'
                  : pattern.severity === 'warning'
                    ? 'warnings'
                    : 'suggestions'
              ].push(issue);
            });
          }
        });
      });
    }
  }

  checkFileStructure(fileName) {
    Object.entries(this.errorPatterns.structure).forEach(([category, patterns]) => {
      patterns.forEach(pattern => {
        if (pattern.pattern.test && pattern.pattern.test(fileName)) {
          this.analysisResults[
            pattern.severity === 'error'
              ? 'errors'
              : pattern.severity === 'warning'
                ? 'warnings'
                : 'suggestions'
          ].push({
            file: fileName,
            message: pattern.message,
            severity: pattern.severity,
            line: 0,
          });
        }
      });
    });
  }

  findLineNumber(content, match) {
    const beforeMatch = content.substring(0, content.indexOf(match));
    return beforeMatch.split('\n').length;
  }

  calculateHealthScore() {
    const totalIssues =
      this.analysisResults.errors.length +
      this.analysisResults.warnings.length +
      this.analysisResults.suggestions.length;

    const totalFiles = Object.keys(this.analysisResults.fileStats).length;

    if (totalFiles === 0) {
      this.analysisResults.overallHealth = 0;
      return;
    }

    // Health score: 100 - (weighted issues per file * 10)
    const errorWeight = 3;
    const warningWeight = 2;
    const suggestionWeight = 1;

    const weightedIssues =
      this.analysisResults.errors.length * errorWeight +
      this.analysisResults.warnings.length * warningWeight +
      this.analysisResults.suggestions.length * suggestionWeight;

    const issuesPerFile = weightedIssues / totalFiles;
    this.analysisResults.overallHealth = Math.max(0, Math.min(100, 100 - issuesPerFile * 10));
  }

  displayResults() {
    const summary = document.getElementById('results-summary');
    const tabs = document.getElementById('results-tabs');

    // Update summary
    document.getElementById('health-score').textContent = Math.round(
