  switchTab(tab) {
    // Update tab buttons
    document.querySelectorAll('.tab-btn').forEach(btn => {
      btn.classList.toggle('active', btn.dataset.tab === tab);
    });

    // Display content
    const content = document.getElementById('results-content');
    let html = '';

    switch (tab) {
      case 'errors':
        html = this.renderIssueList(this.analysisResults.errors, 'error');
        break;
      case 'warnings':
        html = this.renderIssueList(this.analysisResults.warnings, 'warning');
        break;
      case 'suggestions':
        html = this.renderIssueList(this.analysisResults.suggestions, 'suggestion');
        break;
      case 'files':
        html = this.renderFileStats();
        break;
