  async fixAndBuild() {
    if (this.isAnalyzing) return;

    this.isAnalyzing = true;
    this.updateStatus('Running real linting tools and build fixes...', 'analyzing');

    // Show progress
    document.getElementById('analysis-progress').style.display = 'block';

    try {
      if (!this.currentDirectory) {
        throw new Error('No directory selected for fixing');
      }

      // Step 1: Run dependency analysis with real tools
      this.updateProgress(20, 'Running real dependency analysis...');
      const analysisResult = await window.electronAPI.invoke(
        'run-dependency-analysis',
        this.currentDirectory
      );

      if (!analysisResult.success) {
        throw new Error(analysisResult.error);
      }

      // Step 2: Generate real fixes from analysis results
      this.updateProgress(40, 'Generating real automated fixes...');
      const realFixes = await this.generateRealAutomatedFixes(analysisResult.results);

      // Step 3: Apply real fixes using bash commands
      this.updateProgress(60, 'Applying real fixes with bash tools...');
      const applyResults = await window.electronAPI.invoke(
        'apply-real-fixes',
        this.currentDirectory,
        realFixes
      );

      if (!applyResults.success) {
        throw new Error(applyResults.error);
      }

      // Step 4: Run final build validation
      this.updateProgress(80, 'Running final build validation...');
      const finalValidation = await window.electronAPI.invoke(
        'run-dependency-analysis',
        this.currentDirectory
      );

      // Step 5: Complete
      this.updateProgress(100, 'Real fix and build complete!');

      // Display real results
      this.displayRealFixAndBuildResults({
        initialAnalysis: analysisResult.results,
        fixes: realFixes,
        applyResults,
        finalAnalysis: finalValidation.success ? finalValidation.results : null,
        directoryPath: this.currentDirectory,
      });

      this.updateStatus(
        `Real fix and build complete - ${applyResults.applied} fixes applied, ${applyResults.failed} failed`,
        applyResults.failed === 0 ? 'complete' : 'error'
      );
    } catch (error) {
      this.showError('Real Fix and Build Error', error.message);
      this.updateStatus('Real fix and build failed', 'error');
    } finally {
      this.isAnalyzing = false;
      document.getElementById('analysis-progress').style.display = 'none';
    }
  }

  async generateRealAutomatedFixes(analysisResults) {
    const fixes = [];

    // Generate ESLint fixes
    if (analysisResults.eslint && Array.isArray(analysisResults.eslint)) {
      analysisResults.eslint.forEach(fileResult => {
        if (fileResult.messages && fileResult.messages.length > 0) {
          // Check if any messages are fixable
          const fixableMessages = fileResult.messages.filter(msg => msg.fix);
          if (fixableMessages.length > 0) {
            fixes.push({
              type: 'eslint-fix',
              file: fileResult.filePath,
              description: `Fix ${fixableMessages.length} ESLint issues`,
              priority: 'high',
            });
          }
        }
      });
    }

    // Generate Prettier fixes (always add for formatting)
    fixes.push({
      type: 'prettier-fix',
      file: '**/*.{js,jsx,ts,tsx,css,json}',
      description: 'Format code with Prettier',
      priority: 'medium',
    });

    // Generate dependency fixes
    if (analysisResults.dependencies?.problems) {
      fixes.push({
        type: 'install-dependency',
        package: '--force', // Force reinstall
        description: 'Fix dependency issues',
        priority: 'medium',
      });
    }

    return fixes;
  }

