// Dependency Chain Analysis System
class DependencyChainAnalyzer {
  constructor() {
    this.setupEventListeners();
  }

  setupEventListeners() {
    document.addEventListener('DOMContentLoaded', () => {
      const chainAnalyzeBtn = document.getElementById('analyze-dependency-chains');
      const fixBrokenChainsBtn = document.getElementById('fix-broken-chains');
      const exportChainReportBtn = document.getElementById('export-chain-report');

      if (chainAnalyzeBtn) {
        chainAnalyzeBtn.addEventListener('click', () => this.analyzeAllDependencyChains());
      }

      if (fixBrokenChainsBtn) {
        fixBrokenChainsBtn.addEventListener('click', () => this.fixBrokenChains());
      }

      if (exportChainReportBtn) {
        exportChainReportBtn.addEventListener('click', () => this.exportChainReport());
      }
    });
  }

  async analyzeAllDependencyChains() {
    // Starting comprehensive dependency chain analysis - removed debug console.log

    // First, send command to AI terminal to run bash chain analysis
    if (window.electronAPI && window.electronAPI.writeToTerminal) {
      const bashCommand = `
echo "ğŸ”— DEPENDENCY CHAIN ANALYSIS - COMPREHENSIVE SCAN"
echo "================================================"

# Function to validate file chains
validate_file_chain() {
  local file="$1"
  echo "ğŸ” Analyzing: $file"

  if [ ! -f "$file" ]; then
    echo "ğŸ”´ BROKEN: File not found - $file"
    return 1
  fi

  if [[ "$file" == *.js ]]; then
    # Check JavaScript syntax
    if command -v node >/dev/null 2>&1; then
      if node -c "$file" 2>/dev/null; then
        echo "ğŸŸ¢ SYNTAX: Valid - $file"
      else
        echo "ğŸ”´ BROKEN: Syntax error - $file"
        return 1
      fi
    fi

    # Check for require/import dependencies
    local deps=$(grep -n "require\\|import" "$file" 2>/dev/null || true)
    if [ -n "$deps" ]; then
      echo "ğŸ“‹ Dependencies found in $file:"
      echo "$deps" | head -5
    fi
  fi

  echo "ğŸŸ¢ VALIDATED: $file"
  return 0
}

# Analyze critical files
echo "ğŸ¯ Analyzing critical Chrome Container files..."
validate_file_chain "renderer/app.js"
validate_file_chain "renderer/index.html" 
validate_file_chain "preload.js"
validate_file_chain "main.js"
validate_file_chain "src/ipc/handlers.js"
validate_file_chain "src/terminal/pty-manager.js"
validate_file_chain "src/chrome/manager.js"

# Check node_modules dependencies
echo "ğŸ“¦ Checking critical npm packages..."
if [ -d "node_modules/@xterm" ]; then
  echo "ğŸŸ¢ FOUND: XTerm packages"
  ls -la node_modules/@xterm/
else
  echo "ğŸ”´ BROKEN: XTerm packages missing"
fi

if [ -d "node_modules/electron" ]; then
  echo "ğŸŸ¢ FOUND: Electron"
else
  echo "ğŸ”´ BROKEN: Electron missing"
fi

echo "ğŸ”— Chain analysis complete"
`;

      window.electronAPI.writeToTerminal(bashCommand);
    }

    const criticalFunctions = [
      {
        name: 'Terminal Creation',
        dependencies: [
          {
            name: 'XTerm.js',
            path: '../node_modules/@xterm/xterm/lib/xterm.js',
            expectedType: 'object',
          },
          {
            name: 'FitAddon',
            path: '../node_modules/@xterm/addon-fit/lib/addon-fit.js',
            expectedType: 'object',
          },
          { name: 'Terminal Container', path: '#terminal-container', expectedType: 'element' },
          {
            name: 'MultiTerminalManager',
            path: 'window.multiTerminalManager',
            expectedType: 'object',
          },
        ],
      },
      {
        name: 'Strike Errors Integration',
        dependencies: [
          {
            name: 'Strike Errors Script',
            path: 'window.strikeErrorsEnhanced',
            expectedType: 'object',
          },
          { name: 'IPC Communication', path: 'window.electronAPI', expectedType: 'object' },
          {
            name: 'File System Access',
            path: 'window.electronAPI.importMdFile',
            expectedType: 'function',
          },
          {
            name: 'AI Directory Upload',
            path: 'window.electronAPI.strikeErrorsAIOpenDirectory',
            expectedType: 'function',
          },
        ],
      },
      {
        name: 'Chrome Integration',
        dependencies: [
          { name: 'Chrome WebView', path: '#chrome-viewport', expectedType: 'element' },
          { name: 'Chrome Controls', path: '#initialize-chrome', expectedType: 'element' },
          {
            name: 'IPC Chrome Init',
            path: 'window.electronAPI.initializeChrome',
            expectedType: 'function',
          },
          {
            name: 'Chrome Status',
            path: 'window.electronAPI.getChromeStatus',
            expectedType: 'function',
          },
        ],
      },
      {
        name: 'File System Operations',
        dependencies: [
          { name: 'Preload API', path: 'window.electronAPI', expectedType: 'object' },
          {
            name: 'File Dialog',
            path: 'window.electronAPI.importMdFile',
            expectedType: 'function',
          },
          {
            name: 'Directory Access',
            path: 'window.electronAPI.strikeErrorsAIOpenDirectory',
            expectedType: 'function',
          },
          {
            name: 'Screenshot API',
            path: 'window.electronAPI.takeScreenshot',
            expectedType: 'function',
          },
        ],
      },
    ];

    const chainResults = {};
    let totalComplete = 0,
      totalPartial = 0,
      totalBroken = 0,
      totalEnded = 0;

    for (const func of criticalFunctions) {
      const chainStatus = this.validateDependencyChain(func.name, func.dependencies);
      chainResults[func.name] = chainStatus;

      // Count chain statuses
      switch (chainStatus.status) {
        case 'ğŸŸ¢ COMPLETE':
          totalComplete++;
          break;
        case 'ğŸŸ¡ PARTIAL':
          totalPartial++;
          break;
        case 'ğŸ”´ BROKEN':
          totalBroken++;
          break;
        default:
          totalEnded++;
          break;
      }
    }

    // Update UI with results
    this.updateChainUI(totalComplete, totalPartial, totalBroken, totalEnded, chainResults);

    // Send analysis summary to AI terminal
    if (window.electronAPI && window.electronAPI.writeToTerminal) {
      const summary = `echo "ğŸ“Š CHAIN ANALYSIS COMPLETE: âœ…${totalComplete} ğŸŸ¡${totalPartial} ğŸ”´${totalBroken} âš«${totalEnded}"`;
      window.electronAPI.writeToTerminal(summary + '\r\n');

      if (totalBroken > 0) {
        const brokenChains = Object.entries(chainResults)
          .filter(([_, status]) => status.status === 'ğŸ”´ BROKEN')
          .map(([name, _]) => name);

        const alertCmd = `echo "ğŸš¨ CRITICAL: ${brokenChains.join(', ')} - REQUIRE IMMEDIATE FIX"`;
        window.electronAPI.writeToTerminal(alertCmd + '\r\n');
      }
    }

    // Dependency Chain Analysis Complete - removed debug console.log
    return chainResults;
  }

  validateDependencyChain(functionName, dependencies) {
    const chainStatus = {
      function: functionName,
      dependencies: [],
      status: 'UNKNOWN',
      brokenLinks: [],
      chainDepth: dependencies.length,
    };

    dependencies.forEach((dep, index) => {
      const validation = {
        name: dep.name,
        path: dep.path,
        exists: false,
        accessible: false,
        functional: false,
        chainPosition: index,
      };

      // Different validation based on dependency type
      if (dep.path.startsWith('#')) {
        // DOM element check
        const element = document.querySelector(dep.path);
        validation.exists = !!element;
        validation.accessible = validation.exists;
        validation.functional = validation.accessible;
      } else if (dep.path.startsWith('window.')) {
        // Global object check
        const objPath = dep.path.replace('window.', '').split('.');
        let current = window;
        validation.exists = true;

        for (const prop of objPath) {
          if (current && typeof current === 'object' && prop in current) {
            current = current[prop];
          } else {
            validation.exists = false;
            break;
          }
        }
        validation.accessible = validation.exists;
        validation.functional = validation.accessible && typeof current === dep.expectedType;
      } else {
        // File/script check - simplified validation
        validation.exists = true;
        validation.accessible = true;
        validation.functional = true;
      }

      chainStatus.dependencies.push(validation);

      // Mark broken links
      if (!validation.functional) {
        chainStatus.brokenLinks.push({
          position: index,
          dependency: dep.name,
          issue: validation.exists
            ? validation.accessible
              ? 'NOT_FUNCTIONAL'
              : 'NOT_ACCESSIBLE'
            : 'NOT_EXISTS',
        });
      }
    });

    // Determine overall chain status
    if (chainStatus.brokenLinks.length === 0) {
      chainStatus.status = 'ğŸŸ¢ COMPLETE';
    } else if (chainStatus.brokenLinks.length < dependencies.length) {
      chainStatus.status = 'ğŸŸ¡ PARTIAL';
    } else {
      chainStatus.status = 'ğŸ”´ BROKEN';
    }

    return chainStatus;
  }

  updateChainUI(complete, partial, broken, ended, chainResults) {
    // Update counters
    const elements = {
      complete: document.getElementById('complete-count'),
      partial: document.getElementById('partial-count'),
      broken: document.getElementById('broken-count'),
      ended: document.getElementById('ended-count'),
    };

    if (elements.complete) elements.complete.textContent = complete;
    if (elements.partial) elements.partial.textContent = partial;
    if (elements.broken) elements.broken.textContent = broken;
    if (elements.ended) elements.ended.textContent = ended;

    // Display broken chains with red bars
    const brokenChainsList = document.getElementById('broken-chains-list');
    if (brokenChainsList) {
      brokenChainsList.innerHTML = '';

      Object.entries(chainResults).forEach(([functionName, status]) => {
        if (status.status !== 'ğŸŸ¢ COMPLETE') {
          const chainElement = document.createElement('div');
          chainElement.className = 'broken-chain-item';
          chainElement.style.cssText = `
            margin: 8px 0;
            padding: 12px;
            border-radius: 6px;
            ${status.status === 'ğŸ”´ BROKEN' ? 'border-left: 4px solid #e74c3c; background: #2c1810;' : 'border-left: 4px solid #f39c12; background: #2c1f10;'}
          `;

          chainElement.innerHTML = `
            <div class="chain-header" style="display: flex; justify-content: space-between; align-items: center; margin-bottom: 8px;">
              <span class="chain-status" style="font-weight: bold; ${status.status === 'ğŸ”´ BROKEN' ? 'color: #e74c3c;' : 'color: #f39c12;'}">${status.status}</span>
              <span class="chain-name" style="color: #ecf0f1; font-weight: 500;">${functionName}</span>
              <span class="chain-depth" style="color: #95a5a6; font-size: 0.9em;">${status.chainDepth} links</span>
            </div>
            <div class="chain-issues">
              ${status.brokenLinks
                .map(
                  link => `
                <div class="broken-link" style="color: #e74c3c; font-size: 0.9em; margin: 4px 0;">
                  ğŸ”´ Position ${link.position}: ${link.dependency} - ${link.issue}
                </div>
              `
                )
                .join('')}
            </div>
          `;
          brokenChainsList.appendChild(chainElement);
        }
      });
    }

    // Show results panel
    const resultsPanel = document.getElementById('chain-analysis-results');
    if (resultsPanel) {
      resultsPanel.style.display = 'block';
    }
  }

  async fixBrokenChains() {
    // Attempting to fix broken dependency chains - removed debug console.log

    if (window.electronAPI && window.electronAPI.writeToTerminal) {
      const fixCommands = `
echo "ğŸ”§ FIXING BROKEN DEPENDENCY CHAINS"
echo "================================="

# Fix XTerm packages if missing
if [ ! -d "node_modules/@xterm" ]; then
  echo "ğŸ”§ Installing XTerm packages..."
  npm install @xterm/xterm @xterm/addon-fit
fi

# Verify and fix file paths
echo "ğŸ” Checking file paths..."
if [ -f "renderer/index.html" ]; then
  echo "ğŸŸ¢ HTML file exists"
else
  echo "ğŸ”´ HTML file missing"
fi

if [ -f "renderer/app.js" ]; then
  echo "ğŸŸ¢ App.js file exists"  
else
  echo "ğŸ”´ App.js file missing"
fi

echo "ğŸ”§ Chain fix attempt complete"
`;

      window.electronAPI.writeToTerminal(fixCommands);
    }
  }

  async exportChainReport() {
    // Exporting dependency chain report - removed debug console.log

    // Generate report content
    const reportData = {
      timestamp: new Date().toISOString(),
      analysis: 'Dependency Chain Analysis',
      app: 'Chrome Container',
      status: 'Chain health assessment complete',
    };

    // Save to language directory
    if (window.electronAPI && window.electronAPI.writeToTerminal) {
      const exportCommand = `echo '${JSON.stringify(reportData, null, 2)}' > lang/dependency-chain-report-${Date.now()}.json && echo "ğŸ“Š Report exported to lang/ directory"`;
      window.electronAPI.writeToTerminal(exportCommand + '\r\n');
    }
  }
}

// Initialize when DOM is ready
document.addEventListener('DOMContentLoaded', () => {
  window.strikeErrorsEnhanced = new StrikeErrorsEnhanced();
  window.dependencyChainAnalyzer = new DependencyChainAnalyzer();
});

// Export for external use
if (typeof module !== 'undefined' && module.exports) {
  module.exports = StrikeErrorsEnhanced;
}

