// preload.js - Fixed version without breaking Xterm imports

const { contextBridge, ipcRenderer } = require('electron');

const i18n = require('./src/i18n');
console.log(i18n.t('preload.starting'));

// Expose APIs to renderer process
contextBridge.exposeInMainWorld('electronAPI', {
  // Xterm will be loaded via CDN in the renderer

  // Status and Connection
  getChromeStatus: () => ipcRenderer.invoke('get-chrome-status'),
  getConnectionStatus: () => ipcRenderer.invoke('get-connection-status'),
  getSystemMetrics: () => ipcRenderer.invoke('get-system-metrics'),
  getAIStatus: () => ipcRenderer.invoke('get-ai-status'),
  getStrikeStatus: () => ipcRenderer.invoke('get-strike-status'),

  // Chrome Actions
  initializeChrome: () => ipcRenderer.invoke('initialize-chrome'),
  restartChrome: () => ipcRenderer.invoke('restart-chrome'),
  reconnectDevTools: () => {
    console.log('ğŸŒ‰ Preload bridge: reconnectDevTools called, invoking reconnect-devtools');
    return ipcRenderer.invoke('reconnect-devtools');
  },
  navigateCanvas: () => ipcRenderer.invoke('navigate-canvas'),

  // File & Session Management
  importMdFile: () => ipcRenderer.invoke('import-md-file'),
  clearSessions: () => ipcRenderer.invoke('clear-sessions'),
  exportLogs: () => ipcRenderer.invoke('export-logs'),

  // **NEW**: WebSocket Management
  forceKillWebSockets: () => ipcRenderer.invoke('force-kill-websockets'),
  restartWebSocketServer: () => ipcRenderer.invoke('restart-websocket-server'),

  // Enhanced Terminal Streaming with debugging
  onTerminalData: callback => {
    console.log('ğŸ”— PRELOAD: Setting up terminal data listener');
    return ipcRenderer.on('terminal-data', (event, data) => {
      console.log('ğŸ”— PRELOAD: Received terminal data, length:', data?.length);
      callback(data);
    });
  },
  writeToTerminal: (data, terminalId) => {
    console.log(i18n.t('preload.terminal.writing', { data, terminalId }));
    if (terminalId) {
      return ipcRenderer.send('write-to-terminal', data, terminalId);
    } else {
      return ipcRenderer.send('terminal-write', data);
    }
  },
  resizeTerminal: (cols, rows, terminalId) => {
    console.log(i18n.t('preload.terminal.resizing', { cols, rows, terminalId }));
    return ipcRenderer.send(
      terminalId ? 'resize-terminal' : 'terminal-resize',
      terminalId ? cols : { cols, rows },
      terminalId ? rows : undefined,
      terminalId
    );
  },

  // Multi-terminal support
  createNewTerminal: options => ipcRenderer.invoke('create-new-terminal', options),
  closeTerminal: terminalId => ipcRenderer.invoke('close-terminal', terminalId),
  switchTerminal: terminalId => ipcRenderer.invoke('switch-terminal', terminalId),
  getTerminalList: () => ipcRenderer.invoke('get-terminal-list'),
  writeToSpecificTerminal: (data, terminalId) => {
    console.log(i18n.t('preload.terminal.writingSpecific', { data, terminalId }));
    return ipcRenderer.send('write-to-specific-terminal', { data, terminalId });
  },
  getTerminalDebugInfo: () => ipcRenderer.invoke('get-terminal-debug-info'),
  onTerminalExit: callback => ipcRenderer.on('terminal-exit', (event, data) => callback(data)),

  // Messaging
  queueUserMessage: message => ipcRenderer.invoke('queue-user-message', message),
  sendMessage: messageData => ipcRenderer.invoke('send-message', messageData),

  // A function to receive messages from the main process
  onChromeStatusUpdate: callback =>
    ipcRenderer.on('chrome-status', (event, ...args) => callback(...args)),

  // Error reporting for webview errors
  reportWebviewError: error => ipcRenderer.send('webview-error', error),

  // Listen for formatted error logs from backend
  onNewErrorLog: callback => {
    console.log('ğŸ”§ Preload: Setting up new-error-log listener');
    return ipcRenderer.on('new-error-log', (event, error) => {
      console.log('ğŸ”§ Preload: Received new-error-log event:', error);
      callback(error);
    });
  },

  // Screenshot and File System APIs
  ensureDir: dirPath => ipcRenderer.invoke('ensure-dir', dirPath),
  saveFile: (filePath, data, encoding) => ipcRenderer.invoke('save-file', filePath, data, encoding),
  deleteFile: filePath => ipcRenderer.invoke('delete-file', filePath),
  deleteDir: dirPath => ipcRenderer.invoke('delete-dir', dirPath),
  captureScreen: () => ipcRenderer.invoke('capture-screen'),
  takeScreenshot: () => ipcRenderer.invoke('take-screenshot'),
  openPlanningTab: () => ipcRenderer.invoke('open-planning-tab'),

  // Strike Errors File System Functions - CRITICAL MISSING FUNCTIONS
  openDirectory: () => ipcRenderer.invoke('open-directory'),
  getDirectoryFiles: dirPath => ipcRenderer.invoke('get-directory-files', dirPath),
  readFile: filePath => ipcRenderer.invoke('read-file', filePath),
  showNotification: message => ipcRenderer.invoke('show-notification', message),

  // **CLAUDE CODE CLI INTEGRATION** - Native terminal control
  claudeFileOperation: operation => ipcRenderer.invoke('claude-file-operation', operation),
  claudeGetAppState: () => ipcRenderer.invoke('claude-get-app-state'),

  // Listen for Claude-driven cursor commands
  onAiCursorCommand: callback =>
    ipcRenderer.on('ai-cursor-command', (event, command) => callback(command)),

  // Listen for Claude file changes
  onClaudeFileChanged: callback =>
    ipcRenderer.on('claude-file-changed', (event, changeInfo) => callback(changeInfo)),

  // Listen for screenshot captured from main process
  onScreenshotCaptured: callback =>
    ipcRenderer.on('screenshot-captured', (event, screenshot) => callback(screenshot)),

  // Listen for open planning tab request
  onOpenPlanningTab: callback => ipcRenderer.on('open-planning-tab', _event => callback()),

  // Strike Errors Integration API
  strikeErrorsOpen: () => ipcRenderer.invoke('strike-errors:open'),
  strikeErrorsOpenFileDialog: () => ipcRenderer.invoke('strike-errors:open-file-dialog'),

  // Strike Errors AI Integration API
  initializeStrikeErrorsAI: () => ipcRenderer.invoke('initialize-strike-errors-ai'),
  strikeErrorsAIOpenDirectory: () => ipcRenderer.invoke('strike-errors-ai-open-directory'),
  strikeErrorsAILaunch: options => ipcRenderer.invoke('strike-errors-ai-launch', options),
  strikeErrorsAIResolvePath: (targetPath, context) =>
    ipcRenderer.invoke('strike-errors-ai-resolve-path', targetPath, context),

  // Strike Errors Event Listeners
  onStrikeErrorsStatusUpdate: callback =>
    ipcRenderer.on('strike-errors:status-update', (event, data) => callback(data)),
  onStrikeErrorsProgress: callback =>
    ipcRenderer.on('strike-errors:progress', (event, data) => callback(data)),
  onStrikeErrorsAnalysisComplete: callback =>
    ipcRenderer.on('strike-errors:analysis-complete', (event, results) => callback(results)),
  onStrikeErrorsAnalysisError: callback =>
    ipcRenderer.on('strike-errors:analysis-error', (event, error) => callback(error)),
  onStrikeErrorsFilesUploaded: callback =>
    ipcRenderer.on('strike-errors:files-uploaded', (event, files) => callback(files)),

  // Strike Errors AI Event Listeners
  onStrikeErrorsAIStatusUpdate: callback =>
    ipcRenderer.on('strike-errors-ai-status-update', (event, data) => callback(data)),
  onStrikeErrorsAIProgress: callback =>
    ipcRenderer.on('strike-errors-ai-progress', (event, data) => callback(data)),
  onStrikeErrorsAIAnalysisComplete: callback =>
    ipcRenderer.on('strike-errors-ai-analysis-complete', (event, results) => callback(results)),
  onStrikeErrorsAIAnalysisError: callback =>
    ipcRenderer.on('strike-errors-ai-analysis-error', (event, error) => callback(error)),
  onStrikeErrorsAIDirectoryUploaded: callback =>
    ipcRenderer.on('strike-errors-ai-directory-uploaded', (event, data) => callback(data)),
  onStrikeErrorsAIPathSuggestions: callback =>
    ipcRenderer.on('strike-errors-ai-path-suggestions', (event, suggestions) =>
      callback(suggestions)
    ),

  // Strike Errors Terminal Integration
  sendTerminalInput: message => ipcRenderer.send('terminal-input', message),

  // Test property to verify bridge works
  testConnection: () => 'bridge-working',
});

console.log('âœ… Preload script loaded successfully');

