  const tabsContainer = document.getElementById('results-tabs');
  let mapTab = document.querySelector('.tab-btn[data-tab="real-dependencies"]');
  if (!mapTab) {
    mapTab = document.createElement('button');
    mapTab.className = 'tab-btn';
    mapTab.setAttribute('data-tab', 'real-dependencies');
    mapTab.textContent = 'Real Deps';
    tabsContainer.insertBefore(mapTab, tabsContainer.firstChild);
  }

  // Activate the real dependency map tab
  document.querySelectorAll('.tab-btn').forEach(btn => btn.classList.remove('active'));
  mapTab.classList.add('active');
}

generateRealFixRecommendations(results) {
  const recommendations = [];

  // ESLint fixes
  if (results.analysis.eslint?.length > 0) {
    recommendations.push({
      icon: 'ğŸ”',
      title: 'Run ESLint Auto-Fix',
      priority: 'high',
      description: `Fix ${results.analysis.eslint.length} files with ESLint auto-fix`,
      command: `cd "${results.directoryPath}" && npx eslint . --fix`,
    });
  }

  // Prettier fixes
  recommendations.push({
    icon: 'ğŸ’„',
    title: 'Format Code with Prettier',
    priority: 'medium',
    description: 'Format all code files for consistency',
    command: `cd "${results.directoryPath}" && npx prettier --write "**/*.{js,jsx,ts,tsx,css,json}"`,
  });

  // Build fixes
  if (results.analysis.buildTest?.build?.success === false) {
    recommendations.push({
      icon: 'ğŸ—ï¸',
      title: 'Fix Build Errors',
      priority: 'high',
      description: 'Resolve build compilation errors',
      command: `cd "${results.directoryPath}" && npm run build`,
    });
  }

  // TypeScript fixes
  if (results.analysis.tsCheck?.success === false) {
    recommendations.push({
      icon: 'ğŸ“œ',
      title: 'Fix TypeScript Errors',
      priority: 'high',
      description: 'Resolve TypeScript compilation errors',
      command: `cd "${results.directoryPath}" && npx tsc --noEmit`,
    });
  }

  // Dependency fixes
  if (results.analysis.dependencies?.problems) {
    recommendations.push({
      icon: 'ğŸ“¦',
      title: 'Fix Dependencies',
      priority: 'medium',
      description: 'Install missing or update broken dependencies',
      command: `cd "${results.directoryPath}" && npm install`,
    });
  }

  // Broken function fixes
  if (results.graph.broken.length > 0) {
    recommendations.push({
      icon: 'ğŸ”—',
      title: 'Fix Missing Functions',
      priority: 'high',
      description: `Fix ${results.graph.broken.length} broken function references`,
      command: `# Manual review required for missing functions`,
    });
  }

  return recommendations;
}

updateStatus(message, state) {
  // Update main UI status elements
  const dot = document.getElementById('strikeStatusDot');
  const text = document.getElementById('strikeStatusText');

  if (dot) {
    dot.className = `status-dot ${state}`;
  }
  if (text) {
    text.textContent = message;
  }

  // Also update footer status
  const footerText = document.getElementById('footer-strike-status-text');
  const footerIcon = document.getElementById('footer-strike-status-icon');

  if (footerText) {
    footerText.textContent = `Strike: ${message}`;
  }
  if (footerIcon) {
    footerIcon.className = `status-dot ${state}`;
  }
}

showError(title, message) {
  console.error(`${title}: ${message}`);
  // Alert replaced with proper UI notification
  this.showNotification(`${title}: ${message}`);
}

showNotification(message) {
  // Replace alert with a proper notification system
  // This could be integrated with the app's notification system
  if (typeof window.electronAPI !== 'undefined' && window.electronAPI.showNotification) {
    window.electronAPI.showNotification(message);
  } else {
    // Fallback for web environment
    const notification = document.createElement('div');
    notification.className = 'strike-errors-notification';
    notification.textContent = message;
    notification.style.cssText = `
      position: fixed;
      top: 20px;
      right: 20px;
      background: #f44336;
      color: white;
      padding: 12px 16px;
      border-radius: 4px;
      z-index: 10000;
      max-width: 300px;
    `;
    document.body.appendChild(notification);
    setTimeout(() => notification.remove(), 5000);
  }
}

getHealthClass(score) {
  if (score >= 80) return 'excellent';
  if (score >= 60) return 'good';
  if (score >= 40) return 'fair';
  return 'poor';
}

formatBytes(bytes) {
  if (bytes === 0) return '0 B';
  const k = 1024;
  const sizes = ['B', 'KB', 'MB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
}

escapeHtml(text) {
  const div = document.createElement('div');
  div.textContent = text;
  return div.innerHTML;
}

