ipcMain.handle('open-directory', async () => {
  try {
    const { dialog } = require('electron');
    const result = await dialog.showOpenDialog({
      properties: ['openDirectory'],
    });

    if (!result.canceled && result.filePaths.length > 0) {
      return { success: true, path: result.filePaths[0] };
    } else {
      return { success: false, canceled: true };
    }
  } catch (error) {
    console.error(i18n.t('errors.directory.open', { error }));
    return { success: false, error: error.message };
  }
});

ipcMain.handle('get-directory-files', async (event, dirPath) => {
  try {
    const fs = require('fs');
    const path = require('path');

    if (!fs.existsSync(dirPath)) {
      return { success: false, error: 'Directory does not exist' };
    }

    const files = fs.readdirSync(dirPath).map(file => {
      const fullPath = path.join(dirPath, file);
      const stats = fs.statSync(fullPath);
      return {
        name: file,
        path: fullPath,
        isDirectory: stats.isDirectory(),
        size: stats.size,
        modified: stats.mtime,
      };
    });

    return { success: true, files };
  } catch (error) {
    console.error(i18n.t('errors.directory.files', { error }));
    return { success: false, error: error.message };
  }
});

ipcMain.handle('read-file', async (event, filePath) => {
  try {
    const fs = require('fs');
    const path = require('path');

    const fullPath = path.resolve(filePath);
    if (!fs.existsSync(fullPath)) {
      return { success: false, error: 'File does not exist' };
    }

    const content = fs.readFileSync(fullPath, 'utf8');
    return { success: true, content };
  } catch (error) {
    console.error(i18n.t('errors.file.read', { error }));
    return { success: false, error: error.message };
  }
});

ipcMain.handle('show-notification', async (event, message) => {
  try {
    const { Notification } = require('electron');

    if (Notification.isSupported()) {
      new Notification({
        title: 'Chrome Container App',
        body: message,
      }).show();
      return { success: true };
    } else {
      // Fallback - send to renderer for display
      if (mainWindow && !mainWindow.isDestroyed()) {
        mainWindow.webContents.send('show-notification', message);
      }
      return { success: true };
    }
  } catch (error) {
    console.error(i18n.t('errors.notification.show', { error }));
    return { success: false, error: error.message };
  }
});

// Claude Code integration
ipcMain.handle('claude-file-operation', async (event, operation) => {
  try {
    console.log(i18n.t('info.claude.operation', { type: operation.type }));

    switch (operation.type) {
      case 'cursor-move':
        if (containerApp.mainWindow && !containerApp.mainWindow.isDestroyed()) {
          containerApp.mainWindow.webContents.send('ai-cursor-command', {
            action: 'move',
            x: operation.x,
            y: operation.y,
            duration: operation.duration || 1000,
          });
        }
        break;

      case 'navigate-to':
        if (containerApp.cdpClient) {
          await containerApp.cdpClient.Page.navigate({ url: operation.url });
          console.log(i18n.t('info.claude.navigated', { url: operation.url }));
        }
        break;

      case 'take-screenshot':
        const screenshot = await containerApp.mainWindow.webContents.capturePage();
        const timestamp = Date.now();
        const screenshotPath = path.join(__dirname, 'tmp', `claude-screenshot-${timestamp}.png`);

        const tmpDir = path.dirname(screenshotPath);
        if (!fs.existsSync(tmpDir)) {
          fs.mkdirSync(tmpDir, { recursive: true });
        }

        fs.writeFileSync(screenshotPath, screenshot.toPNG());
        console.log(i18n.t('info.claude.screenshot', { path: screenshotPath }));
        return { success: true, path: screenshotPath };

      default:
        console.log(i18n.t('info.claude.unknownOperation', { type: operation.type }));
    }

    return { success: true };
  } catch (error) {
    console.error(i18n.t('errors.claude.operation', { error }));
    return { success: false, error: error.message };
  }
});

