console.log('ğŸ¬ RENDERER SCRIPT LOADED AND EXECUTING!');

// Catch any JavaScript errors
window.onerror = (message, source, lineno, colno, error) => {
  console.error('ğŸš¨ RENDERER ERROR:', message, 'at', source, 'line', lineno);

  // CRITICAL: Forward renderer errors to Error Logs tab
  const errorLog = {
    timestamp: new Date().toISOString(),
    source: 'Renderer',
    level: 'error',
    message: `${message} at ${source}:${lineno}:${colno}`,
    stack: error?.stack,
  };

  // Forward to Error Logs tab
  if (typeof appendErrorLog !== 'undefined') {
    appendErrorLog(errorLog);
    updateErrorCounts('app-errors', 1);
  }

  return false;
};

document.addEventListener('DOMContentLoaded', () => {
  console.log('âœ… DOM CONTENT LOADED EVENT FIRED!');
  initializeApp();
});

async function initializeApp() {
  console.log('ğŸš€ Initializing app...');
  const initStart = performance.now();

  if (!window.electronAPI) {
    console.error('CRITICAL: electronAPI not found. Preload script may have failed.');
    // Display a permanent error message to the user
    const body = document.body;
    body.innerHTML =
      '<div style="color: red; font-size: 2em; padding: 2em;">FATAL ERROR: Could not connect to backend. Please restart the application.</div>';
    await window.electronAPI?.traceDebug('renderer:init-error', 'electronAPI not found', {
      critical: true,
    });
    return;
  }

  console.log('âœ… electronAPI found. Connecting UI elements.');
  await window.electronAPI.traceDebug?.(
    'renderer:init',
    'Starting comprehensive DOM tracer initialization'
  );

  // Initialize comprehensive DOM tracing for all 89 elements
  try {
    console.log('ğŸ” Setting up comprehensive DOM element tracing...');
    domTracer.setupComprehensiveTracing();

    // Setup enhanced footer metrics tracking (Priority 2)
    enhanceFooterMetrics();

    const coverage = domTracer.getTracingCoverageReport();

    await window.electronAPI.traceDebug?.(
      'renderer:tracer-init',
      'Comprehensive DOM tracing active',
      {
        totalElements: coverage.totalExpected,
        presentElements: coverage.present,
        missingElements: coverage.missing,
        coveragePercentage: coverage.coverage,
        categoryBreakdown: coverage.byCategory,
      }
    );

    console.log(
      `âœ… DOM Tracer initialized: ${coverage.present}/${coverage.totalExpected} elements (${coverage.coverage}%) configured for tracing`
    );

    if (coverage.missing > 0) {
      console.warn(`âš ï¸ Missing DOM elements (${coverage.missing}):`, coverage.missingElements);
      await window.electronAPI.traceDebug?.(
        'renderer:tracer-warning',
        'Missing DOM elements detected',
        {
          missingCount: coverage.missing,
          missingElements: coverage.missingElements.slice(0, 10), // Log first 10 missing
          criticalMissing: coverage.missing > 20,
        }
      );
    }
  } catch (error) {
    console.error('âŒ Failed to initialize DOM tracer:', error);
    await window.electronAPI.traceDebug?.('renderer:tracer-error', error.message, {
      critical: false,
    });
  }

  // GTST FIX: Use consolidated functions from gtst-function-fixes.js
  if (typeof window.connectAllButtons === 'function') {
    window.connectAllButtons();
  } else {
    console.error('âŒ GTST: connectAllButtons not available');
  }

  if (typeof window.setupEventListeners === 'function') {
    window.setupEventListeners();
  } else {
    console.error('âŒ GTST: setupEventListeners not available');
  }
  startLivePolling();
  await setupTerminalContainer();
  enhanceTerminalFeatures(); // Add enhanced terminal functionality
  setupWebviewContainer();
  subscribeToBackendEvents();

  const initDuration = performance.now() - initStart;
  await window.electronAPI.traceDebug?.('renderer:init-complete', 'App initialization finished', {
    duration: initDuration,
    target: 2000, // 2s target for init
    withinTarget: initDuration <= 2000,
    tracingEnabled: true,
    elementsTraced: domTracer.allElements.length,
  });

  // Final validation of comprehensive tracing implementation
  const finalCoverage = domTracer.getTracingCoverageReport();
  await window.electronAPI.traceDebug?.(
    'renderer:tracer-validation',
    'Final DOM tracing validation complete',
    {
      expectedElements: 89, // From DOM-EXPECTED-FUNCTIONS.md
      actualElements: finalCoverage.present,
      coverageAchieved: finalCoverage.coverage,
      complianceWithDOMExpectations:
        finalCoverage.present >= 80
          ? 'EXCELLENT'
          : finalCoverage.present >= 60
            ? 'GOOD'
            : 'NEEDS_IMPROVEMENT',
      tracingEvents: [
        'ui:click',
        'ui:state-change',
        'ui:gesture:start|move|end',
        'ui:performance-check',
        'ui:input',
      ],
      performanceTargets: domTracer.performanceTargets,
    }
  );

  // Setup console integration for error logs
  setupConsoleIntegration();
  setupTerminalLogIntegration();

  console.log(
    `ğŸ‰ App initialization complete (${initDuration.toFixed(2)}ms) with comprehensive DOM tracing for ${finalCoverage.present}/${finalCoverage.totalExpected} elements (${finalCoverage.coverage}% coverage)`
  );
  console.log('ğŸ“‹ Console and terminal integration active for error logs');
}

// Global Chrome initialization promise for performance optimization
let chromeInitPromise = null;
let chromeStatus = { running: false };

