// Removed legacy language manager

/**
 * Chrome Container IPC Handlers - Unified Modern JavaScript Architecture
 * Consistent ES6+ patterns, proper async/await, unified error handling
 */

const { ipcMain, dialog, app } = require('electron');
const CDP = require('chrome-remote-interface');
const fs = require('fs').promises;
const fsSync = require('fs');
const path = require('path');

class IPCHandlers {
  constructor(appInstance) {
    this.app = appInstance;
    this.isInitialized = false;
    this.handlers = new Map();

    // Configuration - removed legacy language manager

    this.config = {
      timeouts: {
        default: 3000,
        cdp: 1000,
        canvas: 300,
      },
      paths: {
        screenshots: path.resolve(__dirname, '../../screenshots'),
        logs: path.resolve(__dirname, '../../logs'),
      },
    };
  }

  /**
   * Setup all IPC handlers with modern patterns
   */
  async setupIPC() {
    try {
      this.log('info', 'ğŸš€ Setting up IPC handlers...');

      await Promise.all([
        this.setupStatusHandlers(),
        this.setupChromeControlHandlers(),
        this.setupFileSystemHandlers(),
        this.setupScreenshotHandlers(),
        this.setupTerminalHandlers(),
        this.setupConnectionHandlers(),
