const pty = require('node-pty');
const os = require('os');
const { EventEmitter } = require('events');
// Removed broken language manager

// MINIMAL PTY Manager - works with minimal terminal in renderer/app.js
class PTYManager extends EventEmitter {
  constructor(app) {
    super();
    this.app = app;
    this.terminals = new Map();
  }

  async setupTerminal() {
    // Basic PTY for backend - works with minimal frontend terminal
    const shell = os.platform() === 'win32' ? 'powershell.exe' : '/bin/zsh';

    // Enhanced environment with Claude CLI support
    const claudePath = `${os.homedir()}/.claude/local`;
    const enhancedEnv = {
      ...process.env,
      PATH: `${claudePath}:${process.env.PATH}:/usr/local/bin:/opt/homebrew/bin:${os.homedir()}/.local/bin`,
      SHELL: '/bin/zsh',
      TERM: 'xterm-256color',
    };

    console.log('ğŸ”§ PTY MANAGER: Creating terminal with enhanced PATH:', enhancedEnv.PATH);
    console.log('ğŸ”§ PTY MANAGER: Using shell:', shell);

    // Use login shell to source .zprofile/.bash_profile where PATH is set
    const shellArgs = shell === '/bin/zsh' ? ['--login'] : shell === '/bin/bash' ? ['--login'] : [];

    const ptyProcess = pty.spawn(shell, shellArgs, {
      name: 'xterm-256color',
      cols: 80,
      rows: 30,
      cwd: process.cwd(),
      env: enhancedEnv,
    });

    this.terminals.set(1, ptyProcess);
    this.app.ptyProcess = ptyProcess;

    ptyProcess.onData(data => {
      console.log('ğŸ“¤ Sending terminal data to frontend:', {
        preview: data.substring(0, 50),
      });
      if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
        this.app.mainWindow.webContents.send('terminal-data', {
          terminalId: 1,
          data: data,
        });
      }
    });

    ptyProcess.onExit(() => {
      console.log('ğŸšª Terminal 1 exited with code 0');
      this.app.ptyProcess = null;
    });

    // Setup Claude CLI alias
    await this.setupClaudeCLI(ptyProcess);
  }

  writeToTerminal(terminalId, data) {
    console.log('ğŸ“¥ Receiving terminal data from frontend:', data.substring(0, 20));
    const terminal = this.terminals.get(terminalId);
    if (terminal) {
      terminal.write(data);
      return true;
    }
    console.error(`âŒ Terminal ${terminalId} not found`);
    return false;
  }

  setupClaudeCodeIntegration() {
    console.log('ğŸ¤– Setting up Claude integration for terminal');
  }

  // Setup Claude CLI according to DEZ-VISION terminal-webview-MCP bridge pattern
  async setupClaudeCLI(ptyProcess) {
    console.log('ğŸ”§ Setting up Claude CLI for terminal-webview-MCP bridge...');

    // Setup commands to ensure PATH and Claude work
    setTimeout(() => {
      ptyProcess.write('clear\r');
      ptyProcess.write('echo "ğŸ”§ Setting up Claude CLI..."\r');
      ptyProcess.write('echo "Current PATH: $PATH"\r');
      ptyProcess.write(`export PATH="${os.homedir()}/.claude/local:$PATH"\r`);
      ptyProcess.write('echo "Updated PATH: $PATH"\r');
      ptyProcess.write('which claude && echo "âœ… Claude CLI found" || echo "âŒ Claude CLI not found"\r');
      ptyProcess.write('ls -la ~/.claude/local/claude 2>/dev/null && echo "âœ… Claude binary exists" || echo "âŒ Claude binary missing"\r');
      ptyProcess.write('echo "Terminal ready for MCP bridge"\r');
    }, 1000);
  }

  // Create a new terminal with specific ID (required by IPC handlers)
  async createTerminalWithId(terminalId) {
    console.log(`ğŸ’» Setting up terminal ${terminalId}`);
    const shell = os.platform() === 'win32' ? 'powershell.exe' : '/bin/zsh';

    // Enhanced environment with Claude CLI support
    const claudePath = `${os.homedir()}/.claude/local`;
    const enhancedEnv = {
      ...process.env,
      PATH: `${claudePath}:${process.env.PATH}:/usr/local/bin:/opt/homebrew/bin:${os.homedir()}/.local/bin`,
      SHELL: '/bin/zsh',
      TERM: 'xterm-256color',
    };

    console.log('ğŸ”§ PTY MANAGER: Creating terminal with enhanced PATH:', enhancedEnv.PATH);
    console.log('ğŸ”§ PTY MANAGER: Using shell:', shell);

    // Use login shell to source .zprofile/.bash_profile where PATH is set
    const shellArgs = shell === '/bin/zsh' ? ['--login'] : shell === '/bin/bash' ? ['--login'] : [];

    const ptyProcess = pty.spawn(shell, shellArgs, {
      name: 'xterm-256color',
      cols: 80,
      rows: 30,
      cwd: process.cwd(),
      env: enhancedEnv,
    });

    this.terminals.set(terminalId, ptyProcess);

    ptyProcess.onData(data => {
      console.log('ğŸ“¤ Sending terminal data to frontend:', {
        preview: data.substring(0, 50),
      });
      if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
        this.app.mainWindow.webContents.send('terminal-data', {
          terminalId: terminalId,
          data: data,
        });
      }
    });

    ptyProcess.onExit(() => {
      console.log(`ğŸšª Terminal ${terminalId} exited with code 0`);
      this.terminals.delete(terminalId);
      if (this.app.mainWindow && !this.app.mainWindow.isDestroyed()) {
        this.app.mainWindow.webContents.send('terminal-exit', { terminalId });
      }
    });

    // Setup Claude CLI alias
    await this.setupClaudeCLI(ptyProcess);

    return terminalId;
  }

  // Create new terminal with auto-generated ID
  async createNewTerminal(options = {}) {
    const terminalId = options.terminalId || Math.floor(Math.random() * 10000) + 2;
    await this.setupTerminal(terminalId);
    return terminalId;
  }

  // Close specific terminal
  closeTerminal(terminalId) {
    const terminal = this.terminals.get(terminalId);
    if (terminal) {
      terminal.kill();
      this.terminals.delete(terminalId);
      return true;
    }
    return false;
  }

  // Switch to specific terminal (for UI purposes)
  switchToTerminal(terminalId) {
    return this.terminals.has(terminalId);
  }

  // Get list of active terminals
  getTerminalList() {
    const terminals = [];
    this.terminals.forEach((terminal, id) => {
      terminals.push({
        id,
        active: true,
        pid: terminal.pid,
      });
    });
    return terminals;
  }

  // Resize terminal
  resizeTerminal(terminalId, cols, rows) {
    const terminal = this.terminals.get(terminalId);
    if (terminal) {
      terminal.resize(cols, rows);
      return true;
    }
    return false;
  }

  // Get debug info
  getDebugInfo() {
    const info = {
      terminalCount: this.terminals.size,
      terminals: [],
    };
    this.terminals.forEach((terminal, id) => {
      info.terminals.push({
        id,
        pid: terminal.pid,
        alive: !terminal.killed,
      });
    });
    return info;
  }
}

module.exports = PTYManager;

