// Catch any JavaScript errors
window.onerror = (message, source, lineno, colno, error) => {
  console.error('ğŸš¨ RENDERER ERROR:', message, 'at', source, 'line', lineno);

  // CRITICAL: Forward renderer errors to Error Logs tab
  const errorLog = {
    timestamp: new Date().toISOString(),
    source: 'Renderer',
    level: 'error',
    message: `${message} at ${source}:${lineno}:${colno}`,
    stack: error?.stack,
  };

  // Forward to Error Logs tab
  if (typeof appendErrorLog !== 'undefined') {
    appendErrorLog(errorLog);
    updateErrorCounts('app-errors', 1);
  }

  return false;
};

document.addEventListener('DOMContentLoaded', () => {
