      'Comprehensive DOM tracing active',
      {
        totalElements: coverage.totalExpected,
        presentElements: coverage.present,
        missingElements: coverage.missing,
        coveragePercentage: coverage.coverage,
        categoryBreakdown: coverage.byCategory,
      }
    );

    console.log(
      `âœ… DOM Tracer initialized: ${coverage.present}/${coverage.totalExpected} elements (${coverage.coverage}%) configured for tracing`
    );

    if (coverage.missing > 0) {
      console.warn(`âš ï¸ Missing DOM elements (${coverage.missing}):`, coverage.missingElements);
      await window.electronAPI.traceDebug?.(
        'renderer:tracer-warning',
        'Missing DOM elements detected',
        {
          missingCount: coverage.missing,
          missingElements: coverage.missingElements.slice(0, 10), // Log first 10 missing
          criticalMissing: coverage.missing > 20,
        }
      );
    }
  } catch (error) {
    console.error('âŒ Failed to initialize DOM tracer:', error);
    await window.electronAPI.traceDebug?.('renderer:tracer-error', error.message, {
      critical: false,
    });
  }

