getElementLineNumber(element) {
  // Approximate line number based on DOM position
  return Array.from(document.querySelectorAll('*')).indexOf(element) + 1;
}

// Setup comprehensive tracing for all DOM elements
setupComprehensiveTracing() {
  this.allElements.forEach(elementId => {
    const element = document.getElementById(elementId);
    if (element) {
      this.attachElementTracing(element);
    }
  });

  // Also trace class-based elements
  const classBased = [
    '.tab-btn',
    '.filter-btn',
    '.dropdown-item',
    '.resizer',
    '.modal',
    '.status-item',
  ];

  classBased.forEach(selector => {
    document.querySelectorAll(selector).forEach(element => {
      this.attachElementTracing(element);
    });
  });
}

attachElementTracing(element) {
  const originalId = element.id || element.className;

  // Click events
  if (element.tagName === 'BUTTON' || element.onclick) {
    const originalClick = element.onclick;
    element.addEventListener('click', async e => {
      const startTime = await this.traceInteraction(element, 'ui:click');

      if (originalClick) {
        const result = await originalClick.call(element, e);
        const duration = performance.now() - startTime;
        await this.tracePerformance(
          element,
          'click-handler',
          duration,
          this.performanceTargets.click
        );
      }
    });
  }

  // Input events
  if (
    element.tagName === 'INPUT' ||
    element.tagName === 'SELECT' ||
    element.tagName === 'TEXTAREA'
  ) {
    element.addEventListener('input', async () => {
      await this.traceInteraction(element, 'ui:input', { value: element.value });
    });

    element.addEventListener('change', async () => {
      await this.traceStateChange(
        element,
        { value: null },
        { value: element.value },
        { trigger: 'user-input' }
      );
    });
  }

