function connectAllButtons() {
  console.log('ğŸ”— Connecting all primary action buttons...');

  // Initialize WebView Tab Manager
  webviewTabManager = new WebViewTabManager();

  // Header & Chrome Controls - OPTIMIZED for performance
  connectButton('header-screenshot-btn', 'takeScreenshot');
  connectButton('initialize-chrome', optimizedInitializeChrome); // Use optimized version
  connectButton('restart-chrome', 'restartChrome');
  connectButton('navigate-canvas', 'navigateCanvas');
  connectButton('refresh-webview', () => webviewTabManager.refresh());

  // Left Toolbar: Strike Errors & AI
  connectButton('launch-strike-errors', 'strikeErrorsOpen');
  connectButton('upload-files', 'openDirectory');
  connectButton('generateDiagram', handleGenerateDiagram);
  connectButton('loadDiagramSvg', handleLoadDiagramSvgFromAnalysis);

  // Left tabs switching
  setupLeftTabs();
  setupFilesTabMermaid();
  // Rehydrate Files tab from globally-set project directory (if previously chosen)
  renderFilesFromProject();

  // Right Toolbar: Planning
  connectButton('import-md', handleImportMD);
  connectButton('take-screenshot', 'takeScreenshot');
  connectButton('clear-screenshots', 'clearScreenshots');

  // Setup file import refresh for context dropdown
  setupFileImportRefresh();

  // Also wire header screenshot button if present
  connectButton('take-screenshot-global', 'takeScreenshot');

  // Right Toolbar: Messages
  connectButton('send-message', handleSendMessage);
  connectButton('queue-message', handleQueueMessage);
  connectButton('urgent-message', handleUrgentMessage);

  // Right Toolbar: Error Logs
  connectButton('clear-errors', 'clearErrors');
  connectButton('export-errors', 'exportLogs');

  // Right Toolbar: Terminal
  connectButton('new-terminal-btn', 'createTerminal');
  connectButton('split-terminal-btn', 'splitTerminal');
  connectButton('terminal-dropdown-btn', handleTerminalDropdown);

  // Modals
