function setupLeftTabs() {
  const tabs = document.querySelectorAll('.left-tabs .tab-btn');
  const panels = document.querySelectorAll('.left-tab-content');
  tabs.forEach(btn => {
    btn.addEventListener('click', async () => {
      const tab = btn.getAttribute('data-left-tab');

      // Start snowball chain for left-tab switch
      const chain = await domTracer.startSnowballChain('left-toolbar-tab-switch');
      await chain.recordStep(1, 'Tab Click', 'app.js:setupLeftTabs', `Tab=${tab}`);

      // Visual selection toggle
      tabs.forEach(b => b.classList.remove('active'));
      btn.classList.add('active');
      await chain.recordStep(2, 'Selection Update', 'app.js:setupLeftTabs', 'Active class applied');

      // Panel visibility
      panels.forEach(p => {
        const isActive = p.getAttribute('data-tab') === tab;
        p.classList.toggle('active', isActive);
      });
      await chain.recordStep(3, 'Panel Visibility', 'app.js:setupLeftTabs', 'Panels toggled');

      // Trace event
      await window.electronAPI.traceDebug?.('ui:tab-change', 'left-toolbar', { tab });

      // Persist selection for UX continuity
      localStorage.setItem('leftToolbarActiveTab', tab);
      await chain.recordStep(4, 'Persist', 'localStorage', 'Active tab stored');

      await chain.complete();
    });
  });

  // Restore last active tab if available
  const saved = localStorage.getItem('leftToolbarActiveTab');
  if (saved) {
    const target = document.querySelector(`.left-tabs .tab-btn[data-left-tab="${saved}"]`);
    if (target) target.click();
  }
}

