function connectButton(id, action) {
  const button = document.getElementById(id);
  if (!button) {
    // This is expected for buttons that don't exist on all pages (e.g. take-screenshot-global)
    // console.warn(`Button not found: ${id}`);
    return;
  }

  button.onclick = async () => {
    console.log(`ğŸ”˜ Button clicked: ${id}`);
    const clickStart = await domTracer.traceInteraction(button, 'ui:click');

    // Start snowball chain for critical buttons
    let chain = null;
    const needsChain = [
      'initialize-chrome',
      'restart-chrome',
      'launch-strike-errors',
      'new-terminal-btn',
    ].includes(id);
    if (needsChain) {
      chain = await domTracer.startSnowballChain(`${id}-action-chain`);
      await chain.recordStep(1, 'Button Click', `app.js:${id}`, `${id} clicked`);
    }

    // Track state change to disabled
    const oldState = { disabled: button.disabled };
    button.disabled = true;
    if (chain)
      await chain.recordStep(2, 'Button Disable', 'app.js:586', 'Prevent multiple attempts');

    await domTracer.traceStateChange(
      button,
      oldState,
      { disabled: true },
      { reason: 'click-handler' }
    );

    try {
      if (typeof action === 'string') {
        if (window.electronAPI[action]) {
          if (chain) await chain.recordStep(3, 'IPC Call', `app.js:598`, `Calling ${action}`);

          const ipcStart = performance.now();
          const result = await window.electronAPI[action]();
          const ipcDuration = performance.now() - ipcStart;

          console.log(`âœ… API call ${action} successful`, result || '');

          if (chain) {
            await chain.recordStep(
              4,
              'IPC Response',
              `electronAPI.${action}`,
              `Result: ${result ? 'success' : 'empty'}`
            );

            // Add specific tracking for critical operations
            if (id === 'initialize-chrome' && result?.pid) {
              await chain.recordStep(
                5,
                'Chrome Started',
                'src/chrome/launcher.js',
                `PID: ${result.pid}`
              );
              await chain.recordStep(
                6,
                'Debug Port',
                'src/chrome/connection.js',
                `Port: ${result.debugPort || 9222}`
              );
            } else if (id === 'new-terminal-btn' && result?.terminalId) {
              await chain.recordStep(
                5,
                'Terminal Created',
                'src/terminal/pty-manager.js',
                `Terminal ID: ${result.terminalId}`
              );
              await chain.recordStep(
                6,
                'PTY Connected',
                'src/terminal/pty-manager.js',
                'Shell active'
              );
            }
          }

          await domTracer.tracePerformance(
            button,
            'ipc-call',
            ipcDuration,
            domTracer.performanceTargets.ipc
          );
          await window.electronAPI.traceDebug?.('ui:action-success', `${id} -> ${action}`, {
            resultPreview: JSON.stringify(result).slice(0, 200),
          });
        } else {
          throw new Error(`electronAPI method ${action} not found.`);
        }
      } else if (typeof action === 'function') {
        if (chain) await chain.recordStep(3, 'Handler Call', 'app.js', 'Executing function');

        const result = await action();

        if (chain) await chain.recordStep(4, 'Handler Complete', 'app.js', 'Function executed');

        await window.electronAPI.traceDebug?.('ui:handler-success', id, {
          resultPreview: result ? JSON.stringify(result).slice(0, 200) : 'undefined',
        });
      }

      if (chain) await chain.complete();
    } catch (error) {
      console.error(`âŒ Error during action for button ${id}:`, error);
      await window.electronAPI.traceDebug?.('ui:action-error', `${id}`, { error: error?.message });
      if (chain) {
        await chain.recordStep(99, 'Error', 'app.js', error.message);
        await chain.complete();
      }
    } finally {
      setTimeout(async () => {
        const oldState = { disabled: button.disabled };
        button.disabled = false;
        await domTracer.traceStateChange(
          button,
          oldState,
          { disabled: false },
          { reason: 'timeout-restore' }
        );

        const totalDuration = performance.now() - clickStart;
        await domTracer.tracePerformance(
          button,
          'click-response',
          totalDuration,
          domTracer.performanceTargets.click
        );
      }, 500);
    }
  };
}

