    return;
  }

  // Confirm action with user
  if (
    !confirm(`Are you sure you want to kill all ${terminalTabManager.terminals.size} terminal(s)?`)
  ) {
    console.log('ğŸ” DOM TRACE: Kill all terminals cancelled by user');
    return;
  }

  // Kill all terminals through manager
  terminalTabManager.killAllTerminals();

  // Update UI state
  updateTerminalCount();
  closeDropdown();

  console.log('ğŸ” DOM TRACE: All terminals killed successfully');
}

