async function initializeLiveTerminalStatus() {
  const statusDot = document.getElementById('terminal-status-dot');
  const statusText = document.getElementById('terminal-status-text');
  const inputEl = document.getElementById('terminal-input');

  if (!statusDot || !statusText) return;

  // Set up live status updates
  const updateStatus = async () => {
    try {
      const terminalStatus = await getTerminalStatus();
      const connectionStatus = await window.electronAPI?.getConnectionStatus();

      // Update status text
      if (connectionStatus && connectionStatus.status === 'connected') {
        statusText.textContent = `Terminal Active (${connectionStatus.connections} sessions)`;
        statusDot.className = 'terminal-status-dot connected';
      } else {
        const isTerminalReady = window.Terminal && xtermInstance;
        statusText.textContent = isTerminalReady ? 'Terminal Ready' : 'Terminal Starting';
        statusDot.className = isTerminalReady
          ? 'terminal-status-dot ready'
          : 'terminal-status-dot loading';
      }

      // Update input placeholder based on current state
      if (inputEl) {
        const errorCount = document.querySelectorAll('.error-item').length;
        const fileCount = document.querySelectorAll('.file-context-item').length;

        let placeholder = '';
        if (errorCount > 0) {
          placeholder = `Analyze ${errorCount} error${errorCount > 1 ? 's' : ''}...`;
        } else if (fileCount > 0) {
          placeholder = `Working with ${fileCount} file${fileCount > 1 ? 's' : ''}...`;
        } else {
          placeholder = 'Enter command or query...';
        }

        inputEl.placeholder = placeholder;
      }
    } catch (error) {
      statusText.textContent = 'Terminal Error';
      statusDot.className = 'terminal-status-dot error';
      if (inputEl) inputEl.placeholder = 'Terminal connection error';
    }
  };

  // Initial update
  await updateStatus();

  // Update every 3 seconds
  setInterval(updateStatus, 3000);

  // Update on specific events
  document.addEventListener('terminal-state-change', updateStatus);
  document.addEventListener('connection-status-change', updateStatus);
  document.addEventListener('file-upload', updateStatus);
  document.addEventListener('error-log-update', updateStatus);
}

// Enhanced Terminal Input Context - like Cursor IDE with native context handling
async function setupTerminalInputContext(inputEl) {
  if (!inputEl) return;

  // Get the terminal input bar container
  const inputContainer = inputEl.parentElement;
  if (!inputContainer) return;

  // Transform input container into unified Cursor-style component
  inputContainer.classList.add('cursor-unified-terminal');

  // Create context bar as sub-element within the input container
  const contextBar = document.createElement('div');
  contextBar.className = 'terminal-input-context cursor-style';
  // Generate dynamic context content from live data
  const contextData = await getLiveTerminalContext();
  contextBar.innerHTML = generateContextHTML(contextData);

  // Insert context bar as FIRST child of input container (unified component)
  inputContainer.insertBefore(contextBar, inputContainer.firstChild);

  // Setup context functionality
  setupAdvancedContextHandling(contextBar, inputEl);

  // Enhanced input with native context integration
  const originalInput = inputEl;

  // Add command history, context awareness, and file integration
  let commandHistory = [];
  let historyIndex = -1;
  let currentInput = '';
  let contextFiles = [];
  let planningContext = '';
  let errorLogContext = '';
  let liveDataContext = '';

  // Load initial context
  loadInitialContext();

  // Enhanced input functionality with context integration
  originalInput.addEventListener('keydown', e => {
    switch (e.key) {
      case 'ArrowUp':
        e.preventDefault();
        if (historyIndex === -1) {
          currentInput = originalInput.value;
          historyIndex = commandHistory.length - 1;
        } else if (historyIndex > 0) {
          historyIndex--;
        }
        if (historyIndex >= 0 && commandHistory[historyIndex]) {
          originalInput.value = commandHistory[historyIndex];
        }
        break;

      case 'ArrowDown':
        e.preventDefault();
        if (historyIndex < commandHistory.length - 1) {
          historyIndex++;
          originalInput.value = commandHistory[historyIndex];
        } else {
          historyIndex = -1;
          originalInput.value = currentInput;
        }
        break;

      case 'Enter':
        const command = originalInput.value.trim();
        if (command) {
          // Process command with context
          const enhancedCommand = processCommandWithContext(command);

          if (!commandHistory.includes(command)) {
            commandHistory.push(command);
            if (commandHistory.length > 50) {
              commandHistory.shift();
            }
          }
        }
        historyIndex = -1;
        currentInput = '';

        // Update context based on command
        updateAdvancedTerminalContext(command);
        break;

      case 'Tab':
        e.preventDefault();
        // Advanced command completion with context
        const value = originalInput.value;
        const suggestions = getContextualSuggestions(value);
        if (suggestions.length === 1) {
          originalInput.value = suggestions[0] + ' ';
        } else if (suggestions.length > 1) {
          showSuggestionDropdown(suggestions, originalInput);
        }
        break;

      case '@':
        // File context trigger - now handled by unified @ button
        console.log('@ context now handled by unified dropdown system');
        break;

      case '#':
        // Error log context trigger
        showErrorContextDropdown(originalInput);
        break;
    }
  });

  // Update context information in real-time
  setInterval(() => {
    updateLiveContext();
  }, 2000);

  console.log('âœ… Enhanced terminal input with native context handling initialized');

  // Helper functions for context handling
  function loadInitialContext() {
    console.log('ğŸ” DOM TRACE: Loading initial context from DOM');

    // Load planning files context from DOM (not API)
    try {
      const planningFiles = document.querySelectorAll('#imported-files .file-item:not(.template)');
      const guidanceContent = document.getElementById('guidance-content')?.value;

      let contextParts = [];

      // Extract planning file content
      planningFiles.forEach(file => {
        const fileName = file.querySelector('.file-name')?.textContent;
        const fileContent = file.dataset.content || fileName;
        if (fileName) {
          contextParts.push(`Planning File: ${fileName}\n${fileContent}`);
        }
      });

      // Add guidance content
      if (guidanceContent && guidanceContent.trim()) {
        contextParts.push(`User Guidance:\n${guidanceContent}`);
      }

      planningContext = contextParts.join('\n\n');
      console.log('ğŸ” DOM TRACE: Planning context loaded from DOM:', contextParts.length, 'items');
    } catch (error) {
      console.error('ğŸš¨ DOM TRACE ERROR: Failed to load planning context:', error);
      planningContext = 'Planning context unavailable';
    }

    // Load error logs context
    const errorLogElements = document.querySelectorAll('.error-item');
    errorLogContext = Array.from(errorLogElements)
      .map(el => el.textContent)
      .join('\n');

    // Load directory files
    if (window.electronAPI?.getDirectoryFiles) {
      window.electronAPI.getDirectoryFiles().then(files => {
        contextFiles = files;
      });
    }
  }

  function processCommandWithContext(command) {
    // Enhance command with available context
    let enhancedCommand = command;

    // Add file paths from context if relevant
    if (command.includes('grep') || command.includes('find')) {
      const relevantFiles = contextFiles.filter(
        f => f.name.includes('app.js') || f.name.includes('trace') || f.name.includes('plan')
      );
      if (relevantFiles.length > 0) {
        enhancedCommand += ` ${relevantFiles.map(f => f.path).join(' ')}`;
      }
    }

    return enhancedCommand;
  }

  function getContextualSuggestions(input) {
    const baseSuggestions = ['ls', 'cd', 'pwd', 'cat', 'grep', 'find', 'npm', 'git', 'node'];
    const contextSuggestions = [];

    // Add file-based suggestions
    if (input.startsWith('cat ') || input.startsWith('grep ')) {
      contextSuggestions.push(...contextFiles.map(f => f.name));
    }

    // Add git-based suggestions
    if (input.startsWith('git ')) {
      contextSuggestions.push('status', 'add', 'commit', 'push', 'pull', 'branch');
    }

    // Add npm-based suggestions
    if (input.startsWith('npm ')) {
      contextSuggestions.push('start', 'test', 'build', 'install', 'run');
    }

    return [...baseSuggestions, ...contextSuggestions].filter(
      cmd => cmd.startsWith(input) || cmd.startsWith(input.split(' ').pop())
    );
  }

  function updateLiveContext() {
    // Update live data from Chrome
    const webview = document.querySelector('webview');
    if (webview) {
      liveDataContext = `Chrome URL: ${webview.src || 'about:blank'}`;
    }

    // Update error log context
    const errorLogElements = document.querySelectorAll('.error-item');
    errorLogContext = Array.from(errorLogElements)
      .slice(0, 5)
      .map(el => el.textContent)
      .join('\n');

    // Update context usage indicator
    const usageText = contextBar.querySelector('.usage-text');
    const usageFill = contextBar.querySelector('.usage-fill');
    const contextLength = (planningContext + errorLogContext + liveDataContext).length;
    const usagePercent = Math.min(Math.floor((contextLength / 10000) * 100), 100);

    if (usageText) usageText.textContent = `${usagePercent}% of context used`;
    if (usageFill) usageFill.style.width = `${usagePercent}%`;
  }
}

