      const gitChanges = gitDisplay.querySelector('.git-changes');
      if (gitBranch) gitBranch.textContent = 'main';
      if (gitChanges) gitChanges.textContent = 'Working tree clean';
    }
  }

  // Update session info
  if (sessionDisplay) {
    const activePanes = terminalPanes.size + 1;
    sessionDisplay.textContent = `Terminal ${currentTerminalId}/${activePanes}`;
  }
}

