// Filter files based on search and category
function filterFiles(fileList, allFiles, searchTerm, category) {
  let filteredFiles = allFiles;

  // Filter by category
  if (category !== 'all') {
    filteredFiles = filteredFiles.filter(file => file.source === category);
  }

  // Filter by search term
  if (searchTerm) {
    filteredFiles = filteredFiles.filter(
      file =>
        file.name.toLowerCase().includes(searchTerm) || file.path.toLowerCase().includes(searchTerm)
    );
  }

  // Update file list display
  fileList.innerHTML = filteredFiles
    .map(
      file =>
        `<div class="file-context-item" data-filepath="${file.path}" data-category="${file.source}">
      <span class="file-icon">${getFileIcon(file.type)}</span>
      <div class="file-info">
        <div class="file-name">${file.name}</div>
        <div class="file-path">${file.path}</div>
        <div class="file-source">${file.source}</div>
      </div>
    </div>`
    )
    .join('');

  // Re-add click handlers
  fileList.querySelectorAll('.file-context-item').forEach(item => {
    item.addEventListener('click', () => {
      const filepath = item.dataset.filepath;
      const filename = item.querySelector('.file-name').textContent;
      const inputEl = document.getElementById('terminal-input');
      if (inputEl) {
        inputEl.value += `@${filename}(${filepath}) `;
        document.querySelector('.terminal-file-context-dropdown').remove();
        inputEl.focus();
      }
    });
  });

  // Update file count
  const fileCount = fileList.parentElement.querySelector('.file-count');
  if (fileCount) {
    fileCount.textContent = `${filteredFiles.length} files available`;
  }
}

