function updateAdvancedTerminalContext(command) {
  // Update context files based on command
  const contextFileItems = document.querySelectorAll('.context-file-item');

  if (command.includes('grep') || command.includes('find')) {
    // Highlight relevant files in context
    contextFileItems.forEach(item => {
      item.classList.add('context-highlight');
      setTimeout(() => item.classList.remove('context-highlight'), 2000);
    });
  }

  // Update context description based on command
  const contextDescription = document.querySelector('.context-description');
  if (contextDescription && command.trim()) {
    contextDescription.textContent = `Last command: ${command} - analyzing file dependencies and trace patterns`;
  }
}

// Get file icon based on type
function getFileIcon(type) {
  const icons = {
    javascript: 'js',
    css: 'css',
    html: 'htm',
    json: 'json',
    markdown: 'md',
    text: 'txt',
    default: 'file',
  };
  return icons[type] || icons.default;
}

// Show error context dropdown
function showErrorContextDropdown(inputEl) {
  const errorLogs = Array.from(document.querySelectorAll('.error-item')).slice(0, 5);

  const dropdown = document.createElement('div');
  dropdown.className = 'terminal-error-context-dropdown cursor-dropdown';
  dropdown.innerHTML = `
    <div class="dropdown-header">Error Context</div>
    ${errorLogs
      .map(
        (error, index) =>
          `<div class="error-context-item" data-error="${index}">
        <span class="error-icon">!</span>
        <div class="error-info">
          <div class="error-message">${error.querySelector('.error-message')?.textContent || 'Unknown error'}</div>
          <div class="error-source">${error.querySelector('.error-source')?.textContent || 'Unknown source'}</div>
        </div>
      </div>`
      )
      .join('')}
  `;

  inputEl.parentElement.appendChild(dropdown);

  // Position dropdown
  const rect = inputEl.getBoundingClientRect();
  dropdown.style.position = 'absolute';
  dropdown.style.top = `${rect.bottom + 2}px`;
  dropdown.style.left = `${rect.left}px`;
  dropdown.style.width = '400px';

  // Add click handlers
  dropdown.querySelectorAll('.error-context-item').forEach(item => {
    item.addEventListener('click', () => {
      const errorMessage = item.querySelector('.error-message')?.textContent;
      inputEl.value += `#error:"${errorMessage}" `;
      dropdown.remove();
      inputEl.focus();
    });
  });

  // Remove on outside click
  setTimeout(() => {
    document.addEventListener(
      'click',
      e => {
        if (!dropdown.contains(e.target)) {
          dropdown.remove();
        }
      },
      { once: true }
    );
  }, 100);
}

// Show command history dropdown
function showCommandHistoryDropdown(inputEl) {
  const commandHistory = ['npm start', 'git status', 'ls -la', 'grep -r "terminal"', 'cd src'];

  const dropdown = document.createElement('div');
  dropdown.className = 'terminal-history-dropdown cursor-dropdown';
  dropdown.innerHTML = `
    <div class="dropdown-header">Command History</div>
    ${commandHistory
      .reverse()
      .map(
        command =>
          `<div class="history-item" data-command="${command}">
        <span class="history-icon">></span>
        <span class="history-command">${command}</span>
      </div>`
      )
      .join('')}
  `;

  inputEl.parentElement.appendChild(dropdown);

  // Position dropdown
  const rect = inputEl.getBoundingClientRect();
  dropdown.style.position = 'absolute';
  dropdown.style.top = `${rect.bottom + 2}px`;
  dropdown.style.left = `${rect.left}px`;
  dropdown.style.width = '300px';

  // Add click handlers
  dropdown.querySelectorAll('.history-item').forEach(item => {
    item.addEventListener('click', () => {
      inputEl.value = item.dataset.command;
      dropdown.remove();
      inputEl.focus();
    });
  });

  // Remove on outside click
  setTimeout(() => {
    document.addEventListener(
      'click',
      e => {
        if (!dropdown.contains(e.target)) {
          dropdown.remove();
        }
      },
      { once: true }
    );
  }, 100);
}

// Show context add dialog
function showContextAddDialog() {
  const existingDialog = document.querySelector('.terminal-context-dialog');
  if (existingDialog) existingDialog.remove();

  const dialog = document.createElement('div');
  dialog.className = 'terminal-context-dialog cursor-dialog';
  dialog.innerHTML = `
    <div class="dialog-content">
      <div class="dialog-header">
        <span class="dialog-title">Add Context</span>
        <button class="dialog-close">x</button>
      </div>
      <div class="dialog-body">
        <div class="context-type-selector">
          <button class="context-type-btn active" data-type="file">Files</button>
          <button class="context-type-btn" data-type="error">Errors</button>
          <button class="context-type-btn" data-type="plan">Plans</button>
          <button class="context-type-btn" data-type="live">Live Data</button>
        </div>
        <div class="context-content-area">
          <textarea class="context-textarea" placeholder="Add context information..."></textarea>
        </div>
        <div class="dialog-actions">
          <button class="btn-cancel">Cancel</button>
          <button class="btn-add">Add Context</button>
        </div>
      </div>
    </div>
    <div class="dialog-overlay"></div>
  `;

  document.body.appendChild(dialog);

  // Add event handlers
  dialog.querySelector('.dialog-close').addEventListener('click', () => dialog.remove());
  dialog.querySelector('.btn-cancel').addEventListener('click', () => dialog.remove());
  dialog.querySelector('.dialog-overlay').addEventListener('click', () => dialog.remove());
}

