// Global Chrome initialization promise for performance optimization
let chromeInitPromise = null;
let chromeStatus = { running: false };

// Optimized Chrome initialization - PERFORMANCE CRITICAL
async function optimizedInitializeChrome() {
  const initStart = performance.now();
  const button = document.getElementById('initialize-chrome');

  if (!button) return;

  try {
    // FAST PATH: Check if Chrome already running (target: <100ms)
    const statusCheck = await window.electronAPI.getChromeStatus();
    if (statusCheck && statusCheck.running) {
      const fastDuration = performance.now() - initStart;
      console.log(`âœ… Fast Chrome initialization: ${fastDuration}ms`);

      await domTracer.tracePerformance(
        button,
        'initialize-chrome',
        fastDuration,
        100 // Target: 100ms for fast path
      );

      chromeStatus = statusCheck;

      // CRITICAL: Update Chrome status indicator for fast path too
      updateChromeStatus(chromeStatus);

      return statusCheck;
    }

    // BACKGROUND PATH: Initialize Chrome if not running
    if (!chromeInitPromise) {
      console.log('ğŸš€ Starting background Chrome initialization...');
      chromeInitPromise = window.electronAPI.initializeChrome();
    }

    // Wait for Chrome with timeout to prevent hanging
    const result = await Promise.race([
      chromeInitPromise,
      new Promise((_, reject) =>
        setTimeout(() => reject(new Error('Chrome initialization timeout')), 3000)
      ),
    ]);

    const totalDuration = performance.now() - initStart;
    console.log(`âœ… Chrome initialization completed: ${totalDuration}ms`);

    // Update global status
    chromeStatus = result || { running: true };

    // CRITICAL: Update Chrome status indicator after successful initialization
    updateChromeStatus(chromeStatus);

    await domTracer.tracePerformance(
      button,
      'initialize-chrome',
      totalDuration,
      500 // Target: 500ms for IPC call
    );

    return result;
  } catch (error) {
    console.error('âŒ Chrome initialization failed:', error);
    chromeStatus = { running: false, error: error.message };
    throw error;
  }
}

// WebView Tab Manager for complete browser experience - ENHANCED
class WebViewTabManager {
  constructor() {
    this.tabs = new Map();
    this.activeTabId = null;
    this.webview = document.getElementById('chrome-viewport');
    this.fallback = document.getElementById('webview-fallback');
    this.addressBar = document.getElementById('webview-address-bar');
    this.tabsContainer = document.getElementById('webview-tabs');
    this.browserControls = {
      back: document.getElementById('webview-back'),
      forward: document.getElementById('webview-forward'),
      refresh: document.getElementById('webview-refresh'),
      home: document.getElementById('webview-home'),
      navigate: document.getElementById('webview-navigate'),
      newTab: document.getElementById('webview-new-tab'),
    };
    this.setupEventListeners();
    this.initializeMainTab();
  }

  setupEventListeners() {
    console.log('ğŸŒ Setting up WebView Tab Manager event listeners...');

    // Address bar navigation
    if (this.addressBar) {
      this.addressBar.addEventListener('keypress', e => {
        if (e.key === 'Enter') {
          this.navigateToUrl(this.addressBar.value);
        }
      });
    }

    // Browser navigation controls
    if (this.browserControls.back) {
      this.browserControls.back.addEventListener('click', () => this.navigateBack());
    }
    if (this.browserControls.forward) {
      this.browserControls.forward.addEventListener('click', () => this.navigateForward());
    }
    if (this.browserControls.refresh) {
      this.browserControls.refresh.addEventListener('click', () => this.refresh());
    }
    if (this.browserControls.home) {
      this.browserControls.home.addEventListener('click', () => this.navigateHome());
    }
    if (this.browserControls.navigate) {
      this.browserControls.navigate.addEventListener('click', () => {
        this.navigateToUrl(this.addressBar.value);
      });
    }
    if (this.browserControls.newTab) {
      this.browserControls.newTab.addEventListener('click', () => this.createNewTab());
    }

    // Webview security - CRITICAL for confinement
    if (this.webview) {
      // Prevent new windows (popups) - enforce confinement
      this.webview.addEventListener('new-window', async e => {
        e.preventDefault(); // Block the popup
        console.log(`ğŸš« Blocked popup attempt: ${e.url}`);

        // Instead of opening popup, navigate within the webview
        await this.navigateToUrl(e.url);
      });

      // Track navigation for address bar updates
      this.webview.addEventListener('did-navigate', e => {
        if (this.addressBar) {
          this.addressBar.value = e.url;
        }
        this.updateActiveTab(e.url);
      });

      // Handle loading states
      this.webview.addEventListener('did-start-loading', () => {
        this.setLoadingState(true);
      });

      this.webview.addEventListener('did-stop-loading', () => {
        this.setLoadingState(false);
      });

      // CRITICAL: Forward webview console messages to Error Logs tab
      this.webview.addEventListener('console-message', e => {
        const errorLog = {
          timestamp: new Date().toISOString(),
          source: 'Browser Console',
          level: e.level === 0 ? 'info' : e.level === 1 ? 'warning' : 'error',
          message: e.message,
          url: this.addressBar ? this.addressBar.value : 'unknown',
        };

        // Forward to Error Logs tab
        appendErrorLog(errorLog);

        // Update error count
        updateErrorCounts('chrome-errors', 1);

        console.log(`ğŸŒ Browser Console [${errorLog.level.toUpperCase()}]:`, errorLog.message);
      });
    }

    console.log('âœ… WebView Tab Manager listeners configured');
  }

  async initializeMainTab() {
    const mainTab = {
      id: 'main',
      url: 'https://www.google.com',
      title: 'Google',
      active: true,
    };

    this.tabs.set('main', mainTab);
    this.activeTabId = 'main';

    if (this.addressBar) {
      this.addressBar.value = mainTab.url;
    }
  }

  async navigateToUrl(url) {
    if (!url) return;

    const navStart = performance.now();
    const chain = await domTracer.startSnowballChain(`webview-navigate-${Date.now()}`);

    await chain.recordStep(
      1,
      'Navigation Request',
      'WebViewTabManager.navigateToUrl',
      `URL: ${url}`
    );

    if (!url.startsWith('http://') && !url.startsWith('https://')) {
      url = 'https://' + url;
    }

    try {
      // Try webview first (faster)
      if (this.webview && !this.webview.classList.contains('hidden')) {
        this.webview.src = url;
        this.fallback.classList.add('hidden');
        this.webview.classList.remove('hidden');
        await chain.recordStep(2, 'Webview Navigation', 'webview', `Source set: ${url}`);

        const navDuration = performance.now() - navStart;
        await domTracer.tracePerformance(this.webview, 'webview-navigation', navDuration, 200);

        if (this.addressBar) this.addressBar.value = url;
        this.updateActiveTab(url, this.getUrlTitle(url));

        await chain.recordStep(
          3,
          'Navigation Complete',
          'performance',
          `Duration: ${navDuration}ms`
        );
        await chain.complete();

        if (navDuration < 200) {
          console.log(`âœ… Fast webview navigation: ${navDuration}ms`);
        }
      } else if (this.fallback) {
        // Use fallback iframe
        this.fallback.src = url;
        this.fallback.classList.remove('hidden');
        if (this.webview) this.webview.classList.add('hidden');
        await chain.recordStep(2, 'Fallback Navigation', 'iframe', `Fallback used: ${url}`);
      }
    } catch (error) {
      await chain.recordStep(99, 'Navigation Error', 'error', error.message);
      await chain.complete();
      console.error('âŒ Navigation failed:', error);
    }
  }

  getUrlTitle(url) {
    try {
      const domain = new URL(url).hostname;
      return domain.replace('www.', '');
    } catch {
      return 'New Tab';
    }
  }

  async navigateBack() {
    if (this.webview && this.webview.canGoBack()) {
      this.webview.goBack();
    }
  }

  async navigateForward() {
    if (this.webview && this.webview.canGoForward()) {
      this.webview.goForward();
    }
  }

  async navigateHome() {
    await this.navigateToUrl('https://www.google.com');
  }

  async refresh() {
    if (this.webview) {
      this.webview.reload();
    } else if (this.fallback) {
      this.fallback.src = this.fallback.src; // Force reload
    }
  }

  updateActiveTabTitle(title) {
    const activeTab = this.tabsContainer.querySelector('.webview-tab.active .tab-title');
    if (activeTab) {
      activeTab.textContent = title;
    }
  }

  updateActiveTab(url, title) {
    if (this.activeTabId) {
      const activeTab = this.tabs.get(this.activeTabId);
      if (activeTab) {
        activeTab.url = url;
        if (title) {
          activeTab.title = title;
          if (activeTab.element) {
            const titleSpan = activeTab.element.querySelector('.tab-title');
            if (titleSpan) titleSpan.textContent = title;
          }
        }
      }
    }
  }

  setLoadingState(loading) {
    // Update UI to show loading state
    const refreshBtn = this.browserControls.refresh;
    if (refreshBtn) {
      if (loading) {
        refreshBtn.classList.add('loading');
      } else {
        refreshBtn.classList.remove('loading');
      }
    }
  }

  async createNewTab(url = 'https://www.google.com', title = 'New Tab') {
    const tabId = `tab-${Date.now()}`;
    const chain = await domTracer.startSnowballChain(`create-tab-${tabId}`);

    await chain.recordStep(
      1,
      'Tab Creation Request',
      'WebViewTabManager.createNewTab',
      `URL: ${url}`
    );

    try {
      // Create tab UI element
      const tabElement = this.createTabElement(tabId, title);

      // Store tab data
      const newTab = {
        id: tabId,
        url,
        title,
        element: tabElement,
        active: false,
      };

      this.tabs.set(tabId, newTab);
      await chain.recordStep(2, 'Tab Registered', 'TabManager', `Tab ID: ${tabId}`);

      // Switch to new tab
      await this.switchToTab(tabId);
      await this.navigateToUrl(url);

      await chain.recordStep(3, 'Tab Active', 'switch', `Active tab: ${tabId}`);
      await chain.complete();

      return tabId;
    } catch (error) {
      await chain.recordStep(99, 'Tab Creation Error', 'error', error.message);
      await chain.complete();
      console.error('âŒ Tab creation failed:', error);
      return null;
    }
  }

  createTabElement(tabId, title) {
    if (!this.tabsContainer) return null;

    const tabElement = document.createElement('div');
    tabElement.className = 'webview-tab';
    tabElement.dataset.tabId = tabId;

    tabElement.innerHTML = `
      <span class="tab-title">${title}</span>
      <button class="tab-close" onclick="webviewTabManager.closeTab('${tabId}')">&times;</button>
    `;

    // Tab click handler
    tabElement.addEventListener('click', e => {
      if (!e.target.classList.contains('tab-close')) {
        this.switchToTab(tabId);
      }
    });

    this.tabsContainer.appendChild(tabElement);
    return tabElement;
  }

  async switchToTab(tabId) {
    const tab = this.tabs.get(tabId);
    if (!tab) return;

    // Deactivate current tab
    if (this.activeTabId) {
      const currentTab = this.tabs.get(this.activeTabId);
      if (currentTab && currentTab.element) {
        currentTab.element.classList.remove('active');
      }
    }

    // Activate new tab
    this.activeTabId = tabId;
    tab.active = true;
    if (tab.element) {
      tab.element.classList.add('active');
    }

    // Update webview
    if (tab.url) {
      await this.navigateToUrl(tab.url);
    }
  }

  async closeTab(tabId) {
    const tab = this.tabs.get(tabId);
    if (!tab) return;

    // Remove tab element
    if (tab.element && tab.element.parentNode) {
      tab.element.parentNode.removeChild(tab.element);
    }

    // Remove from tabs map
    this.tabs.delete(tabId);

    // If this was the active tab, switch to another
    if (this.activeTabId === tabId) {
      const remainingTabs = Array.from(this.tabs.keys());
      if (remainingTabs.length > 0) {
        await this.switchToTab(remainingTabs[0]);
      } else {
        // Create a new main tab if no tabs remain
        await this.createNewTab();
      }
    }
  }
}

