// Helper function to close terminal pane
function closeTerminalPane(terminalId) {
  const paneData = terminalPanes.get(terminalId);
  if (paneData) {
    paneData.terminal.dispose();
    paneData.pane.remove();
    terminalPanes.delete(terminalId);

    // Reorganize remaining terminals
    const terminalContainer = document.getElementById('terminal-container');
    const remainingPanes = terminalContainer.querySelectorAll('.terminal-pane');

    if (remainingPanes.length === 1) {
      terminalContainer.classList.remove('split-layout');
      remainingPanes[0].style.width = '100%';
    } else if (remainingPanes.length > 1) {
      const width = `${100 / remainingPanes.length}%`;
      remainingPanes.forEach(pane => {
        pane.style.width = width;
      });
    }
  }
}

// Helper function to toggle terminal minimize
function toggleTerminalMinimize(terminalId) {
  const paneData = terminalPanes.get(terminalId);
  if (paneData) {
    const xtermContainer = paneData.pane.querySelector('.xterm-container');
    const isMinimized = xtermContainer.style.display === 'none';

    xtermContainer.style.display = isMinimized ? 'block' : 'none';
    paneData.pane.classList.toggle('minimized', !isMinimized);
  }
}

