function updateChromeStatus(status) {
  if (!status) return;
  const statusElement = document.getElementById('chrome-status')?.querySelector('.status-text');
  const statusIcon = document.getElementById('chrome-status')?.querySelector('.status-icon');
  if (statusElement && statusIcon) {
    if (status.running) {
      statusElement.textContent = `Chrome: Running (PID: ${status.pid})`;
      // Use codicon dot; avoid emoji content
      statusIcon.textContent = '';
      statusIcon.classList.remove('danger');
      statusIcon.classList.add('success');
    } else {
      statusElement.textContent = 'Chrome: Not Connected';
      statusIcon.textContent = '';
      statusIcon.classList.remove('success');
      statusIcon.classList.add('danger');
    }
  }
}

function updateConnectionStatus(status) {
  if (!status) return;
  const connectionsElement = document.getElementById('footer-connections');
  const wsElement = document.getElementById('footer-websocket-status');
  const cdpElement = document.getElementById('footer-cdp-status');
  const uploadedFileCountBadge = document.getElementById('uploadedFileCountBadge');
  const confidenceBadgeBadge = document.getElementById('confidenceBadgeBadge');
  const directoryReadyBadge = document.getElementById('directoryReadyBadge');

  if (connectionsElement) connectionsElement.textContent = `${status.connections || 0}`;
  if (wsElement) wsElement.textContent = `WS: ${status.websocketPort || 'N/A'}`;
  if (cdpElement) cdpElement.textContent = `CDP: ${status.cdpPort || 'N/A'}`;

  // Optional: refresh file tab badges if backend provides counts (non-breaking)
  if (typeof status.uploadedFiles === 'number' && uploadedFileCountBadge) {
    uploadedFileCountBadge.textContent = String(status.uploadedFiles);
  }
  if (typeof status.confidence === 'number' && confidenceBadgeBadge) {
    confidenceBadgeBadge.textContent = `${Math.round(status.confidence)}%`;
  }
  if (directoryReadyBadge && status.uploadedFiles >= 0) {
    directoryReadyBadge.textContent = status.uploadedFiles > 0 ? 'Loaded' : 'Ready';
  }
}

// --- Live Polling ---

let pollingInterval;
function startLivePolling() {
  if (pollingInterval) clearInterval(pollingInterval);

  if (!window.electronAPI) {
    console.warn('Polling stopped: electronAPI not available.');
    return;
  }

  const poll = async () => {
    try {
      const metrics = await window.electronAPI.getSystemMetrics();
      updateFooterMetrics(metrics);

      const chromeStatus = await window.electronAPI.getChromeStatus();
      updateChromeStatus(chromeStatus);

      const connectionStatus = await window.electronAPI.getConnectionStatus();
      updateConnectionStatus(connectionStatus);
    } catch (error) {
      console.error('Polling error:', error.message);
      // Stop polling on error to avoid flooding logs
      if (pollingInterval) clearInterval(pollingInterval);
    }
  };

  poll(); // Initial poll
  pollingInterval = setInterval(poll, 5000); // Poll every 5 seconds
}

