// Setup terminal dropdown menu handlers
function setupTerminalDropdownHandlers() {
  console.log('ğŸ” DOM TRACE: Setting up terminal dropdown handlers');

  // Use data-action selectors to match HTML structure
  const dropdownItems = document.querySelectorAll('#terminal-dropdown-menu .dropdown-item');

  dropdownItems.forEach(item => {
    const action = item.dataset.action;

    item.addEventListener('click', () => {
      console.log('ğŸ” DOM TRACE: Dropdown menu item clicked:', action);

      switch (action) {
        case 'new-terminal':
          if (terminalTabManager) {
            terminalTabManager.createTerminal({ name: `Terminal ${terminalTabManager.nextId}` });
          }
          break;

        case 'split-terminal':
          splitTerminal();
          break;

        case 'connect-strike-errors':
          connectToStrikeErrors();
          break;

        case 'kill-all':
          killAllTerminals();
          break;

        default:
          console.warn('ğŸš¨ DOM TRACE WARNING: Unknown dropdown action:', action);
      }

      closeDropdown();
    });
  });

  // Close dropdown when clicking outside
  document.addEventListener('click', e => {
    const dropdown = document.getElementById('terminal-dropdown-menu');
    const btn = document.getElementById('terminal-dropdown-btn');
    if (dropdown && !dropdown.contains(e.target) && e.target !== btn && !btn?.contains(e.target)) {
      dropdown.style.display = 'none';
    }
  });
}

