async function populateFilesContext(container) {
  if (!container) return;

  try {
    // Get uploaded files
    const uploadedFiles = (await window.electronAPI?.getUploadedFiles()) || [];
    const recentFiles = (await window.electronAPI?.getRecentFiles()) || [];

    // Get screenshots from temp folders and DOM
    const screenshots = await getScreenshotsFromTemp();

    const items = [];

    // Add uploaded files
    uploadedFiles.slice(0, 3).forEach(file => {
      items.push({
        icon: getFileIcon(file.ext || file.name),
        name: file.name,
        path: file.path,
        type: 'file',
      });
    });

    // Add screenshots
    screenshots.slice(0, 2).forEach(shot => {
      items.push({
        icon: 'ğŸ“·',
        name: shot.filename,
        path: shot.path,
        type: 'screenshot',
      });
    });

    if (items.length === 0) {
      container.innerHTML = `
        <div class="context-item no-items">
          <span class="item-icon">ğŸ“</span>
          <span class="item-name">No files available</span>
        </div>
      `;
    } else {
      container.innerHTML = items
        .map(
          item => `
        <div class="context-item" data-path="${item.path}" data-type="${item.type}">
          <span class="item-icon">${item.icon}</span>
          <span class="item-name">${item.name}</span>
        </div>
      `
        )
        .join('');
    }
  } catch (error) {
    container.innerHTML = `
      <div class="context-item no-items">
        <span class="item-icon">âš ï¸</span>
        <span class="item-name">Error loading files</span>
      </div>
