window.streamToTerminal = function(message, level = 'info', source = 'phase') {
  console.log(`ğŸ“¤ streamToTerminal: [${source}] ${message}`);

  // Get active terminal or first available terminal
  const activeTerminal = terminalTabManager.getActiveTerminal();
  if (activeTerminal) {
    // Format message with appropriate color based on level
    let formattedMessage = '';
    switch(level) {
      case 'error':
        formattedMessage = `\x1b[31m${message}\x1b[0m`; // Red
        break;
      case 'warning':
        formattedMessage = `\x1b[33m${message}\x1b[0m`; // Yellow
        break;
      case 'success':
        formattedMessage = `\x1b[32m${message}\x1b[0m`; // Green
        break;
      case 'info':
      default:
        formattedMessage = `\x1b[36m${message}\x1b[0m`; // Cyan
        break;
    }

    // Write to terminal with newline
    activeTerminal.write(`${formattedMessage}\r\n`);

    // Also trace for debugging
    if (window.electronAPI?.traceDebug) {
      window.electronAPI.traceDebug(`terminal:stream:${source}`, message, { level });
    }
  } else {
    console.warn('âš ï¸ streamToTerminal: No active terminal found');

    // Fallback: Try to create a terminal if none exists
    if (terminalTabManager.terminals.size === 0) {
      console.log('ğŸ“ streamToTerminal: Creating initial terminal');
      terminalTabManager.createTerminal().then(() => {
        // Retry writing after terminal creation
        const newTerminal = terminalTabManager.getActiveTerminal();
        if (newTerminal) {
          newTerminal.write(`\x1b[36m${message}\x1b[0m\r\n`);
        }
      });
    }
  }
};

// Alias for compatibility
window.writeTerminal = window.streamToTerminal;

