  async traceInteraction(element, eventType, data = {}) {
    const startTime = performance.now();
    const elementId = element.id || element.className || element.tagName;

    try {
      await window.electronAPI.traceDebug?.(eventType, elementId, {
        ...data,
        timestamp: startTime,
        elementType: element.tagName,
        classList: Array.from(element.classList || []),
        location: `renderer/index.html:${this.getElementLineNumber(element)}`,
      });
    } catch (error) {
      console.warn('Trace failed:', error);
    }

    return startTime;
  }

  async traceStateChange(element, oldState, newState, context = {}) {
    const elementId = element.id || element.className;
    this.elementStates.set(elementId, newState);

    await window.electronAPI.traceDebug?.('ui:state-change', elementId, {
      oldState,
      newState,
      context,
      timestamp: performance.now(),
    });
  }

  async tracePerformance(element, operation, duration, target) {
    const elementId = element.id || element.className;
    const withinTarget = duration <= target;

    await window.electronAPI.traceDebug?.('ui:performance-check', elementId, {
      operation,
      duration,
      target,
      withinTarget,
      violation: !withinTarget,
    });

    if (!withinTarget) {
      console.warn(
        `âš ï¸ Performance violation: ${elementId} ${operation} took ${duration}ms (target: ${target}ms)`
      );

      // Track violations for reporting
      if (!this.performanceViolations) {
        this.performanceViolations = [];
      }
      this.performanceViolations.push({
        element: elementId,
        operation,
        duration,
        target,
        timestamp: Date.now(),
      });
    }
  }

  async trackAnimationPerformance(element, animationName) {
    const observer = new PerformanceObserver(list => {
      for (const entry of list.getEntries()) {
        if (entry.name === animationName) {
          const duration = entry.duration;
          const target = this.performanceTargets.animation;

          this.tracePerformance(element, animationName, duration, target);

          if (duration > target) {
            console.error(
              `âš ï¸ Animation violation: ${animationName} took ${duration}ms (target: ${target}ms)`
            );
          }
        }
      }
    });

    observer.observe({ entryTypes: ['measure'] });

    // Mark animation start
    performance.mark(`${animationName}-start`);

    // After animation (via transitionend event)
    element.addEventListener(
      'transitionend',
      () => {
        performance.mark(`${animationName}-end`);
        performance.measure(animationName, `${animationName}-start`, `${animationName}-end`);
      },
      { once: true }
    );
  }

  getElementLineNumber(element) {
    // Approximate line number based on DOM position
    return Array.from(document.querySelectorAll('*')).indexOf(element) + 1;
  }

  // Setup comprehensive tracing for all DOM elements
  setupComprehensiveTracing() {
    this.allElements.forEach(elementId => {
      const element = document.getElementById(elementId);
      if (element) {
        this.attachElementTracing(element);
      }
    });

    // Also trace class-based elements
    const classBased = [
      '.tab-btn',
      '.filter-btn',
      '.dropdown-item',
      '.resizer',
      '.modal',
      '.status-item',
    ];

    classBased.forEach(selector => {
      document.querySelectorAll(selector).forEach(element => {
        this.attachElementTracing(element);
      });
    });
  }

  attachElementTracing(element) {
    const originalId = element.id || element.className;

    // Click events
    if (element.tagName === 'BUTTON' || element.onclick) {
      const originalClick = element.onclick;
      element.addEventListener('click', async e => {
        const startTime = await this.traceInteraction(element, 'ui:click');

        if (originalClick) {
          const result = await originalClick.call(element, e);
          const duration = performance.now() - startTime;
          await this.tracePerformance(
            element,
            'click-handler',
            duration,
            this.performanceTargets.click
          );
        }
      });
    }

    // Input events
    if (
      element.tagName === 'INPUT' ||
      element.tagName === 'SELECT' ||
      element.tagName === 'TEXTAREA'
    ) {
      element.addEventListener('input', async () => {
        await this.traceInteraction(element, 'ui:input', { value: element.value });
      });

      element.addEventListener('change', async () => {
        await this.traceStateChange(
          element,
          { value: null },
          { value: element.value },
          { trigger: 'user-input' }
        );
      });
    }

    // Visibility changes
    const observer = new MutationObserver(async mutations => {
      mutations.forEach(async mutation => {
        if (mutation.type === 'attributes' && mutation.attributeName === 'class') {
          const oldClasses = mutation.oldValue ? mutation.oldValue.split(' ') : [];
          const newClasses = Array.from(element.classList);

          if (!this.arrayEquals(oldClasses, newClasses)) {
            await this.traceStateChange(
              element,
              { classList: oldClasses },
              { classList: newClasses },
              { trigger: 'class-change' }
            );
          }
        }
      });
    });

    observer.observe(element, { attributes: true, attributeOldValue: true });
  }

  arrayEquals(a, b) {
    return a.length === b.length && a.every((val, i) => val === b[i]);
  }

  // Generate comprehensive tracing coverage report
  getTracingCoverageReport() {
    const presentElements = this.allElements.filter(id => document.getElementById(id));
    const missingElements = this.allElements.filter(id => !document.getElementById(id));

    const coverage = {
      totalExpected: this.allElements.length,
      present: presentElements.length,
      missing: missingElements.length,
      coverage: ((presentElements.length / this.allElements.length) * 100).toFixed(1),
      presentElements,
      missingElements,
      byCategory: {
        header: this.allElements.slice(0, 6).filter(id => document.getElementById(id)).length,
        leftToolbar: this.allElements.slice(6, 28).filter(id => document.getElementById(id)).length,
        centralViewport: this.allElements.slice(28, 34).filter(id => document.getElementById(id))
          .length,
        rightToolbar: this.allElements.slice(34, 64).filter(id => document.getElementById(id))
          .length,
        footer: this.allElements.slice(64, 76).filter(id => document.getElementById(id)).length,
        modals: this.allElements.slice(76).filter(id => document.getElementById(id)).length,
      },
    };

    return coverage;
  }
}

