// Enhanced footer metrics tracking with state changes and thresholds
async function enhanceFooterMetrics() {
  const metricsElements = [
    'footer-cpu-usage',
    'footer-memory-usage',
    'footer-chrome-tabs',
    'footer-ai-status-icon',
    'footer-strike-status-icon',
    'footer-connections',
    'footer-websocket-status',
    'footer-cdp-status',
  ];

  // Create metric observer for each element
  metricsElements.forEach(id => {
    const element = document.getElementById(id);
    if (!element) return;

    // Store previous value
    let previousValue = element.textContent;
    let previousTime = Date.now();

    // Store original textContent setter safely
    const originalTextContent = element.textContent;

    // Override textContent with safe fallback
    Object.defineProperty(element, 'textContent', {
      set: async function (newValue) {
        const oldValue = previousValue;
        const currentTime = Date.now();
        const timeSinceLastUpdate = currentTime - previousTime;

        // Apply the actual change safely
        try {
          // Use innerText as fallback since textContent property descriptor may not exist
          this.innerText = newValue;
        } catch (e) {
          // Fallback to direct assignment
          Node.prototype.__lookupSetter__.call(this, 'textContent').call(this, newValue);
        }
        previousValue = newValue;
        previousTime = currentTime;

        // Track metric change
        await domTracer.traceStateChange(
          this,
          { value: oldValue, timestamp: currentTime - timeSinceLastUpdate },
          { value: newValue, timestamp: currentTime },
          {
            trigger: 'metric-update',
            source: 'live-polling',
            interval: timeSinceLastUpdate,
          }
        );

        // Check thresholds for specific metrics
        if (id === 'footer-cpu-usage') {
          const cpu = parseFloat(newValue);
          if (cpu > 80) {
            await window.electronAPI.traceDebug?.('ui:threshold-violation', id, {
              value: cpu,
              threshold: 80,
              severity: cpu > 90 ? 'critical' : 'warning',
            });
          }
        } else if (id === 'footer-memory-usage') {
          const memory = parseFloat(newValue);
          if (memory > 1024) {
            // Over 1GB
            await window.electronAPI.traceDebug?.('ui:threshold-violation', id, {
              value: memory,
              threshold: 1024,
              severity: memory > 2048 ? 'critical' : 'warning',
            });
          }
        } else if (id === 'footer-chrome-tabs') {
          const tabs = parseInt(newValue);
          if (tabs > 20) {
            await window.electronAPI.traceDebug?.('ui:threshold-violation', id, {
              value: tabs,
              threshold: 20,
              severity: tabs > 50 ? 'critical' : 'warning',
            });
          }
        }
      },
      get: function () {
        return this.innerText;
      },
      configurable: true,
    });
  });

  console.log('âœ… Enhanced footer metrics tracking initialized');
}

