// --- Backend Event Subscriptions & Debug UI ---
function subscribeToBackendEvents() {
  try {
    // Live chrome status pushes
    window.electronAPI.onChromeStatus?.(status => {
      updateChromeStatus(status);
    });

    // Status updates (generic)
    window.electronAPI.onStatusUpdate?.(data => {
      console.debug('ğŸ“¡ Status update:', data);
    });

    // Error logs from backend
    window.electronAPI.onErrorLog?.(err => {
      appendErrorLog(err);
    });

    // Screenshot event
    window.electronAPI.onScreenshotCaptured?.(data => {
      try {
        const list = document.getElementById('screenshots-list');
        const countEl = document.getElementById('screenshot-count');
        const container = document.getElementById('screenshots-container');
        if (!list || !countEl) return;
        // remove placeholder if present
        const placeholder = list.querySelector('.no-screenshots');
        if (placeholder) placeholder.remove();
        const item = document.createElement('div');
        item.className = 'screenshot-item';
        item.innerHTML = `
          <div class="screenshot-header">
            <span class="screenshot-path"><code>${data.relativePath || data.path}</code></span>
            <span class="screenshot-meta">${new Date(data.timestamp).toLocaleTimeString()} â€¢ ${(data.size / 1024).toFixed(1)} KB</span>
          </div>
          <div class="screenshot-content expanded">
            <img class="screenshot-image" src="file://${data.path}" alt="screenshot" />
          </div>
        `;
        list.appendChild(item);
        const currentCount = list.querySelectorAll('.screenshot-item').length;
        countEl.textContent = `${currentCount} ${currentCount === 1 ? 'image' : 'images'}`;
        container.scrollTop = container.scrollHeight;
        window.electronAPI.traceDebug?.(
          'renderer:screenshot-added',
          data.relativePath || data.path
        );

        // NEW: Refresh context dropdown if open
        setTimeout(async () => {
          const dropdown = document.querySelector('.context-dropdown-unified');
          if (dropdown && dropdown.style.display === 'block') {
            const docsContainer = document.getElementById('docs-context-items');
            if (docsContainer) {
              await populateDocsContext(docsContainer);
              console.log('ğŸ” DOM TRACE: Context refreshed after screenshot');
            }
          }
        }, 500);
      } catch (err) {
        window.electronAPI.traceDebug?.('renderer:screenshot-error', err?.message || String(err));
      }
    });

    // Renderer trace that initialization completed
    window.electronAPI.traceDebug?.('renderer:init', 'Subscriptions established');
    // Small poller to refresh screenshot count from filesystem via IPC if needed in future
  } catch (e) {
    console.warn('âš ï¸ Failed to subscribe to backend events:', e.message);
  }
}

function appendErrorLog(error) {
  try {
    const container = document.getElementById('error-log');
    if (!container) return;

    // Clear the "no errors" message if it exists
    const noErrors = container.querySelector('.no-errors');
    if (noErrors) {
      noErrors.remove();
    }

    const item = document.createElement('div');
    const ts = error?.timestamp || new Date().toISOString();
    const source = error?.source || 'App';
    const message = error?.message || String(error);
    const level = error?.level || 'error';

    // Escape HTML to prevent XSS
    const escapeHtml = str => {
      const div = document.createElement('div');
      div.textContent = str;
      return div.innerHTML;
    };

    item.innerHTML = `
      <span class="error-timestamp">[${ts.substring(11, 19)}]</span>
      <span class="error-source">${escapeHtml(source)}:</span>
      <span class="error-message ${level}">${escapeHtml(message)}</span>
    `;

    // Add styling based on level
    item.className = `error-item ${level}`;

    container.appendChild(item);

    // Limit to max 100 error items to prevent memory issues
    const errorItems = container.querySelectorAll('.error-item');
    if (errorItems.length > 100) {
      errorItems[0].remove();
    }

    // Scroll to bottom
    container.scrollTop = container.scrollHeight;
  } catch (err) {
    // Silently fail to prevent recursion
  }
}

function updateErrorCounts(type, increment = 1) {
