                console.log('ğŸ” DOM TRACE: Context refreshed after MD import');
              }
            }
          }, 1000); // Allow time for file processing
        }
      });
    });

    observer.observe(importedFilesContainer, {
      childList: true,
      subtree: true,
    });

    console.log('ğŸ” DOM TRACE: File import observer active');
  }

  // Also monitor guidance-content textarea changes
  const guidanceTextarea = document.getElementById('guidance-content');
  if (guidanceTextarea) {
