// Show suggestion dropdown
function showSuggestionDropdown(suggestions, inputEl) {
  const existingDropdown = document.querySelector('.terminal-suggestions-dropdown');
  if (existingDropdown) existingDropdown.remove();

  const dropdown = document.createElement('div');
  dropdown.className = 'terminal-suggestions-dropdown cursor-dropdown';
  dropdown.innerHTML = suggestions
    .map(
      suggestion =>
        `<div class="suggestion-item" data-suggestion="${suggestion}">
      <span class="suggestion-text">${suggestion}</span>
    </div>`
    )
    .join('');

  inputEl.parentElement.appendChild(dropdown);

  // Position dropdown
  const rect = inputEl.getBoundingClientRect();
  dropdown.style.position = 'absolute';
  dropdown.style.top = `${rect.bottom + 2}px`;
  dropdown.style.left = `${rect.left}px`;
  dropdown.style.width = `${rect.width}px`;

  // Add click handlers
  dropdown.querySelectorAll('.suggestion-item').forEach(item => {
    item.addEventListener('click', () => {
      inputEl.value = item.dataset.suggestion + ' ';
      dropdown.remove();
      inputEl.focus();
    });
  });

  // Remove on outside click
  setTimeout(() => {
    document.addEventListener(
      'click',
      e => {
        if (!dropdown.contains(e.target)) {
          dropdown.remove();
        }
      },
      { once: true }
    );
  }, 100);
}

