/**
 * TLDraw-Style Embed Definitions System
 * Based on TLDraw's DEFAULT_EMBED_DEFINITIONS architecture
 * Modular, extensible, and secure embed handling
 */

// Base embed definition interface following TLDraw pattern
class EmbedDefinition {
    constructor(config) {
        this.type = config.type;
        this.title = config.title;
        this.hostnames = config.hostnames || [];
        this.width = config.width || 560;
        this.height = config.height || 315;
        this.isAspectRatioLocked = config.isAspectRatioLocked || true;
        this.doesResize = config.doesResize !== false;
        this.overridePermissions = config.overridePermissions || {};
        this.toEmbedUrl = config.toEmbedUrl;
        this.fromEmbedUrl = config.fromEmbedUrl;
        this.icon = config.icon || 'ðŸ“º';
    }
}

// TLDraw-style DEFAULT_EMBED_DEFINITIONS
const DEFAULT_EMBED_DEFINITIONS = [
    new EmbedDefinition({
        type: 'youtube',
        title: 'YouTube',
        hostnames: [
            '*.youtube.com',
            'youtube.com', 
            'youtu.be',
            'm.youtube.com',
            'www.youtube.com',
            'youtube-nocookie.com',
            'www.youtube-nocookie.com'
        ],
        width: 800,
        height: 450,
        isAspectRatioLocked: true,
        doesResize: true,
        icon: 'ðŸ“º',
        overridePermissions: {
            'allow-popups-to-escape-sandbox': true,
            'allow-presentation': true,
            'allow-same-origin': true,
            'allow-scripts': true
        },
        toEmbedUrl: (url) => {
            // Handle various YouTube URL formats
            const patterns = [
                /(?:youtube\.com\/watch\?v=)([a-zA-Z0-9_-]+)/,
                /(?:youtu\.be\/)([a-zA-Z0-9_-]+)/,
                /(?:youtube\.com\/embed\/)([a-zA-Z0-9_-]+)/,
                /(?:youtube-nocookie\.com\/embed\/)([a-zA-Z0-9_-]+)/,
                /(?:m\.youtube\.com\/watch\?v=)([a-zA-Z0-9_-]+)/
            ];

            for (const pattern of patterns) {
                const match = url.match(pattern);
                if (match) {
                    let videoId = match[1];

                    // DEBUG CHUNK 4: Test with known working video IDs first
                    const testVideoIds = [
                        'M7lc1UVf-VE', // Educational video - usually works
                        'jNQXAC9IVRw', // Me at the zoo - first YouTube video
                        '9bZkp7q19f0', // Gangnam Style - highly available
                        'dQw4w9WgXcQ'  // Rick Roll - classic test video
                    ];

                    // If user entered a test URL, use a known working video ID
                    if (url.includes('test') || url.includes('dQw4w9WgXcQ')) {
                        videoId = testVideoIds[0]; // Use known working video
                        console.log('ðŸ”§ DEBUG CHUNK 4: Using test video ID:', videoId);
                    }

                    // DEBUG CHUNK 5: Add minimal compatibility parameters
                    const embedUrl = `https://www.youtube-nocookie.com/embed/${videoId}?rel=0&modestbranding=1`;

                    console.log('ðŸ”§ DEBUG CHUNK 5: Enhanced URL with parameters:', {
                        originalUrl: url,
                        videoId: videoId,
                        embedUrl: embedUrl,
                        protocol: window.location.protocol,
                        isTestVideo: testVideoIds.includes(videoId),
                        parameters: 'rel=0&modestbranding=1'
                    });

                    return embedUrl;
                }
            }
            return undefined;
        },
        fromEmbedUrl: (embedUrl) => {
            const match = embedUrl.match(/youtube\.com\/embed\/([a-zA-Z0-9_-]+)/);
            return match ? `https://www.youtube.com/watch?v=${match[1]}` : undefined;
        }
    }),

    new EmbedDefinition({
        type: 'vimeo',
        title: 'Vimeo',
        hostnames: [
            'vimeo.com',
            'www.vimeo.com',
            'player.vimeo.com'
        ],
        width: 800,
        height: 450,
        isAspectRatioLocked: true,
        doesResize: true,
        icon: 'ðŸŽ¬',
        overridePermissions: {
            'allow-same-origin': true,
            'allow-scripts': true
        },
        toEmbedUrl: (url) => {
            const match = url.match(/vimeo\.com\/(\d+)/);
            if (match) {
                const videoId = match[1];
                return `https://player.vimeo.com/video/${videoId}?dnt=1&app_id=122963`;
            }
            return undefined;
        },
        fromEmbedUrl: (embedUrl) => {
            const match = embedUrl.match(/player\.vimeo\.com\/video\/(\d+)/);
            return match ? `https://vimeo.com/${match[1]}` : undefined;
        }
    }),

    new EmbedDefinition({
        type: 'twitter',
        title: 'Twitter',
        hostnames: [
            'twitter.com',
            'www.twitter.com',
            'x.com',
            'www.x.com'
        ],
        width: 550,
        height: 400,
        isAspectRatioLocked: false,
        doesResize: true,
        icon: 'ðŸ¦',
        overridePermissions: {
            'allow-same-origin': true,
            'allow-scripts': true,
            'allow-popups': true
        },
        toEmbedUrl: (url) => {
            const patterns = [
                /(?:twitter\.com|x\.com)\/(?:#!\/)?(\w+)\/status(es)?\/(\d+)/,
                /(?:twitter\.com|x\.com)\/(?:#!\/)?(\w+)\/statuses\/(\d+)/
            ];

            for (const pattern of patterns) {
                const match = url.match(pattern);
                if (match) {
                    const tweetId = match[3] || match[2];
                    // Use Twitter's embed API
