// Embed detection and matching system
class EmbedMatcher {
    constructor(definitions = DEFAULT_EMBED_DEFINITIONS) {
        this.definitions = new Map();
        this.hostnameIndex = new Map();

        // Index definitions for fast lookup
        definitions.forEach(def => {
            this.definitions.set(def.type, def);
            def.hostnames.forEach(hostname => {
                if (!this.hostnameIndex.has(hostname)) {
                    this.hostnameIndex.set(hostname, []);
                }
                this.hostnameIndex.get(hostname).push(def);
            });
        });
    }

    // Match URL to embed definition (TLDraw pattern)
    matchUrl(url) {
        try {
            const urlObj = new URL(url);
            const hostname = urlObj.hostname.toLowerCase();

            // Try exact hostname match first
            if (this.hostnameIndex.has(hostname)) {
                for (const def of this.hostnameIndex.get(hostname)) {
                    const embedUrl = def.toEmbedUrl(url);
                    if (embedUrl) {
                        return { definition: def, embedUrl, originalUrl: url };
                    }
                }
            }

            // Try wildcard hostname match
            for (const [pattern, definitions] of this.hostnameIndex.entries()) {
                if (pattern.startsWith('*.')) {
                    const domain = pattern.substring(2);
                    if (hostname.endsWith(domain)) {
                        for (const def of definitions) {
                            const embedUrl = def.toEmbedUrl(url);
                            if (embedUrl) {
                                return { definition: def, embedUrl, originalUrl: url };
                            }
                        }
                    }
                }
            }

            return null;
        } catch (error) {
            console.warn('Invalid URL provided to embed matcher:', url);
            return null;
        }
    }

    // Get all supported platforms
    getSupportedPlatforms() {
        return Array.from(this.definitions.values()).map(def => ({
            type: def.type,
            title: def.title,
            icon: def.icon,
            hostnames: def.hostnames
        }));
    }

    // Add custom embed definition
    addDefinition(definition) {
        if (!(definition instanceof EmbedDefinition)) {
            throw new Error('Definition must be an instance of EmbedDefinition');
        }

        this.definitions.set(definition.type, definition);
        definition.hostnames.forEach(hostname => {
            if (!this.hostnameIndex.has(hostname)) {
                this.hostnameIndex.set(hostname, []);
            }
            this.hostnameIndex.get(hostname).push(definition);
        });
    }

    // Remove embed definition
    removeDefinition(type) {
        const definition = this.definitions.get(type);
        if (definition) {
            this.definitions.delete(type);
            definition.hostnames.forEach(hostname => {
                const defs = this.hostnameIndex.get(hostname);
                if (defs) {
                    const index = defs.indexOf(definition);
                    if (index > -1) {
                        defs.splice(index, 1);
                    }
                    if (defs.length === 0) {
                        this.hostnameIndex.delete(hostname);
                    }
                }
            });
        }
    }
}

// Global embed matcher instance
const globalEmbedMatcher = new EmbedMatcher();

// Backward compatibility with existing detectEmbedType function
function detectEmbedType(url) {
    const match = globalEmbedMatcher.matchUrl(url);
    if (match) {
        return {
            type: match.definition.type,
            definition: match.definition,
            embedUrl: match.embedUrl,
            originalUrl: match.originalUrl,
            handler: {
                createEmbed: (embedUrl, originalUrl) => ({
                    type: 'embed',
                    embedType: match.definition.type,
                    url: embedUrl,
                    originalUrl: originalUrl,
                    width: match.definition.width,
                    height: match.definition.height,
                    title: match.definition.title,
                    icon: match.definition.icon,
                    isAspectRatioLocked: match.definition.isAspectRatioLocked,
                    doesResize: match.definition.doesResize,
                    canResize: match.definition.doesResize,
                    canTranslate: true,
                    permissions: match.definition.overridePermissions
                })
            }
        };
    }

    // Default: treat as generic iframe (backward compatibility)
    return { 
        type: 'generic', 
        embedUrl: url,
        originalUrl: url,
        handler: {
            createEmbed: (embedUrl, originalUrl) => ({
                type: 'embed',
                embedType: 'generic',
