                isAspectRatioLocked: false,
                doesResize: true,
                permissions: {
                    'allow-same-origin': true
                }
            })
        }
    };
}

// Export for use in other modules
if (typeof window !== 'undefined') {
    window.EmbedDefinition = EmbedDefinition;
    window.DEFAULT_EMBED_DEFINITIONS = DEFAULT_EMBED_DEFINITIONS;
    window.EmbedMatcher = EmbedMatcher;
    window.globalEmbedMatcher = globalEmbedMatcher;
    window.detectEmbedType = detectEmbedType;
}

