#!/usr/bin/env python3
"""
SNAPE YouTube Embed Test System
Uses the comprehensive SNAPE unified system to test YouTube embed functionality
with visual pointer feedback and human-like interactions
"""

import time
import json
import subprocess
import sys
import os
from pathlib import Path
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.chrome.options import Options
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.common.exceptions import TimeoutException, NoSuchElementException

class SNAPEYouTubeEmbedTester:
    """
    Specialized SNAPE YouTube embed tester with comprehensive visual feedback
    """

    def __init__(self, server_url="http://localhost:8000"):
        self.server_url = server_url
        self.driver = None
        self.wait = None
        self.youtube_url = "https://www.youtube.com/watch?v=dQw4w9WgXcQ"

        print("ðŸŽ¬ SNAPE YOUTUBE EMBED TEST SYSTEM")
        print("=" * 50)
        print("ðŸŽ¯ Testing YouTube embed with visual pointer feedback")
        print("ðŸ” State tracking and comparison")
        print("ðŸ–±ï¸ Human-like interactions with highlighted trails")
        print(f"ðŸ“º Test URL: {self.youtube_url}")

    def setup_chrome(self):
        """Setup Chrome with optimizations for YouTube embed testing"""
        print("ðŸ”§ Setting up Chrome for YouTube embed testing...")

        chrome_options = Options()

        # Performance optimizations
        chrome_options.add_argument("--no-sandbox")
        chrome_options.add_argument("--disable-dev-shm-usage")
        chrome_options.add_argument("--disable-web-security")
        chrome_options.add_argument("--disable-features=VizDisplayCompositor")
        chrome_options.add_argument("--disable-blink-features=AutomationControlled")
        chrome_options.add_argument("--window-size=1600,1000")
        chrome_options.add_argument("--window-position=50,50")

        # Enhanced logging
        chrome_options.add_argument("--enable-logging")
        chrome_options.add_argument("--log-level=0")
        chrome_options.set_capability('goog:loggingPrefs', {
            'browser': 'ALL', 
            'performance': 'ALL',
            'driver': 'ALL'
        })

        # Anti-detection
        chrome_options.add_experimental_option("excludeSwitches", ["enable-automation"])
        chrome_options.add_experimental_option('useAutomationExtension', False)

        print("ðŸš€ Launching Chrome...")
        self.driver = webdriver.Chrome(options=chrome_options)
        self.driver.execute_script("Object.defineProperty(navigator, 'webdriver', {get: () => undefined})")
        self.wait = WebDriverWait(self.driver, 15)

        # Navigate to canvas
        print("ðŸŒ Navigating to canvas...")
        self.driver.get(f"{self.server_url}/canvas.html")
        time.sleep(3)

        return self.driver

    def inject_snape_system(self):
        """Inject the comprehensive SNAPE unified system"""
        print("ðŸ’‰ Injecting SNAPE unified system...")

        # Read the unified script from the existing SNAPE system
        snape_script = """
        // SNAPE YouTube Embed Testing System
        window.SNAPEYouTubeEmbed = {
            // State tracking
            actionCoordinates: [],
            stateChanges: [],
            embedStates: [],
            pointerTrails: [],

            // Visual feedback system
            activePointers: [],
            trailElements: [],
            embedIndicators: [],

            init() {
                this.setupStyles();
                this.startMonitoring();
                this.connectToSystems();
                console.log('âœ… SNAPE YouTube Embed System Active');
            },

            setupStyles() {
                const style = document.createElement('style');
                style.id = 'snape-youtube-embed-styles';
                style.textContent = `
                    .snape-embed-pointer {
                        position: fixed;
                        width: 35px;
                        height: 35px;
                        background: radial-gradient(circle, #ff0000, #cc0000);
                        border: 3px solid #ffffff;
                        border-radius: 50%;
                        pointer-events: none;
                        z-index: 999999;
                        box-shadow: 
                            0 0 25px rgba(255, 0, 0, 0.9),
                            0 0 50px rgba(255, 68, 68, 0.7);
                        animation: embedPointerPulse 1.2s infinite alternate;
                        transform: scale(1);
                        transition: all 0.3s cubic-bezier(0.68, -0.55, 0.265, 1.55);
                    }

                    .snape-embed-trail {
                        position: fixed;
                        width: 12px;
                        height: 12px;
                        background: linear-gradient(45deg, #ff4444, #ff0000);
                        border-radius: 50%;
                        pointer-events: none;
                        z-index: 999998;
                        opacity: 0.8;
                        animation: embedTrailFade 2s ease-out forwards;
                        box-shadow: 0 0 10px rgba(255, 68, 68, 0.6);
                    }

                    .snape-embed-success {
                        position: fixed;
                        width: 80px;
                        height: 80px;
                        background: radial-gradient(circle, #00ff00, #00cc00);
                        border: 4px solid #ffffff;
                        border-radius: 50%;
                        pointer-events: none;
                        z-index: 999997;
                        box-shadow: 
                            0 0 30px rgba(0, 255, 0, 0.9),
                            0 0 60px rgba(0, 255, 0, 0.6);
                        animation: embedSuccessPulse 2s ease-out forwards;
                        display: flex;
                        align-items: center;
                        justify-content: center;
                        font-size: 30px;
                        color: white;
                    }

                    .snape-embed-modal-highlight {
                        position: fixed;
                        border: 4px solid #ffaa00;
                        background: rgba(255, 170, 0, 0.1);
                        pointer-events: none;
                        z-index: 999996;
                        border-radius: 8px;
                        animation: modalHighlight 3s ease-out forwards;
                        box-shadow: 
                            0 0 20px rgba(255, 170, 0, 0.8),
                            inset 0 0 20px rgba(255, 170, 0, 0.3);
                    }

                    @keyframes embedPointerPulse {
                        0% { transform: scale(1); opacity: 0.9; }
                        100% { transform: scale(1.3); opacity: 1; }
                    }

                    @keyframes embedTrailFade {
                        0% { opacity: 0.8; transform: scale(1); }
                        100% { opacity: 0; transform: scale(0.2); }
                    }

                    @keyframes embedSuccessPulse {
                        0% { transform: scale(0.5); opacity: 1; }
                        50% { transform: scale(1.2); opacity: 0.9; }
                        100% { transform: scale(1); opacity: 0.8; }
                    }

                    @keyframes modalHighlight {
                        0% { border-width: 6px; opacity: 1; }
                        50% { border-width: 8px; opacity: 0.8; }
                        100% { border-width: 4px; opacity: 0.6; }
                    }
                `;
                document.head.appendChild(style);
            },

            startMonitoring() {
                // Console override for embed tracking
                const originalLog = console.log;
                const originalError = console.error;

                console.log = (...args) => {
                    this.trackEmbedConsoleEvent('log', args);
                    originalLog.apply(console, args);
                };

                console.error = (...args) => {
                    this.trackEmbedConsoleEvent('error', args);
                    originalError.apply(console, args);
                };
            },

            connectToSystems() {
                // Connect to existing systems
                this.tlEditor = window.tlEditor;
                this.unifiedDiagnostics = window.unifiedDiagnostics || window.canvasStateDiag;

                if (this.tlEditor) {
                    console.log('ðŸ”— Connected to TLDraw editor');
                }
                if (this.unifiedDiagnostics) {
                    console.log('ðŸ”— Connected to unified diagnostics');
                }
            },

            // Pointer management for YouTube embed testing
            createEmbedPointer(x, y, type = 'default') {
                const pointer = document.createElement('div');
                pointer.className = 'snape-embed-pointer';
                pointer.style.left = (x - 17.5) + 'px';
                pointer.style.top = (y - 17.5) + 'px';

                // Add pointer type indicator
                pointer.innerHTML = `<div style="
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%);
                    font-size: 14px;
                    color: white;
                    font-weight: bold;
                    text-shadow: 0 0 5px rgba(0,0,0,0.8);
                ">ðŸŽ¬</div>`;

                document.body.appendChild(pointer);
                this.activePointers.push(pointer);

                console.log(`ðŸŽ¯ YouTube embed pointer created at (${x}, ${y})`);
                return pointer;
            },

            moveEmbedPointer(pointer, x, y, withTrail = true) {
                if (pointer && pointer.parentNode) {
                    pointer.style.left = (x - 17.5) + 'px';
                    pointer.style.top = (y - 17.5) + 'px';

                    if (withTrail) {
                        this.createEmbedTrail(x, y);
                    }

                    this.pointerTrails.push({x, y, timestamp: Date.now()});
                }
            },

            createEmbedTrail(x, y) {
                const trail = document.createElement('div');
                trail.className = 'snape-embed-trail';
                trail.style.left = (x - 6) + 'px';
                trail.style.top = (y - 6) + 'px';
                document.body.appendChild(trail);

                this.trailElements.push(trail);

                setTimeout(() => {
                    if (trail.parentNode) {
                        trail.remove();
                    }
                    this.trailElements = this.trailElements.filter(t => t !== trail);
                }, 2000);
            },

            createSuccessIndicator(x, y, message = 'âœ…') {
                const indicator = document.createElement('div');
                indicator.className = 'snape-embed-success';
                indicator.style.left = (x - 40) + 'px';
                indicator.style.top = (y - 40) + 'px';
                indicator.innerHTML = message;

                document.body.appendChild(indicator);

                setTimeout(() => {
                    if (indicator.parentNode) {
                        indicator.remove();
                    }
                }, 3000);

                console.log(`âœ… Success indicator created: ${message}`);
            },

            highlightModal(element) {
                if (!element) return;

                const rect = element.getBoundingClientRect();
                const highlight = document.createElement('div');
                highlight.className = 'snape-embed-modal-highlight';
                highlight.style.left = (rect.left - 4) + 'px';
                highlight.style.top = (rect.top - 4) + 'px';
                highlight.style.width = (rect.width + 8) + 'px';
                highlight.style.height = (rect.height + 8) + 'px';

                document.body.appendChild(highlight);

                setTimeout(() => {
                    if (highlight.parentNode) {
                        highlight.remove();
                    }
                }, 3000);

                console.log('ðŸ”¥ Modal highlighted');
            },

            // State tracking for embeds
            trackEmbedAction(actionType, x, y, additionalData = {}) {
                const action = {
                    type: actionType,
                    coordinates: {x, y},
                    timestamp: Date.now(),
                    additionalData
                };

                this.actionCoordinates.push(action);
                console.log(`ðŸ“ Embed action tracked: ${actionType} at (${x}, ${y})`);
                return action;
            },

            trackEmbedState(stateType, data) {
                const state = {
                    type: stateType,
                    data: data,
                    timestamp: Date.now(),
                    shapes: document.querySelectorAll('.tl-shape').length,
                    embeds: document.querySelectorAll('[data-shape-type="embed"]').length
                };

                this.embedStates.push(state);
                console.log(`ðŸ”„ Embed state tracked: ${stateType}`);
                return state;
            },

            trackEmbedConsoleEvent(type, args) {
                // Track console events related to embeds
                const argsStr = args.map(String).join(' ');
                if (argsStr.includes('embed') || argsStr.includes('youtube') || argsStr.includes('iframe')) {
                    this.stateChanges.push({
                        type: 'console_' + type,
                        message: argsStr,
                        timestamp: Date.now()
                    });
                }
            },

            // Capture current state for comparison
            captureEmbedState() {
                const state = {
                    timestamp: Date.now(),
                    dom: {
                        shapes: document.querySelectorAll('.tl-shape').length,
                        embeds: document.querySelectorAll('[data-shape-type="embed"]').length,
                        iframes: document.querySelectorAll('iframe').length,
                        modals: document.querySelectorAll('[role="dialog"], .modal').length
                    },
                    editor: {},
                    embedDetails: []
                };

                // Capture TLDraw state
                try {
                    if (this.tlEditor) {
                        state.editor = {
                            selectedShapes: this.tlEditor.getSelectedShapes().length,
                            allShapes: this.tlEditor.getCurrentPageShapes().length,
                            currentTool: this.tlEditor.getCurrentTool?.()?.id
                        };

                        // Get embed shapes specifically
                        const shapes = this.tlEditor.getCurrentPageShapes();
                        state.embedDetails = shapes
                            .filter(shape => shape.type === 'embed')
                            .map(shape => ({
                                id: shape.id,
                                url: shape.props?.url,
                                w: shape.props?.w,
                                h: shape.props?.h,
                                x: shape.x,
                                y: shape.y
                            }));
                    }
                } catch (e) {
                    state.editor.error = e.message;
                }

                return state;
            },

            // Compare states to validate embed creation
            compareEmbedStates(beforeState, afterState) {
                const comparison = {
                    timestamp: Date.now(),
                    before: beforeState,
                    after: afterState,
                    changes: {},
                    success: false
                };

                // Check for embed creation
                const beforeEmbeds = beforeState.dom?.embeds || 0;
                const afterEmbeds = afterState.dom?.embeds || 0;
                const embedsAdded = afterEmbeds - beforeEmbeds;

                const beforeShapes = beforeState.editor?.allShapes || 0;
                const afterShapes = afterState.editor?.allShapes || 0;
                const shapesAdded = afterShapes - beforeShapes;

                comparison.changes = {
                    embedsAdded,
                    shapesAdded,
                    iframesAdded: (afterState.dom?.iframes || 0) - (beforeState.dom?.iframes || 0),
                    embedDetails: afterState.embedDetails || []
                };

                // Determine success
                comparison.success = embedsAdded > 0 || shapesAdded > 0 || 
                                   (afterState.embedDetails && afterState.embedDetails.length > beforeState.embedDetails?.length);

                if (comparison.success) {
                    console.log('âœ… YouTube embed creation successful');
                } else {
                    console.error('âŒ YouTube embed creation failed');
                }

                return comparison;
            },

            // Clean up
            removeAllPointers() {
                this.activePointers.forEach(pointer => {
                    if (pointer.parentNode) pointer.remove();
                });
                this.activePointers = [];

                this.trailElements.forEach(trail => {
                    if (trail.parentNode) trail.remove();
                });
                this.trailElements = [];
            },

            // Report generation
            generateEmbedReport() {
                return {
                    summary: {
                        totalActions: this.actionCoordinates.length,
                        totalStateChanges: this.stateChanges.length,
                        embedStates: this.embedStates.length,
                        pointerTrails: this.pointerTrails.length
                    },
                    actions: this.actionCoordinates,
                    stateChanges: this.stateChanges,
                    embedStates: this.embedStates,
                    pointerTrails: this.pointerTrails,
                    timestamp: Date.now()
                };
            }
        };

        // Auto-initialize
        window.SNAPEYouTubeEmbed.init();
        """

        self.driver.execute_script(snape_script)
        time.sleep(1)

        # Verify initialization
        status = self.driver.execute_script("""
            return {
                youtubeSystemReady: !!window.SNAPEYouTubeEmbed,
                functionsAvailable: window.SNAPEYouTubeEmbed ? Object.keys(window.SNAPEYouTubeEmbed).length : 0,
                tlEditorConnected: !!(window.SNAPEYouTubeEmbed && window.SNAPEYouTubeEmbed.tlEditor)
            };
        """)

        print(f"âœ… SNAPE YouTube embed system injected: {status}")
        return status

    def human_like_movement(self, start_x, start_y, end_x, end_y, steps=30):
        """Generate smooth human-like movement with embed pointer"""
        print(f"ðŸŽ¯ Human movement: ({start_x}, {start_y}) â†’ ({end_x}, {end_y})")

        # Create YouTube embed pointer
        self.driver.execute_script(f"""
            window.SNAPEYouTubeEmbed.createEmbedPointer({start_x}, {start_y}, 'movement');
        """)

        time.sleep(0.5)

        # Generate smooth path
        actions = ActionChains(self.driver)

        for i in range(1, steps + 1):
            t = i / steps

            # Smooth curve with control points
            control_x = (start_x + end_x) / 2 + (20 * (0.5 - abs(t - 0.5)))
            control_y = (start_y + end_y) / 2 + (15 * (0.5 - abs(t - 0.5)))

            # Bezier curve calculation
            x = (1-t)**2 * start_x + 2*(1-t)*t * control_x + t**2 * end_x
            y = (1-t)**2 * start_y + 2*(1-t)*t * control_y + t**2 * end_y

            # Add natural micro-movements
            x += (t * 2 - 1) * 2  # Slight horizontal drift
            y += (t * 2 - 1) * 1  # Slight vertical drift

            # Update pointer position with trail
            if i % 2 == 0:  # Update every other step for smooth animation
                self.driver.execute_script(f"""
                    const pointers = window.SNAPEYouTubeEmbed.activePointers;
                    if (pointers.length > 0) {{
                        window.SNAPEYouTubeEmbed.moveEmbedPointer(pointers[pointers.length - 1], {int(x)}, {int(y)}, true);
                    }}
                """)

            # Variable speed for natural movement
            if i < steps * 0.2:  # Slow start
                pause_time = 0.04
            elif i > steps * 0.8:  # Slow end
                pause_time = 0.03
            else:  # Faster middle
                pause_time = 0.015

            body = self.driver.find_element('tag name', 'body')
            actions.move_to_element_with_offset(body, int(x), int(y))
            actions.pause(pause_time)

        actions.perform()
        time.sleep(0.8)

    def test_youtube_embed_workflow(self):
        """Test the complete YouTube embed workflow with visual feedback"""
        print("\nðŸŽ¬ TESTING YOUTUBE EMBED WORKFLOW")
        print("=" * 50)

        # Capture initial state
        print("ðŸ“Š Capturing initial state...")
        initial_state = self.driver.execute_script("return window.SNAPEYouTubeEmbed.captureEmbedState();")

        # Track workflow start
        self.driver.execute_script(f"""
            window.SNAPEYouTubeEmbed.trackEmbedAction('workflow_start', 800, 400, {{
                youtubeUrl: '{self.youtube_url}',
                initialState: 'captured'
            }});
        """)

        try:
            # Step 1: Look for embed button or tool
            print("\n1ï¸âƒ£ Finding embed button/tool...")

            # Try multiple selectors for embed button
            embed_selectors = [
                '[data-tool="embed"]',
                'button[title*="embed" i]',
                'button[aria-label*="embed" i]',
                '.toolbar button:contains("Embed")',
                '[data-testid="embed-tool"]',
                'button:contains("ðŸ“º")',
                'button:contains("ðŸŽ¬")'
            ]

            embed_button = None
            embed_button_rect = None

            for selector in embed_selectors:
                try:
                    embed_button = self.wait.until(EC.element_to_be_clickable((By.CSS_SELECTOR, selector)))
                    embed_button_rect = embed_button.rect
                    print(f"âœ… Found embed button: {selector}")
                    break
                except:
                    continue

            if not embed_button:
                # If no specific embed button, try right-click for context menu
                print("âš ï¸ No embed button found, trying right-click for context menu...")

                # Right-click on canvas center
                canvas_center_x, canvas_center_y = 800, 400

                # Move to canvas center with visual pointer
                self.human_like_movement(400, 300, canvas_center_x, canvas_center_y)

                # Track right-click action
                self.driver.execute_script(f"""
                    window.SNAPEYouTubeEmbed.trackEmbedAction('right_click_for_embed', {canvas_center_x}, {canvas_center_y});
                """)

                # Perform right-click
                actions = ActionChains(self.driver)
                body = self.driver.find_element('tag name', 'body')
                actions.move_to_element_with_offset(body, canvas_center_x, canvas_center_y)
                actions.context_click()
                actions.perform()

                time.sleep(2)

                # Look for embed option in context menu
                try:
                    embed_menu_item = self.wait.until(EC.element_to_be_clickable((By.XPATH, "//button[contains(text(), 'Embed') or contains(text(), 'YouTube')]")))
                    embed_button_rect = embed_menu_item.rect
                    embed_button = embed_menu_item
                    print("âœ… Found embed option in context menu")
                except:
                    print("âŒ No embed option found in context menu")
                    # Try to create embed programmatically
                    return self.create_embed_programmatically()

            # Step 2: Click embed button with visual feedback
            if embed_button and embed_button_rect:
                print("\n2ï¸âƒ£ Clicking embed button...")

                button_x = embed_button_rect['x'] + embed_button_rect['width'] / 2
                button_y = embed_button_rect['y'] + embed_button_rect['height'] / 2

                # Move to embed button
                self.human_like_movement(800, 400, button_x, button_y)

                # Track button click
                self.driver.execute_script(f"""
                    window.SNAPEYouTubeEmbed.trackEmbedAction('embed_button_click', {button_x}, {button_y});
                """)

                # Click with visual feedback
                embed_button.click()
                print(f"âœ… Clicked embed button at ({button_x}, {button_y})")

                time.sleep(2)

            # Step 3: Wait for modal and enter YouTube URL
            print("\n3ï¸âƒ£ Looking for embed modal...")

            # Try multiple modal selectors
            modal_selectors = [
                '[role="dialog"]',
                '.modal',
                '[data-testid="embed-modal"]',
                '.embed-dialog',
                'div[class*="modal"]',
                'div[class*="dialog"]'
            ]

            modal = None
            for selector in modal_selectors:
                try:
                    modal = self.wait.until(EC.presence_of_element_located((By.CSS_SELECTOR, selector)))
                    print(f"âœ… Found modal: {selector}")
                    break
                except:
                    continue

            if modal:
                # Highlight the modal
                self.driver.execute_script(f"""
                    window.SNAPEYouTubeEmbed.highlightModal(arguments[0]);
                """, modal)

                # Look for URL input field
                url_input_selectors = [
                    'input[type="url"]',
                    'input[placeholder*="URL" i]',
                    'input[placeholder*="link" i]',
                    'input[placeholder*="youtube" i]',
                    'input[name*="url"]',
                    'textarea',
                    'input[type="text"]'
                ]

                url_input = None
                for selector in url_input_selectors:
                    try:
                        url_input = modal.find_element(By.CSS_SELECTOR, selector)
                        print(f"âœ… Found URL input: {selector}")
                        break
                    except:
                        continue

                if url_input:
                    print("\n4ï¸âƒ£ Entering YouTube URL...")

                    # Get input field position
                    input_rect = url_input.rect
                    input_x = input_rect['x'] + input_rect['width'] / 2
                    input_y = input_rect['y'] + input_rect['height'] / 2

                    # Move to input field
                    self.human_like_movement(button_x or 800, button_y or 400, input_x, input_y)

                    # Click input field
                    url_input.click()

                    # Track URL entry
                    self.driver.execute_script(f"""
                        window.SNAPEYouTubeEmbed.trackEmbedAction('url_input_focus', {input_x}, {input_y}, {{
                            youtubeUrl: '{self.youtube_url}'
                        }});
                    """)

                    # Clear field and enter URL
                    url_input.clear()
                    url_input.send_keys(self.youtube_url)

                    print(f"âœ… Entered YouTube URL: {self.youtube_url}")
                    time.sleep(1)

                    # Step 4: Submit the embed
                    print("\n5ï¸âƒ£ Submitting embed...")

                    # Look for submit button
                    submit_selectors = [
                        'button[type="submit"]',
                        'button:contains("Add")',
                        'button:contains("Create")',
                        'button:contains("OK")',
                        'button:contains("Embed")',
                        '.modal button:last-child',
                        '[data-testid="submit-embed"]'
                    ]

                    submit_button = None
                    for selector in submit_selectors:
                        try:
                            submit_button = modal.find_element(By.CSS_SELECTOR, selector)
                            print(f"âœ… Found submit button: {selector}")
                            break
                        except:
                            continue

                    if submit_button:
                        # Get submit button position 
                        submit_rect = submit_button.rect
                        submit_x = submit_rect['x'] + submit_rect['width'] / 2
                        submit_y = submit_rect['y'] + submit_rect['height'] / 2

                        # Move to submit button
                        self.human_like_movement(input_x, input_y, submit_x, submit_y)

                        # Track submit action
                        self.driver.execute_script(f"""
                            window.SNAPEYouTubeEmbed.trackEmbedAction('embed_submit', {submit_x}, {submit_y});
                        """)

                        # Click submit
                        submit_button.click()

                        print("âœ… Submitted embed creation")
                        time.sleep(3)

                        # Create success indicator
                        self.driver.execute_script(f"""
                            window.SNAPEYouTubeEmbed.createSuccessIndicator({submit_x}, {submit_y}, 'ðŸŽ¬âœ…');
                        """)
                    else:
                        # Try pressing Enter
                        print("âš ï¸ No submit button found, trying Enter key...")
                        url_input.send_keys('\n')
                        time.sleep(3)

                else:
                    print("âŒ No URL input field found in modal")
                    return self.create_embed_programmatically()
            else:
                print("âŒ No embed modal found")
                return self.create_embed_programmatically()

            # Step 5: Verify embed creation
            print("\n6ï¸âƒ£ Verifying embed creation...")
            time.sleep(2)

            # Capture final state
            final_state = self.driver.execute_script("return window.SNAPEYouTubeEmbed.captureEmbedState();")

            # Compare states
            comparison = self.driver.execute_script(f"""
                return window.SNAPEYouTubeEmbed.compareEmbedStates(
                    {json.dumps(initial_state)},
                    {json.dumps(final_state)}
                );
            """)

            # Track completion
            self.driver.execute_script(f"""
                window.SNAPEYouTubeEmbed.trackEmbedState('workflow_complete', {{
                    comparison: {json.dumps(comparison)},
                    success: {str(comparison.get('success', False)).lower()}
                }});
            """)

            print(f"ðŸ“Š Embed creation result: {'âœ… SUCCESS' if comparison.get('success') else 'âŒ FAILED'}")

            if comparison.get('success'):
                print(f"   Embeds added: {comparison['changes']['embedsAdded']}")
                print(f"   Shapes added: {comparison['changes']['shapesAdded']}")
                print(f"   Iframes added: {comparison['changes']['iframesAdded']}")

                # Test embed interaction
                self.test_embed_interaction(final_state)
            else:
                print("   No embed detected, trying programmatic creation...")
                return self.create_embed_programmatically()

            return comparison

        except Exception as e:
            print(f"âŒ YouTube embed workflow error: {e}")
            return self.create_embed_programmatically()

    def create_embed_programmatically(self):
        """Create YouTube embed programmatically if UI method fails"""
        print("\nðŸ”§ Creating YouTube embed programmatically...")

        # Capture state before programmatic creation
        initial_state = self.driver.execute_script("return window.SNAPEYouTubeEmbed.captureEmbedState();")

        # Create embed via TLDraw API
        creation_result = self.driver.execute_script(f"""
            try {{
                if (window.tlEditor && window.tlEditor.createShapes) {{
                    const embedShape = {{
                        id: 'youtube-embed-' + Date.now(),
                        type: 'embed',
                        x: 400,
                        y: 300,
                        props: {{
                            url: '{self.youtube_url}',
                            w: 560,
                            h: 315
                        }}
                    }};

                    window.tlEditor.createShapes([embedShape]);

                    console.log('âœ… YouTube embed created programmatically');

                    // Create visual success indicator
                    window.SNAPEYouTubeEmbed.createSuccessIndicator(680, 457, 'ðŸŽ¬ðŸ”§');

                    return {{ success: true, method: 'programmatic', shape: embedShape }};
                }} else {{
                    console.error('âŒ TLDraw editor not available');
                    return {{ success: false, error: 'TLDraw editor not available' }};
                }}
            }} catch (e) {{
                console.error('âŒ Programmatic embed creation failed:', e);
                return {{ success: false, error: e.message }};
            }}
        """)

        time.sleep(2)

        # Capture final state
        final_state = self.driver.execute_script("return window.SNAPEYouTubeEmbed.captureEmbedState();")

        # Compare states
        comparison = self.driver.execute_script(f"""
            return window.SNAPEYouTubeEmbed.compareEmbedStates(
                {json.dumps(initial_state)},
                {json.dumps(final_state)}
            );
        """)

        # Track programmatic creation
        self.driver.execute_script(f"""
            window.SNAPEYouTubeEmbed.trackEmbedState('programmatic_creation', {{
                result: {json.dumps(creation_result)},
                comparison: {json.dumps(comparison)}
            }});
        """)

        print(f"ðŸ”§ Programmatic creation result: {'âœ… SUCCESS' if creation_result.get('success') else 'âŒ FAILED'}")

        if creation_result.get('success'):
            self.test_embed_interaction(final_state)

        return comparison

    def test_embed_interaction(self, embed_state):
        """Test interaction with created embed (select, move, resize)"""
        print("\nðŸŽ® Testing embed interaction...")

        embed_details = embed_state.get('embedDetails', [])
        if not embed_details:
            print("âš ï¸ No embed details available for interaction testing")
            return

        embed = embed_details[0]  # Test first embed
        embed_x = embed.get('x', 400) + embed.get('w', 560) / 2
        embed_y = embed.get('y', 300) + embed.get('h', 315) / 2

        print(f"ðŸŽ¯ Testing embed at ({embed_x}, {embed_y})")

        # Test 1: Select embed
        print("   1ï¸âƒ£ Selecting embed...")
        self.human_like_movement(800, 200, embed_x, embed_y)

        # Click to select
        self.driver.execute_script(f"""
            const canvas = document.querySelector('canvas') || document.body;
            const clickEvent = new MouseEvent('click', {{
                clientX: {embed_x},
                clientY: {embed_y},
                bubbles: true
            }});
            canvas.dispatchEvent(clickEvent);

            window.SNAPEYouTubeEmbed.trackEmbedAction('embed_select', {embed_x}, {embed_y});
        """)

        time.sleep(1)

        # Test 2: Drag embed
        print("   2ï¸âƒ£ Dragging embed...")
        new_x, new_y = embed_x + 100, embed_y + 50

        # Simulate drag
        self.driver.execute_script(f"""
            const canvas = document.querySelector('canvas') || document.body;

            const mouseDown = new MouseEvent('mousedown', {{
                clientX: {embed_x},
                clientY: {embed_y},
                bubbles: true
            }});
            canvas.dispatchEvent(mouseDown);

            setTimeout(() => {{
                const mouseMove = new MouseEvent('mousemove', {{
                    clientX: {new_x},
                    clientY: {new_y},
                    bubbles: true
                }});
                canvas.dispatchEvent(mouseMove);
            }}, 100);

            setTimeout(() => {{
                const mouseUp = new MouseEvent('mouseup', {{
                    clientX: {new_x},
                    clientY: {new_y},
                    bubbles: true
                }});
                canvas.dispatchEvent(mouseUp);
            }}, 300);

            window.SNAPEYouTubeEmbed.trackEmbedAction('embed_drag', {new_x}, {new_y});
        """)

        # Visual movement
        self.human_like_movement(embed_x, embed_y, new_x, new_y, steps=15)

        # Test 3: Resize embed (try corner drag)
        print("   3ï¸âƒ£ Resizing embed...")
        corner_x = new_x + 100
        corner_y = new_y + 50

        self.human_like_movement(new_x, new_y, corner_x, corner_y, steps=10)

        # Simulate resize drag
        self.driver.execute_script(f"""
            const canvas = document.querySelector('canvas') || document.body;

            const mouseDown = new MouseEvent('mousedown', {{
                clientX: {corner_x - 50},
                clientY: {corner_y - 25},
                bubbles: true
            }});
            canvas.dispatchEvent(mouseDown);

            setTimeout(() => {{
                const mouseMove = new MouseEvent('mousemove', {{
                    clientX: {corner_x},
                    clientY: {corner_y},
                    bubbles: true
                }});
                canvas.dispatchEvent(mouseMove);
            }}, 100);

            setTimeout(() => {{
                const mouseUp = new MouseEvent('mouseup', {{
                    clientX: {corner_x},
                    clientY: {corner_y},
                    bubbles: true
                }});
                canvas.dispatchEvent(mouseUp);
            }}, 300);

            window.SNAPEYouTubeEmbed.trackEmbedAction('embed_resize', {corner_x}, {corner_y});
        """)

        time.sleep(2)

        # Create interaction success indicator
        self.driver.execute_script(f"""
            window.SNAPEYouTubeEmbed.createSuccessIndicator({corner_x}, {corner_y}, 'ðŸŽ®âœ…');
        """)

        print("âœ… Embed interaction tests completed")

    def generate_report(self):
        """Generate comprehensive test report"""
        print("\nðŸ“Š Generating YouTube embed test report...")

        report = self.driver.execute_script("return window.SNAPEYouTubeEmbed.generateEmbedReport();")

        # Add browser logs
        try:
            browser_logs = self.driver.get_log('browser')
            report['browserLogs'] = browser_logs[-15:]
        except Exception as e:
            report['browserLogs'] = [{'error': f'Log collection failed: {e}'}]

        # Final state capture
        report['finalState'] = self.driver.execute_script("return window.SNAPEYouTubeEmbed.captureEmbedState();")

        # Generate summary
        print(f"\nðŸ“‹ YOUTUBE EMBED TEST REPORT")
        print(f"=" * 40)
        print(f"ðŸŽ¬ Total Actions: {report['summary']['totalActions']}")
        print(f"ðŸ”„ State Changes: {report['summary']['totalStateChanges']}")
        print(f"ðŸ“º Embed States: {report['summary']['embedStates']}")
        print(f"ðŸŽ¯ Pointer Trails: {report['summary']['pointerTrails']}")

        # Embed analysis
        final_embeds = report['finalState']['dom']['embeds']
        final_shapes = report['finalState']['editor'].get('allShapes', 0)

        print(f"\nðŸ“Š Final Results:")
        print(f"   Embeds created: {final_embeds}")
        print(f"   Total shapes: {final_shapes}")
        print(f"   Embed details: {len(report['finalState'].get('embedDetails', []))}")

        # Success determination
        success = final_embeds > 0 or len(report['finalState'].get('embedDetails', [])) > 0
        print(f"\nðŸ† Overall Result: {'âœ… SUCCESS' if success else 'âŒ FAILED'}")

        # Save report
        report_filename = f"snape_youtube_embed_report_{int(time.time())}.json"
        with open(report_filename, 'w') as f:
            json.dump(report, f, indent=2, default=str)

        print(f"ðŸ“„ Report saved: {report_filename}")

        return report

    def run_test(self):
        """Run the complete YouTube embed test"""
        print("ðŸš€ STARTING SNAPE YOUTUBE EMBED TEST")
        print("=" * 60)

        try:
            # Setup
            self.setup_chrome()
            time.sleep(3)

            # Inject SNAPE system
            status = self.inject_snape_system()
            if not status['youtubeSystemReady']:
                print("âŒ SNAPE YouTube system not ready")
                return

            # Take initial screenshot
            self.driver.save_screenshot("snape_youtube_embed_start.png")
            print("ðŸ“¸ Initial screenshot captured")

            # Run YouTube embed workflow test
            workflow_result = self.test_youtube_embed_workflow()

            # Generate comprehensive report
            report = self.generate_report()

            # Clean up pointers
            self.driver.execute_script("window.SNAPEYouTubeEmbed.removeAllPointers();")

            # Final screenshot
            self.driver.save_screenshot("snape_youtube_embed_complete.png")
            print("ðŸ“¸ Final screenshot captured")

            # Display results for user observation
            print(f"\nðŸŽ¬ YOUTUBE EMBED TEST COMPLETE!")
            print(f"Keeping browser open for 10 seconds to observe results...")
            time.sleep(10)

        except Exception as e:
            print(f"âŒ YouTube embed test failed: {e}")
            if self.driver:
                self.driver.save_screenshot("snape_youtube_embed_error.png")

        finally:
            if self.driver:
                print("ðŸ”’ Closing browser...")
                self.driver.quit()

def main():
    """Main entry point"""
    print("ðŸŽ¬ SNAPE YOUTUBE EMBED TESTING SYSTEM")
    print("=" * 60)
    print("ðŸŽ¯ Testing YouTube embed creation with visual feedback")
    print("ðŸ” State tracking and human-like interactions")
    print("ðŸ–±ï¸ Highlighted pointer trails and success indicators")
    print()

    tester = SNAPEYouTubeEmbedTester()
    tester.run_test()

if __name__ == "__main__":
    main()

