  1390â†’                            fill="${shape.props.fill === 'none' ? 'transparent' : shape.props.color}" 
  1391â†’                            stroke="${shape.props.color}" stroke-width="${shape.props.strokeWidth || 2}"/>`;
  1392â†’                } else if (shape.type === 'geo' && shape.props.geo === 'ellipse') {
  1393â†’                    svg += `<ellipse cx="${shape.x + shape.props.w/2}" cy="${shape.y + shape.props.h/2}" 
  1394â†’                            rx="${shape.props.w/2}" ry="${shape.props.h/2}"
  1395â†’                            fill="${shape.props.fill === 'none' ? 'transparent' : shape.props.color}" 
  1396â†’                            stroke="${shape.props.color}" stroke-width="${shape.props.strokeWidth || 2}"/>`;
  1397â†’                }
  1398â†’                // Add more shape types as needed
  1399â†’            });
  1400â†’            svg += '</svg>';
