// Canvas versioning and operational transform utilities
class VersionControlUtils {
    constructor() {
        this.versions = new Map();
        this.branches = new Map();
        this.mergeStrategies = new Map();
        this.conflictResolvers = new Map();
        this.currentBranch = 'main';
        this.versionCounter = 0;

        this.initializeDefaultStrategies();
    }

    initializeDefaultStrategies() {
        // Register default merge strategies
        this.registerMergeStrategy('last-write-wins', this.lastWriteWinsMerge.bind(this));
        this.registerMergeStrategy('field-level-merge', this.fieldLevelMerge.bind(this));
        this.registerMergeStrategy('operational-transform', this.operationalTransformMerge.bind(this));

        // Register default conflict resolvers
        this.registerConflictResolver('position', this.resolvePositionConflict.bind(this));
        this.registerConflictResolver('properties', this.resolvePropertyConflict.bind(this));
        this.registerConflictResolver('structure', this.resolveStructureConflict.bind(this));
    }

    // Version management
    createVersion(snapshot, metadata = {}) {
        const version = {
            id: generateId('version'),
            number: ++this.versionCounter,
            timestamp: Date.now(),
            snapshot: this.deepCloneSnapshot(snapshot),
            metadata: {
                author: 'anonymous',
                message: 'Canvas update',
                branch: this.currentBranch,
                ...metadata
            },
            parent: this.getCurrentVersion()?.id || null,
            checksum: this.calculateChecksum(snapshot)
        };

        this.versions.set(version.id, version);

        // Update branch pointer
        if (!this.branches.has(this.currentBranch)) {
            this.branches.set(this.currentBranch, []);
        }
        this.branches.get(this.currentBranch).push(version.id);

        console.log('üì¶ PHASE 4: Created version:', {
            id: version.id,
            number: version.number,
            branch: this.currentBranch,
            checksum: version.checksum
        });

        return version;
    }

    getCurrentVersion() {
        const branchVersions = this.branches.get(this.currentBranch) || [];
        if (branchVersions.length === 0) return null;

        const latestVersionId = branchVersions[branchVersions.length - 1];
        return this.versions.get(latestVersionId);
    }

    getVersion(versionId) {
        return this.versions.get(versionId);
    }

    getVersionHistory(branch = this.currentBranch) {
        const branchVersions = this.branches.get(branch) || [];
        return branchVersions.map(id => this.versions.get(id)).filter(Boolean);
    }

    // Branch management
    createBranch(branchName, fromVersion = null) {
        if (this.branches.has(branchName)) {
            throw new Error(`Branch ${branchName} already exists`);
        }

        const sourceVersion = fromVersion ? this.getVersion(fromVersion) : this.getCurrentVersion();
        if (!sourceVersion) {
            throw new Error('No source version to branch from');
        }

        this.branches.set(branchName, [sourceVersion.id]);

        console.log('üåø PHASE 4: Created branch:', {
            name: branchName,
            from: sourceVersion.id,
            number: sourceVersion.number
        });

        return branchName;
    }

    switchBranch(branchName) {
        if (!this.branches.has(branchName)) {
            throw new Error(`Branch ${branchName} does not exist`);
        }

        this.currentBranch = branchName;
        console.log('üîÑ PHASE 4: Switched to branch:', branchName);

        return this.getCurrentVersion();
    }

    getBranches() {
        return Array.from(this.branches.keys());
    }

    // Conflict detection and resolution
    detectVersionConflicts(baseVersion, localVersion, remoteVersion) {
        const conflicts = [];

        // Compare snapshots for structural conflicts
        const baseRecords = baseVersion.snapshot.records || {};
        const localRecords = localVersion.snapshot.records || {};
        const remoteRecords = remoteVersion.snapshot.records || {};

        // Find records modified in both branches
        Object.keys(localRecords).forEach(recordId => {
            if (remoteRecords[recordId] && baseRecords[recordId]) {
                const baseRecord = baseRecords[recordId];
                const localRecord = localRecords[recordId];
                const remoteRecord = remoteRecords[recordId];

                // Check if both sides modified the same record
                const localChanged = JSON.stringify(baseRecord) !== JSON.stringify(localRecord);
                const remoteChanged = JSON.stringify(baseRecord) !== JSON.stringify(remoteRecord);

                if (localChanged && remoteChanged) {
                    conflicts.push({
                        type: 'concurrent-modification',
                        recordId,
                        baseRecord,
                        localRecord,
                        remoteRecord,
                        fieldConflicts: this.findFieldConflicts(baseRecord, localRecord, remoteRecord)
                    });
                }
            }
        });

        // Check for deletion conflicts
        Object.keys(baseRecords).forEach(recordId => {
            const deletedLocal = !localRecords[recordId];
            const modifiedRemote = remoteRecords[recordId] && 
                JSON.stringify(baseRecords[recordId]) !== JSON.stringify(remoteRecords[recordId]);

            if (deletedLocal && modifiedRemote) {
                conflicts.push({
                    type: 'delete-modify-conflict',
                    recordId,
                    baseRecord: baseRecords[recordId],
                    remoteRecord: remoteRecords[recordId]
                });
            }

            const deletedRemote = !remoteRecords[recordId];
            const modifiedLocal = localRecords[recordId] && 
                JSON.stringify(baseRecords[recordId]) !== JSON.stringify(localRecords[recordId]);

            if (deletedRemote && modifiedLocal) {
                conflicts.push({
                    type: 'modify-delete-conflict',
                    recordId,
                    baseRecord: baseRecords[recordId],
                    localRecord: localRecords[recordId]
                });
            }
        });

        return conflicts;
    }

    findFieldConflicts(baseRecord, localRecord, remoteRecord) {
        const conflicts = [];
        const allFields = new Set([
            ...Object.keys(baseRecord),
            ...Object.keys(localRecord),
            ...Object.keys(remoteRecord)
        ]);

        allFields.forEach(field => {
            const baseValue = baseRecord[field];
            const localValue = localRecord[field];
            const remoteValue = remoteRecord[field];

            const localChanged = JSON.stringify(baseValue) !== JSON.stringify(localValue);
            const remoteChanged = JSON.stringify(baseValue) !== JSON.stringify(remoteValue);
            const valuesConflict = JSON.stringify(localValue) !== JSON.stringify(remoteValue);

            if (localChanged && remoteChanged && valuesConflict) {
                conflicts.push({
                    field,
                    baseValue,
                    localValue,
                    remoteValue
                });
            }
        });

        return conflicts;
    }

    // Merge strategies
    registerMergeStrategy(name, strategyFunction) {
        this.mergeStrategies.set(name, strategyFunction);
    }

    lastWriteWinsMerge(conflicts, options = {}) {
        const resolved = [];

        conflicts.forEach(conflict => {
            // Use the version with the later timestamp
            const localTime = conflict.localRecord?.timestamp || 0;
            const remoteTime = conflict.remoteRecord?.timestamp || 0;

            const winner = localTime > remoteTime ? 'local' : 'remote';

            resolved.push({
                ...conflict,
                resolution: winner,
                resolvedRecord: winner === 'local' ? conflict.localRecord : conflict.remoteRecord,
                strategy: 'last-write-wins'
            });
        });

        return resolved;
    }

    fieldLevelMerge(conflicts, options = {}) {
        const resolved = [];

        conflicts.forEach(conflict => {
            if (conflict.type === 'concurrent-modification') {
                const mergedRecord = { ...conflict.baseRecord };

                // Merge non-conflicting fields
                conflict.fieldConflicts.forEach(fieldConflict => {
                    // Apply custom field resolution or default to remote
                    const resolver = this.conflictResolvers.get(fieldConflict.field) || 
                                    this.conflictResolvers.get('default');

                    if (resolver) {
                        mergedRecord[fieldConflict.field] = resolver(fieldConflict, options);
                    } else {
                        mergedRecord[fieldConflict.field] = fieldConflict.remoteValue;
                    }
                });

                // Add non-conflicting changes from both sides
                Object.keys(conflict.localRecord).forEach(field => {
                    if (!conflict.fieldConflicts.find(fc => fc.field === field)) {
                        mergedRecord[field] = conflict.localRecord[field];
                    }
                });

                Object.keys(conflict.remoteRecord).forEach(field => {
                    if (!conflict.fieldConflicts.find(fc => fc.field === field)) {
                        mergedRecord[field] = conflict.remoteRecord[field];
                    }
                });

                resolved.push({
                    ...conflict,
                    resolution: 'merged',
                    resolvedRecord: mergedRecord,
                    strategy: 'field-level-merge'
                });
            } else {
                // Default to remote for other conflict types
                resolved.push({
                    ...conflict,
                    resolution: 'remote',
                    resolvedRecord: conflict.remoteRecord,
                    strategy: 'field-level-merge'
                });
            }
        });

        return resolved;
    }

    operationalTransformMerge(conflicts, options = {}) {
        const resolved = [];

        conflicts.forEach(conflict => {
            if (conflict.type === 'concurrent-modification') {
                // Apply operational transform for position and size changes
                const transformedRecord = this.applyOperationalTransform(
                    conflict.baseRecord,
                    conflict.localRecord,
                    conflict.remoteRecord
                );

                resolved.push({
                    ...conflict,
                    resolution: 'transformed',
                    resolvedRecord: transformedRecord,
                    strategy: 'operational-transform'
                });
            } else {
                // Use field-level merge as fallback
                const fieldResolved = this.fieldLevelMerge([conflict], options);
                resolved.push(...fieldResolved);
            }
        });

        return resolved;
    }

    applyOperationalTransform(baseRecord, localRecord, remoteRecord) {
        const transformed = { ...baseRecord };

        // Transform position changes
        if (this.hasPositionChanges(baseRecord, localRecord, remoteRecord)) {
            const position = this.transformPosition(baseRecord, localRecord, remoteRecord);
            transformed.x = position.x;
            transformed.y = position.y;
        }

        // Transform size changes
        if (this.hasSizeChanges(baseRecord, localRecord, remoteRecord)) {
            const size = this.transformSize(baseRecord, localRecord, remoteRecord);
            if (transformed.props) {
                transformed.props.w = size.w;
                transformed.props.h = size.h;
            }
        }

        // Apply other non-conflicting changes
        Object.keys(remoteRecord).forEach(key => {
            if (!['x', 'y', 'props'].includes(key)) {
                transformed[key] = remoteRecord[key];
            }
        });

        return transformed;
    }

    hasPositionChanges(base, local, remote) {
        return (base.x !== local.x || base.y !== local.y) &&
               (base.x !== remote.x || base.y !== remote.y);
    }

    hasSizeChanges(base, local, remote) {
        const baseW = base.props?.w || 0;
        const baseH = base.props?.h || 0;
        const localW = local.props?.w || 0;
        const localH = local.props?.h || 0;
        const remoteW = remote.props?.w || 0;
        const remoteH = remote.props?.h || 0;

        return (baseW !== localW || baseH !== localH) &&
               (baseW !== remoteW || baseH !== remoteH);
    }

    transformPosition(base, local, remote) {
        // Calculate deltas
        const localDelta = {
            x: local.x - base.x,
            y: local.y - base.y
        };

        const remoteDelta = {
            x: remote.x - base.x,
            y: remote.y - base.y
        };

        // Apply both transformations
        return {
            x: base.x + localDelta.x + remoteDelta.x,
            y: base.y + localDelta.y + remoteDelta.y
        };
    }

    transformSize(base, local, remote) {
        const baseW = base.props?.w || 0;
        const baseH = base.props?.h || 0;
        const localW = local.props?.w || 0;
        const localH = local.props?.h || 0;
        const remoteW = remote.props?.w || 0;
        const remoteH = remote.props?.h || 0;

        // Calculate scale factors
        const localScaleX = baseW > 0 ? localW / baseW : 1;
        const localScaleY = baseH > 0 ? localH / baseH : 1;
        const remoteScaleX = baseW > 0 ? remoteW / baseW : 1;
        const remoteScaleY = baseH > 0 ? remoteH / baseH : 1;

        // Apply combined scaling
        return {
            w: baseW * localScaleX * remoteScaleX,
            h: baseH * localScaleY * remoteScaleY
        };
    }

    // Conflict resolvers
    registerConflictResolver(type, resolverFunction) {
        this.conflictResolvers.set(type, resolverFunction);
    }

    resolvePositionConflict(fieldConflict, options = {}) {
        // Prefer the position that moves the shape less
        const localDistance = Math.abs(fieldConflict.localValue - fieldConflict.baseValue);
        const remoteDistance = Math.abs(fieldConflict.remoteValue - fieldConflict.baseValue);

        return localDistance < remoteDistance ? fieldConflict.localValue : fieldConflict.remoteValue;
    }

    resolvePropertyConflict(fieldConflict, options = {}) {
        // For properties, prefer the value that seems more intentional
        if (typeof fieldConflict.localValue === 'string' && typeof fieldConflict.remoteValue === 'string') {
            // Prefer longer strings (more content)
            return fieldConflict.localValue.length > fieldConflict.remoteValue.length ? 
                   fieldConflict.localValue : fieldConflict.remoteValue;
        }

        // Default to remote
        return fieldConflict.remoteValue;
    }

    resolveStructureConflict(fieldConflict, options = {}) {
        // For structural changes, prefer remote (assume it's authoritative)
        return fieldConflict.remoteValue;
    }

    // Version merging
    mergeVersions(baseVersionId, localVersionId, remoteVersionId, strategy = 'field-level-merge') {
        const baseVersion = this.getVersion(baseVersionId);
        const localVersion = this.getVersion(localVersionId);
        const remoteVersion = this.getVersion(remoteVersionId);

        if (!baseVersion || !localVersion || !remoteVersion) {
            throw new Error('Invalid version IDs for merge');
        }

        // Detect conflicts
        const conflicts = this.detectVersionConflicts(baseVersion, localVersion, remoteVersion);

        // Resolve conflicts using specified strategy
        const mergeStrategy = this.mergeStrategies.get(strategy);
        if (!mergeStrategy) {
            throw new Error(`Unknown merge strategy: ${strategy}`);
        }

        const resolvedConflicts = mergeStrategy(conflicts);

        // Create merged snapshot
        const mergedSnapshot = this.createMergedSnapshot(
            baseVersion.snapshot,
            localVersion.snapshot,
            remoteVersion.snapshot,
            resolvedConflicts
        );

        // Create merge version
        const mergeVersion = this.createVersion(mergedSnapshot, {
            author: 'merge-bot',
            message: `Merge ${localVersionId} and ${remoteVersionId}`,
            mergeInfo: {
                strategy,
                baseVersion: baseVersionId,
                localVersion: localVersionId,
                remoteVersion: remoteVersionId,
                conflicts: conflicts.length,
                resolved: resolvedConflicts.length
            }
        });

        console.log('üîÄ PHASE 4: Merged versions:', {
            base: baseVersionId,
            local: localVersionId,
            remote: remoteVersionId,
            result: mergeVersion.id,
            conflicts: conflicts.length,
            strategy
        });

        return {
            version: mergeVersion,
            conflicts,
            resolved: resolvedConflicts
        };
    }

    createMergedSnapshot(baseSnapshot, localSnapshot, remoteSnapshot, resolvedConflicts) {
        const merged = {
            records: { ...baseSnapshot.records }
        };

        // Apply non-conflicting changes from local
        Object.keys(localSnapshot.records || {}).forEach(recordId => {
            if (!resolvedConflicts.find(rc => rc.recordId === recordId)) {
                merged.records[recordId] = localSnapshot.records[recordId];
            }
        });

        // Apply non-conflicting changes from remote
        Object.keys(remoteSnapshot.records || {}).forEach(recordId => {
            if (!resolvedConflicts.find(rc => rc.recordId === recordId)) {
                merged.records[recordId] = remoteSnapshot.records[recordId];
            }
        });

        // Apply resolved conflicts
        resolvedConflicts.forEach(resolved => {
            if (resolved.resolvedRecord) {
                merged.records[resolved.recordId] = resolved.resolvedRecord;
            } else if (resolved.resolution === 'delete') {
                delete merged.records[resolved.recordId];
            }
        });

        return merged;
    }

    // Utility methods
    deepCloneSnapshot(snapshot) {
        return JSON.parse(JSON.stringify(snapshot));
    }

    calculateChecksum(snapshot) {
        const serialized = JSON.stringify(snapshot, Object.keys(snapshot).sort());
        let hash = 0;
        for (let i = 0; i < serialized.length; i++) {
            const char = serialized.charCodeAt(i);
            hash = ((hash << 5) - hash) + char;
            hash = hash & hash; // Convert to 32-bit integer
        }
        return hash.toString(36);
    }

    // localStorage persistence
    saveToStorage(key = 'tldraw-version-control') {
        try {
            const data = {
                versions: Object.fromEntries(this.versions),
                branches: Object.fromEntries(this.branches),
                currentBranch: this.currentBranch,
                versionCounter: this.versionCounter
            };

            localStorage.setItem(key, JSON.stringify(data));
            console.log('üíæ PHASE 4: Version control state saved');
            return true;
        } catch (error) {
            console.error('‚ùå PHASE 4: Failed to save version control state:', error);
            return false;
        }
    }

    loadFromStorage(key = 'tldraw-version-control') {
        try {
            const stored = localStorage.getItem(key);
            if (!stored) return false;

            const data = JSON.parse(stored);
            this.versions = new Map(Object.entries(data.versions || {}));
            this.branches = new Map(Object.entries(data.branches || {}));
            this.currentBranch = data.currentBranch || 'main';
            this.versionCounter = data.versionCounter || 0;

            console.log('üìÇ PHASE 4: Version control state loaded');
            return true;
        } catch (error) {
            console.error('‚ùå PHASE 4: Failed to load version control state:', error);
            return false;
        }
    }

    getStats() {
        return {
            versions: this.versions.size,
            branches: this.branches.size,
            currentBranch: this.currentBranch,
            currentVersion: this.getCurrentVersion()?.number || 0,
            strategies: this.mergeStrategies.size,
            resolvers: this.conflictResolvers.size
        };
    }
}

