/**
 * Core Store System - Based on @tldraw/store architecture
 * Provides typed records, schema migrations, and reactive updates
 */

// Record types - strongly typed like tldraw's tlschema
const TLRecordTypes = {
    SHAPE: 'shape',
    INSTANCE: 'instance', 
    PAGE: 'page',
    CAMERA: 'camera',
    ASSET: 'asset'
};

// Shape types are defined in types-definitions.js

// Base record class
class BaseRecord {
    constructor(type, id, props = {}) {
        this.id = id || this.generateId(type);
        this.typeName = type;
        this.meta = {};
        this.index = 'a1';
        Object.assign(this, props);
    }

    generateId(type) {
        // Use centralized generateId from utils.js
        return window.generateId ? window.generateId(type) : `${type}:${Date.now().toString(36)}${Math.random().toString(36).substr(2)}`;
    }

    clone() {
        return new this.constructor(this.typeName, this.id, { ...this });
    }

    update(changes) {
        Object.assign(this, changes);
        return this;
    }
}

